    padding: 20px;
    border-radius: 50px;
    background: #f1f0f0;
    font-size: 23px;
    font-weight: 300;
    font-family: "Segoe UI"!important;
}

.textarea {
    padding: 10px 10px;
    border-radius: 30px;
    font-size: 14px;
    line-height: 27px;
    resize: both;
}

.ts_main .thread {
    height: 310px;
    padding: 22px 10px 10px;
    border-bottom: 0px solid #d0d4d6;
    overflow-y: scroll;
}

.ts_main .ts_textarea .info {
    float: right;
    margin-right: 10px;
    color: #797c7d;
    font-size: 12px;
    display: none;
}

.ts_main .message_content:before {
    content: " ";
    position: absolute;
    top: 20px;
    left: -10px;
    display: block;
    width: 0;
    height: 0;
    border-top: 5px solid transparent;
    border-right: 10px solid #e4e8eb;
    border-bottom: 5px solid transparent;
    display:none;}

}
