css:
@-moz-document domain("docs.google.com") {
/* List of documents */

html,
body {
    background-color: #292929;
}
.gb_ce .gb_ld {
    background: #808080;
}
.docs-homescreen-grid-header,
.docs-homescreen-list-header {
    background: #808080;
    color: #000;
}
.docs-homescreen-grid-item {
    background-color: #808080;
    border-bottom: 0px solid #c4c4c4;
    border-left: 1px solid #808080;
    border-right: 1px solid #808080;
    border-top: 1px solid #d3d3d3;
    box-shadow: 0 1px 0 rgba(0, 0, 0, 0.07);
    color: #000;
}
.docs-homescreen-docs .docs-homescreen-grid-item.goog-control-hover,
.docs-homescreen-docs .docs-homescreen-grid-item.goog-control-focused {
    box-shadow: none;
    border-bottom: 0px;
}
.docs-homescreen-grid-item-time-container {
    color: #FFF;
}
.gb_ub .gb_wb {
    background-position: 0 2150px;
}
/* Document itself */

#docs-chrome {
    background: #808080;
    color: #FFFFFF;
}
.docs-title-input {
    background-color: #484848;
}
.docs-title-input:focus {
    color: #FFFFFF;
}
.docs-title .docs-title-untitled,
.docs-title-input-label.docs-title-untitled {
    visibility: hidden!important;
}
.goog-control-hover {
    background-color: #484848!important;
    border-color: rgba(0, 0, 0, 0)!important;
    cursor: pointer;
    color: #ffffff;
    box-shadow: none;
}
.docs-title-save-label {
    color: #000000;
}
.kix-ruler-background-inner {
    background-color: #5A5A5A;
}
.kix-ruler-face-number {
    color: #FFFFFF;
}
#docs-editor {
    background: #292929;
}
.docos-anchoreddocoview .docos-replyview-first.docos-anchoredreplyview,
.docos-anchoredreplyview,
.docos-showrepliesbutton {
    background: #808080;
    border-bottom: 1px solid #292929;
}
.docos-anchoredreplyview-header .docos-anchoredreplyview-timestamp {
    color: #CECECE;
}
.docs-promo-action-row a,
a:link,
a:active,
a:hover,
a:visited {
    color: #0345B3;
}
.docs-material-appbar #docs-header .docs-titlebar-buttons,
.docos-anchoreddocoview .docos-replyview-first .docos-show-more,
.docos-anchoreddocoview .docos-replyview-first .docos-show-less,
.docos-anchoreddocoview.docos-docoview-active .docos-replyview-first .docos-show-more,
.docos-anchoreddocoview.docos-docoview-active .docos-replyview-first .docos-show-less,
.docos-anchoreddocoview .docos-draftdiscussionview-input-pane {
    background: #808080!important;
}
.docos-show-more, .docos-show-less {
    color: #0345B3;
}
.docos-layout-vertical .docos-anchoreddocoview .docos-anchoreddocoview-arrow-inner {
    border-right: 18px solid #808080;
}
.docos-anchoreddocoview {
    background-color: #808080;
}
}
