css:
@-moz-document url-prefix("https://www.google.com/cloudprint/dialog"), url-prefix("http://www.google.com/cloudprint/dialog") {
.cp-printdialog-printwidget-header div {
    text-align: center;
    height: 37px;
    width: 100%;
    background-image: url("https://www.google.com/images/nav_logo231.png") !important;
    background-position: -24px 0px;
    margin: 0px;
}
.cp-printdialog-printwidget-header div img,
.cp-printdialog-printwidget-filepicker,
.cp-printdialog-printwidget-close-button {
    display: none;
}
.cp-printdialog-search-input {
    width: 20%;
    text-align: center;
}
.cp-printdialog-printerpicker-search-box {
    padding-right: 0px;
    text-align: center;
}
.cp-printdialog-printer-content {
    text-align: center;
}
.cp-printdialog-printer-control-area {
    text-align: center;
    width: 100%;
}
.cp-printdialog-printer-inactive {
    width: 100%
}
.cp-printdialog-printerpicker-content {
    width: 30%;
}
.cp-printdialog-printwidget-header {
    width: 117px;
    height: 40px;
    left: 46%;
    top: 9px;
    max-width: 115px;
}
.cp-printdialog-printwidget {
    min-height: 0px;
    min-width: 0px;
    overflow: hidden;
    position: relative;
}
.cp-printdialog-printerpicker-content {
    width: 30%;
    margin-left: 35%;
}
.cp-printdialog-printer-info {
    float: none;
}
.cp-printdialog-jobpreview-printer-info,
.cp-printdialog-jobpreview-printer-info {
    cursor: default;
    float: none;
    text-align: center;
}
.cp-capabilities-capabilities-leftcol {
    display: table-cell;
    width: 100%;
    vertical-align: top;
    padding-right: 10px;
}
.cp-capabilities-capabilities-semantic-capability-container {
    display: block;
    float: none;
    margin: 10px 40% 10px 40%;
}
.cp-printdialog-printer-name,
.cp-printdialog-printer-desc {
    max-width: 330px;
    cursor: default;
    margin: auto;
}
.cp-printdialog-printer-info {
    margin-top: 10px;
}
.cp-printdialog-printwidget-header {
    width: 115px;
    height: 37px;
    left: 46%;
    top: 9px;
}
.cp-printdialog-jobpreview-printer-info,
.cp-printdialog-jobpreview-printer-info {
    width: 200px;
    float: right;
    text-align: right;
}
.cp-capabilities-checkbox-capability-toggle-label {
    width: 190px;
    text-align: right;
}
.cp-capabilities-capabilities-semantic-capability-container {
    display: block;
    float: none;
    margin: 10px 40% 10px 40%;
    width: 39%;
}
.cp-capabilities-select-capability-label,
.cp-capabilities-copies-label,
.cp-capabilities-pagerange-label {
    text-align: right;
}
.cp-printdialog-printwidget-footer {
    position: absolute;
    width: 100%;
    bottom: 100%;
    top: 8px;
}
body {
    background-color: #22252A;
}
.cp-printdialog-printer-active,
.cp-printdialog-printer-inactive {
    border-bottom: 1px solid #444;
}
.cp-printdialog-printer-inactive:focus>.cp-printdialog-printer-content,
.cp-printdialog-printer-inactive:hover>.cp-printdialog-printer-content,
.cp-printdialog-printer-active>.cp-printdialog-printer-content {
    background-color: #A9A9A9;
}
.cp-printdialog-printwidget-user-info {
    color: #FFF;
}
a:visited {
    color: #717171;
}
a:link {
    color: #4CB59D;
}
.cp-capabilities-capabilities-leftcol {
    color: #FFF;
}
.cp-printdialog-jobpreview-printer-name a:link,
.cp-printdialog-jobpreview-printer-name a:visited {
    color: white;
}
.cp-printdialog-jobpreview-header {
    border-top: #6E716E solid 1px;
    border-bottom: #6E716E solid 1px;
}
.cp-printdialog-printer-name,
.cp-printdialog-printer-desc {
    color: #CCCCCC;
}
.cp-printdialog-printer-info {
    margin-top: 0px;
}
}
