css:
@-moz-document domain("html-color-codes.info") {
/*
Made by Kizaro
To get info on next style created, Follow https://twitter.com/ViktorSIXth
*/

a:link {
    color: #73FF00 !important
}
a:visited {
    color: #FF8D00 !important
}
a:hover {
    color: #EFFF00 !important;
}
a:active {
    color: #EF00FF !important
}
#insertcolor {
    background-color: #570000!important;
    width: 615px;
    font-size: 20px;
    position: relative;
    text-align: right;
    padding: 5px;
    margin: 5px 0;
}
.htmlcode {
    border: 1px solid red!important;
    background-color: black!important;
    color: red!important;
    padding: 5px;
}
#container {
    background-color: #4A0000!important;
    height: 380px;
    width: 625px;
    position: relative;
}
#main {
    float: left;
    width: 730px;
    margin-left: 30px;
    color: #CE0000!important;
}
h1 {
    color: #8C0900!important;
    font-size: 40px;
    margin: 20px 0px;
}
h2 {
    color: #8C0900!important;
    font-size: 40px;
    margin: 20px 0px;
}
body {
    background-color: black!important;
}
.cc_container {
    background: #000!important;
    color: #A50000;
    font-size: 17px;
    font-family: "Helvetica Neue Light", "HelveticaNeue-Light", "Helvetica Neue", Calibri, Helvetica, Arial;
    box-sizing: border-box;
    border: 2px dotted #AD0101;
}
#footer {
    clear: both;
    background-color: #340000!important;
    margin-left: 10px;
    margin-right: 10px;
    height: 170px;
    padding-left: 50px;
}
.menusprite {
    background-color: #340000!important;
    background-repeat: no-repeat;
    margin-top: 9px;
    padding-bottom: 7px;
    border: 1.5px solid #FF0000!important;
}
._1drm ._1dro {
    bottom: 40px;
    left: 0;
    position: absolute;
    right: 0;
    top: 0;
    z-index: 1;
    background-color: black!important;
    border: 1.5px solid #FF0000!important;
}
._h7l>div {
    box-sizing: border-box;
    height: 100%;
    position: relative;
    width: 100%;
    background-color: black!important;
}
._h7n {
    background: #3A0000!important;
    border: 1.5px solid #FF0000!important;
    border-radius: 2px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .12);
    position: relative;
}
._1drm._1ds4 ._1z7u {
    background: #000000!important;
    border-top: 1px solid #5D0303!important;
    padding-top: 7px;
}
.menu ul {
    background-color: black!important;
    background-repeat: no-repeat;
    margin-top: 7px;
    padding-bottom: 8px;
}
.menu ul {
    background-color: black!important;
    height: 35px;
    list-style: none;
    margin: 0;
    padding: 0;
}
.menu li a {
    background-color: black!important;
}
._1drm._1b90,
._1drm._1ds4 {
    border: 1px solid #8A0F0F!important;
}
._1drm {
    background: #AD0000!important;
    overflow: hidden;
    position: relative;
}
._h7l {
    background: #730000!important;
    border: 1px solid #750000!important;
    border-top: 0;
    overflow-y: auto;
}
._1drm {
    background: #000!important;
}
._1drm._1b90, ._1drm._1ds4 {
    border: 1px solid #FF0000!important;
}
._1drm._1ds4 ._1z7u {
    background: #000000!important;
    border-top: 1px solid #FF0101!important;
}
._h7n {
    background: #000!important;
    border-color: #FF0000 #FF0000 #FF0000!important;
}
._h7l {
    background: #000000!important;
    border: 1px solid #EB0101!important;
}
}
