@-moz-document domain("animefillerlist.com") {
a:link{color:#C600B9 !important}
a:visited{color:#9E00FF !important}
a:hover {color:#FF00A0 !important;}
a:active{color:#F300FF !important}


/*Background middle*/
.FrontMessage {
    background: #000000!important;
}

/*Background Top*/
#Header {
    background: rgba(189,61,161,1);
background: -moz-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(189,61,161,1)), color-stop(6%, rgba(195,34,142,1)), color-stop(16%, rgba(164,55,135,1)), color-stop(31%, rgba(138,0,96,1)), color-stop(54%, rgba(138,0,96,1)), color-stop(79%, rgba(138,0,96,0.75)));
background: -webkit-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -o-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -ms-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: linear-gradient(to bottom, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%)!important;
        color: #BC3939!important;
}

/*Background general*/
body {
    background: #000000!important;
    color: #9400B5!important;
}

/*Background filler*/
table.EpisodeList tr.filler {
    background: #000000!important;
    color: #A14A40;
}

table.EpisodeList th {
    text-shadow: 0 1px 0 #000!important;
}

/*content right side*/
#Content > .Right h2 {
    background: rgba(189,61,161,1);
background: -moz-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(189,61,161,1)), color-stop(6%, rgba(195,34,142,1)), color-stop(16%, rgba(164,55,135,1)), color-stop(31%, rgba(138,0,96,1)), color-stop(54%, rgba(138,0,96,1)), color-stop(79%, rgba(138,0,96,0.75)));
background: -webkit-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -o-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -ms-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: linear-gradient(to bottom, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%)!important;
    background-color: rgb(93, 0, 0);
    border-bottom: #7F008E 2px solid!important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(38, 255, 0);
    color: #F300D3!important;
    font-family: "HelveticaNeue","Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 12px;
    margin-bottom: 10px;
    padding: 10px 15px;
    text-shadow: 0 1px 0 #000!important;
    text-transform: uppercase;
}

/*header episodes*/
#Condensed .Label {
    border-bottom: #7F008E 2px solid!important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(16, 151, 0);
    background: rgba(189,61,161,1);
background: -moz-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(189,61,161,1)), color-stop(6%, rgba(195,34,142,1)), color-stop(16%, rgba(164,55,135,1)), color-stop(31%, rgba(138,0,96,1)), color-stop(54%, rgba(138,0,96,1)), color-stop(79%, rgba(138,0,96,0.75)));
background: -webkit-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -o-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: -ms-linear-gradient(top, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%);
background: linear-gradient(to bottom, rgba(189,61,161,1) 0%, rgba(195,34,142,1) 6%, rgba(164,55,135,1) 16%, rgba(138,0,96,1) 31%, rgba(138,0,96,1) 54%, rgba(138,0,96,0.75) 79%)!important;
    display: block;
    font-weight: bold;
    text-shadow: 0 1px 0 #298F1C!important;
    overflow: hidden;
    padding: 5px 10px;
}
/*border header episodes*/
#Condensed {
    border: #7F008E 2px solid!important;
    border-top-color: rgb(0, 134, 16);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(0, 134, 16);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(0, 134, 16);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: rgb(0, 134, 16);
    border-left-style: solid;
    border-left-width: 1px;
    color: #3EB833!important;
    font-size: 14px;
    margin-bottom: 20px;
    overflow: hidden;
}

table.EpisodeList th.header {
    background-color: rgb(0, 0, 0)!important;
}
table.EpisodeList th.header.headerSortDown {
    background-color: rgb(0, 0, 0)!important;
}

#Condensed .Episodes {
    display: block;
    padding: 5px 10px;
    background-color: black!important;
}

/*episodes border*/
table.EpisodeList td, table.EpisodeList th {
    border-right: #7F008E 2px solid!important;
    border-bottom: #7F008E 2px solid!important;
    text-align: center;
    padding: 5px 10px;
}

table.EpisodeList {
    border-collapse: collapse;
    border-spacing: 0;
    border-top: #7F008E 2px solid!important;
    border-left: #7F008E 2px solid!important;
    border-right: #7F008E 2px solid!important;
    border-bottom: #7F008E 2px solid!important;
    margin-bottom: 20px;
    width: 100%;
}

.node-anime > .Details > .Right > .item-list {
    border-top: #7F008E 2px solid!important;
    border-bottom: #7F008E 2px solid!important;
    margin-bottom: 20px;
    padding: 10px 0;
}

element.style {
    color: rgb(226, 0, 205)!important;
}

.form-required, .form-item label {
    color: #9953D4!important;
    font-size: 13px;
    font-weight: normal;
    line-height: 13px;
}

ul.tabs li a {
    background: #102D40!important;
    border: none;
    border-radius: 5px;
    color: #ffffff;
    display: block;
    margin: 0;
    padding: 7px 15px;
    text-decoration: none;
    text-shadow: 1px 1px rgba(0,0,0,0.3);
}

#Filters a {
    background: #000000!important;
    border: #7F008E 2px solid!important;
    border-radius: 4px;
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
    color: #FFFFFF;
    float: left;
    height: 12px;
    margin-right: 5px;
    padding: 5px 10px;
}


#Filters {
    border-bottom: #7F008E 2px solid!important;
    font-size: 12px;
    height: 24px;
    line-height: 12px;
    margin-bottom: 20px;
}

#Condensed .filler .Label {
    border-top: #7F008E 2px solid!important;
}

#Content > .Right .block {
    border: #7F008E 2px solid!important;
    border-radius: 4px;
    margin-bottom: 15px;
}



#Content > .Right .block .MoreLink {
    border: #7F008E 2px solid!important;
    border-radius: 4px;
    display: block;
    margin-bottom: 18px;
    padding: 5px 0;
    text-align: center;
}

#Content > .Right .block .item-list li {
    border-bottom: #7F008E 2px solid!important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(0, 128, 27)!important;
    font-size: 14px;
    list-style: none;
    list-style-image: none;
    line-height: 1.3;
    margin: 0;
    overflow: hidden;
    padding: 8px 0;
}

h1, h2, h3, h4 {
    color: #8E018E!important;
}

.node-anime > .Details > .Right .field-name-body p {
    color: #9565FF!important;
    font-size: 14px;
}

.node-anime > .Details > .Right > .Contributors p {
    color: #9565FF!important;
    font-size: 12px;
    margin: 0;
}


#ShowList > .Group > h2 {
    background-color: #000000!important;
    border: 3px dashed #646EE7!important;
    font-size: 21px;
    margin: 0;
    padding: 5px 10px;
    text-shadow: 0 1px 0 #000!important;
}

#ShowList > .Group > ul {
    border-left: 1px solid #C000FF!important;
    border-left-width: 1px;
    border-left-style: solid;
    border-left-color: rgb(192, 0, 255)!important;
        border-right: 1px solid #C000FF!important;
    border-right-width: 1px;
    border-right-style: solid;
    border-right-color: rgb(192, 0, 255)!important;
        border-bottom: 1px solid #C000FF!important;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(192, 0, 255)!important;
        border-top: 1px solid #C000FF!important;
    border-top-width: 1px;
    border-top-style: solid;
    border-top-color: rgb(192, 0, 255)!important;
}

.form-item .form-textarea, .form-item .form-text {
    background: #000000!important;
    border: #6200FF 1px solid!important;
    border-radius: 0;
    color: #E755E6!important;
    font-size: 15px;
    font-weight: 300;
    line-height: 15px;
    outline: none;
    margin: 0 0 0 0;
    padding: 16px 16px;
    -webkit-appearance: none;
}


.form-item .description {
    background: #2D2D2D!important;
    font-size: 12px;
    font-weight: 300;
    margin-top: 10px;
    padding: 5px;
}

.Breadcrumb {
    color: #B43CF9!important;
    font-size: 12px;
    margin: 0 auto 10px auto;
    width: 960px;
}

.Filler .Bubbles .Bubble p {
    color: #AA65D2!important;
    font-size: 17px;
    text-align: center;
    margin: 0;
}

#Footer > .Right {
    color: #A253D0!important;
    font-size: 14px;
    float: right;
    line-height: 1;
    text-align: right;
    width: 25%;
}

.node-anime > .Details > .Right .field-name-body p {
    color: #B46BCA!important;
    font-size: 14px;
}
.FrontMessage .Buttons a {
    border: #CC04C6 2px solid!important;
}


table.EpisodeList tr td.Type span {
    background: #8B09BD;
    border-radius: 4px;
    border-color: purple;
    color: #000!important;
    font-size: 11px;
    font-weight: 800;
}



}
