css:
@-moz-document domain("1jux.net") {
a {
    color: 			#AAB3D0;
    }

a:hover, a:focus {
    color: 			#AAB3D0;
    }

::selection {
    text-shadow:		none;
    background-color:		#AAB3D0;
    color:			#fff;
    }

body {
    background-image:		url("http://ikillyou312.bplaced.net/Stylish/1Jux/lightnoise.jpg");
	background-color: #232323;    
}

.l-head {
    background-color: 		#18243F;
    }

.l-head .l-item.link:hover, .l-head .l-item.link.active {
    background-color:		#AAB3D0;
    color:			#18243F;
    box-shadow: 		0 0 0 3px rgba(255, 255, 255, 0.2) inset, 0 0 0 1px #000000 inset;
    }

.l-head .l-head-bottom {
    background-color: #636B83;
}

.l-head .l-head-bottom a {
    color: #eee;
}

.l-head .l-head-bottom a.active {
    font-weight: bold;
    color: #eee;
    box-shadow: 0 -2px 0 0 #AAB3D0 inset;
}

.l-head .l-item.blur {
    color:			#C8CDDF;
    box-shadow:			none;
    }

.l-head #xsearch-box #xsearch-field {
    background-color: 		#203052;
    box-shadow: 		0 0 0 3px rgba(60, 60, 60, 0.4) inset, 0 0 0 1px #000000 inset;
	}

.l-jux-menu-box a:hover {
    color:			#18243F;
    }

.jux-drop-menu.box .drop-content {
    background-color: #636B83;
}

.jux-drop-menu.box .drop-content .drop-link-list .drop-link i {
    background-color: #18243F;
}

.jux-drop-menu.box .drop-content .drop-login p {
    color: #eee;
    font-size: 13px;
}
.jux-drop-menu.box .drop-content .drop-login .drop-form-field .i-out .i {
    color: #eee;
}

.l-fan-buttons a span.title {
    color: #eee;
}

ul.posts li.post-item .l-post-box {
    background-color: #232323;
}

ul.posts li.post-item .post-image {
    background-color: #232323;
}

ul.posts li.post-item .l-post-box .l-post-head {
    background-color: #AAB3D0;
}

.l-jux-down-love .jux-love {
    background-color: #B6BFDE;
}

.l-el.white {
    background-color: #575757;
}

section.white {
    background: #575757;
}

section .l-title h1 {
    color: #eee;
}

ul.posts li.article .l-details {
    background-color: #636B83;
}

ul.posts li.article .l-details .article-head .title {
    color: #eee;
}

ul.posts li.article .l-details .article-description {
    color: #eee;
}

ul.posts li.article .l-details .article-footer {
    color: #eee;
}

body {
    color: #eee;
}

.forumbit_nopost .forumhead h2, .forumbit_post .forumhead h2 {
    overflow: hidden;
    border-left: solid 1px #636B83;
    border-right: solid 1px #636B83;
    width: 100%;
    background-color: #636B83;
}

.column-right {
    background-color: #636B83;
    padding: 24px 12px;
    border-left: solid 12px #232323;
    vertical-align: top;
}

body a {
    color: #AAB3D0;
    text-decoration: none;
}

.l-mod {
    background-color: #AAB3D0;
}

section.grey {
    background: none repeat scroll 0 0 #232323;
    border-bottom: 1px solid rgba(0,0,0,0.1);
}

.l-tabs .l-tab-head h5.active {
    box-shadow: 0 -3px 0 0 #636B83 inset;
}

.l-tabs .l-tab-head h5 {
    color: #eee;
}

.suggestions_list .l-item .t {
    color: #eee;
}

.suggestions_list .l-item .t .d {
    color: #eee;
}

.l-right .l-mod {
    background-color: #eee;
}

.simple-button:hover, .simple-button:focus {
    color: 			#FFFFFF;
    background-color: 		#636B83;
    }

.simple-button {
    color: 			#AAB3D0;

    }
}
