  /*===== Overall ====*/ 
  /* Background */
  body.site-center-aligned{background: #000!important;background:#000000 }
  /*===== HEADER ====*/ 
  
  /*== Header Bar & first horzontal blue bar ==*/
  
  .sorterbar th,#yt-masthead-container{
  background: #000 none repeat scroll 0% 0%;
  border-bottom: 1px solid #007095  ;
  }

  .yt-card{
    background: #000 none repeat scroll 0% 0%;
  }
  .yt-default h1, .yt-default h2, .yt-default h3, .yt-default h4, .yt-default h5, .yt-default h6, h1.yt, h2.yt, h3.yt, h4.yt, h5.yt, h6.yt{
    color: #00b2ee;
  }
  #watch-header .yt-user-info a{
    color: #00b2ee;  
  }
  
    /*== search bar ==*/
  
  #masthead-search-terms input{
    color: #00b2ee;
    background: #4c4c4c none repeat scroll 0% 0%;
  }
  .masthead-search-terms-border{
    border: 1px solid #007095 ;
    border-right-color:  #000;
  }
  .yt-uix-button{
    box-shadow: 0px 1px 0px #007095 ;
  }
  
  /*== search bar button ==*/
  
  #masthead-search .search-btn-component, #masthead-search .search-btn-component .start{
    background-color:  #8f8f8f;
    border: 1px solid #007095 ;
  }
  
  /*== Upload button == */
  #upload-btn{
    background:  #8f8f8f none repeat scroll 0% 0%;
    color: #4cc9f3  ;
  }
  
  /*== notification button ==*/
  
  #yt-masthead-user #sb-button-notify{
    background:  #8f8f8f none repeat scroll 0% 0%;
    border-bottom: 1px solid #007095  ;
  }
  
/*===== video description & subscribe =====*/
  
  /*== subscribes control ==*/
  .yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded[disabled], .yt-uix-button-subscribed-branded[disabled]:hover, .yt-uix-button-subscribed-branded[disabled]:active, .yt-uix-button-subscribed-branded[disabled]:focus, .yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-unbranded[disabled], .yt-uix-button-subscribed-unbranded[disabled]:hover, .yt-uix-button-subscribed-unbranded[disabled]:active, .yt-uix-button-subscribed-unbranded[disabled]:focus{

  border: 1px solid #000;
  background-color: #202020;
  color: #00b2ee;
  } 
  /*== subscribes border color ==*/
  .yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded[disabled], .yt-uix-button-subscribed-branded[disabled]:hover, .yt-uix-button-subscribed-branded[disabled]:active, .yt-uix-button-subscribed-branded[disabled]:focus, .yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-unbranded[disabled], .yt-uix-button-subscribed-unbranded[disabled]:hover, .yt-uix-button-subscribed-unbranded[disabled]:active, .yt-uix-button-subscribed-unbranded[disabled]:focus{
    border: 1px solid #000;
    
  }
  /*== channel notification control ==*/
  .yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus{
  border: 1px solid #000;
  background: #202020 none repeat scroll 0% 0%;
  color: #00b2ee;
  }
  /*== subscriber number ==*/
  .yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal{
    color: #00b2ee;
  }
   /*== subscriber number background ==*/
  .yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal{
    border: 1px solid #000;
  background-color: #202020;
  }
  /*== video view count ==*/
  .watch-view-count{
    color: #00b2ee;
  }
  /*== likes bar remove ==*/
  .video-extras-sparkbar-likes{
    background: transparent none repeat scroll 0% 0%;
  }
  /*== hate bar remove ==*/
  .video-extras-sparkbar-dislikes{
    background: transparent none repeat scroll 0% 0%;
  }
  /*== video info top horzontal  border ==*/
  #watch8-action-buttons{
    border-top: 1px solid #007095 ;
  }
  /*== watch control ==*/
  #watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover{
    background: #202020 none repeat scroll 0% 0%;
    opacity: 1;
    color: #00b2ee;
  } 
  /*== watch control ==*/
  #watch-description{
    color: #00b2ee; 
  }
  /*== show more control ==*/
  .yt-card .yt-uix-button-expander{
    color: #00b2ee;
    border-top: 1px solid #007095 ;
  }
   /*===== Comment Area =====*/
  /*All comments */
  .all-comments, .all-comments a{
  background-color: #202020;
  color: #00b2ee;
  text-transform: uppercase;
  }
  /*== comments color ==*/
  .comments .comment-text .comment-text-content{
    color: #00b2ee;
  }
  /*== comment standing box*/
  #yt-comments-sb-standin .box{
    color: #00b2ee;
    background-color: #202020;
    border: 1px solid #000000;
  }
  /*== commenting box ==*/
  .yt-commentbox-text, .yt-simplebox-text{
    color: #00b2ee;
    background-color: #202020;
    border: 1px solid #000000;
  }
  /*== arrow ==*/
  .yt-commentbox-arrow-inner, .yt-simplebox-arrow-inner{
    border-color: transparent ;
     top: 0px;
     left: 0px;
  }
  #yt-comments-sb-standin .callout-inner, #yt-comments-sb-standin .callout-outer{
    border-color:transparent ;
  }
  .yt-commentbox-arrow-outer, .yt-simplebox-arrow-outer{
    border-color:transparent ;
  }
  .yt-commentbox-text:focus + .yt-commentbox-arrow .yt-commentbox-arrow-outer, .yt-simplebox-text:focus + .yt-simplebox-arrow .yt-simplebox-arrow-outer{
     border-color:transparent ;
  }
  /*== reply button ==*/
  .comments .comment-footer-action{
    color: #4c66b4;
  }
  /*== comment time ==*/
  .comments .comment-header .time a{
    color: #4c66b4;
  }
  /*show more button*/
  .comments .paginator{
    color: #00b2ee;
    border: 1px solid #000;  
    background-image: linear-gradient(to top, #202020 0px, #202020 100%);
  }
  
  /*footer*/
  body #footer-container{
    background-color: #000;
    border-top: 1px solid #007095;
  }
  #footer-main{
    border-bottom: 1px solid #007095;
  }
  
}
