css:
@-moz-document domain("hyunsdojo.com") {
body {
    background: url('http://i.imgur.com/c9O3HLJ.png') 0 0;
}
.front_submit, .ptl_submit,.post_error,.ptl_up_btn{
    background: linear-gradient(to bottom, rgb(125, 30, 30) 0%, rgb(224, 143, 199) 100%);
}
#profile_top_footer {
    background: linear-gradient(to bottom, #FFF 0%, #C1A3A3 65px);
}
.forum_row_text_hi, a.mention_url{
    color: #867B2A;
}
.forum_row_right_topic a, .topictitle, a.topictitle:visited, [class="postlink"], .previewPosted a, #chat_tabs .chat_tab .chat_new_box .chat_box_messages a{
    color: #BF5E5E!important;
}
.forum_cat, .view_forum_top, .ptl_title, #ptl_menu .front_menu_a.label, #front_menu .front_menu_a.label, #chat_tabs .chat_tab .chat_new_box .chat_box_title, .button, .right_cat,.usr_cat,input[type=submit], button, #chat_tabs .chat_tab, .more_info h3,.ptl_cat,dd input, .submit-buttons input,.box_header,.box_footer {
    background: linear-gradient(to bottom, #2F2C2F 0%, #481717 100%);
}
.u_com_blue, .dropdown{
    background: linear-gradient(to bottom,#CC8078 0%,#A1475B 100%)!important;
}
.u_com_blue:hover, .logo:hover,.dropdownlist li.hover{
    background: linear-gradient(to bottom,rgb(173, 141, 138) 0%,rgb(202, 118, 161) 100%);
}
.logo, #favorites div, #notifications, #messages div, .post_info{
  -webkit-filter: grayscale(0%) brightness(60%) sepia(65%) hue-rotate(-50deg) saturate(500%) !important;
}
.activity_right a{
    color: #552200
}
#top-search:focus{
	border: 1px solid rgba(199, 81, 25, 0.7);
}
.mentions-input-box{
    background: -webkit-linear-gradient(top,#35291D 0%,#423C1D 100%);
}
.paginate,.paginate_btn{
    background: linear-gradient(to bottom,rgb(212, 203, 190) 0%,rgb(236, 227, 219) 100%)!important;
}
.forum_row_icon{
    background-image: url(http://i.imgur.com/wkD1o9x.png);
    background-repeat: no-repeat;
}
.forum_row_icon img{
    width:0px
}
.quotetitle, .attachtitle, .codetitle{
    border-color: #BFBB9C;
    background-color: #BFBB9C;
}
/*following written by m_x*/
.photo_view_image {
     max-width: 100% !important;
  	 width: auto !important;
    max-height: 100vh !important;
}



}
