css:
@-moz-document domain("play.pokemonshowdown.com"), domain("replay.pokemonshowdown.com") {
body {
    color: white;
    font-size: 11pt;
    font-family: Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, Verdana, sans-serif;
    font-style: oblique;
    font-style: normal;
    font-style: inherit;
    /* license: CC-BY source: http://commons.wikimedia.org/wiki/File:%22La_piedra%22_(2809466426).jpg */
    
    background: url("http://i.imgur.com/3lVsRjP.png");
    background-size: cover
}
.backdrop {
    display: block;
    content: "";
    background: url(http://i283.photobucket.com/albums/kk307/malmal718/backgrounds-3.jpg) !important;
    background-size: cover !important;
}
/**************
  * Buttons CSS *
  **************/

button[name="selectFormat"] {
    background: url("http://i.imgur.com/rkADfUd.png") bottom right no-repeat;
    background-color: #FFF;
    border: 1px solid #FFF;
    color: #000;
    text-shadow: 1px 1px 1px #FFF;
    font-size: 12px;
}
button[name="selectFormat"]:hover {
    background: url("https://upload.wikimedia.org/wikipedia/commons/thumb/f/f8/Color_icon_gray_v2.svg/220px-Color_icon_gray_v2.svg.png") top left no-repeat;
    background-color: #FFF;
}
.ladder th {
    background: url("https://upload.wikimedia.org/wikipedia/commons/thumb/f/f8/Color_icon_gray_v2.svg/220px-Color_icon_gray_v2.svg.png") bottom right no-repeat;
    background-color: #EEE;
    color: #EEE;
    text-shadow: 1px 1px 1px #EEE;
}
button[name="edit"],
button[name="delete"],
button[name="newTop"],
button[name="new"],
button[name="backup"],
button[name="back"],
button[name="import"],
button[name="saveImport"],
button[name="setStatFormGuesses"],
button[name="saveBackup"],
button[name="joinRoomPopup"],
button[name="close"],
button[name="login"],
button[name="revealMessages"],
button[name="pm"],
button[name="challenge"],
button[name="makeChallenge"],
button[name="dismissChallenge"],
button[name="addPokemon"],
button[name="refresh"],
button[name="setTimer"],
button[name="instantReplay"],
button[name="saveReplay"],
button[name="closeAndMainMenu"],
button[name="closeAndRematch"],
button[name="openSounds"],
button[name="openOptions"],
button[type="submit"],
button[name="logout"],
button[name="changepassword"],
button[name="undeleteSet"],
button[name="cancelSearch"],
.teambuilder-clipboard-button-right {
    background: url("https://writergirlkp.files.wordpress.com/2013/11/black-windows_542931.jpg") bottom right no-repeat;
    background-color: #000;
    border: 1px solid #000;
    color: #FFF;
    text-shadow: none;
    font-size: 12px;
}
button[name="edit"],
button[name="newTop"] {
    margin-bottom: 1px;
}
button[name="openSounds"],
button[name="openOptions"]
{
    margin-bottom: 8px;
}
button[name="edit"]:hover,
button[name="delete"]:hover,
button[name="newTop"]:hover,
button[name="new"]:hover,
button[name="backup"]:hover,
button[name="back"]:hover,
button[name="import"]:hover,
button[name="saveImport"]:hover,
button[name="setStatFormGuesses"]:hover,
button[name="saveBackup"]:hover,
button[name="joinRoomPopup"]:hover,
button[name="close"]:hover,
button[name="login"]:hover,
button[name="revealMessages"]:hover,
button[name="pm"]:hover,
button[name="challenge"]:hover,
button[name="makeChallenge"]:hover,
button[name="dismissChallenge"]:hover,
button[name="addPokemon"]:hover,
button[name="refresh"]:hover,
button[name="setTimer"]:hover,
button[name="instantReplay"]:hover,
button[name="saveReplay"]:hover,
button[name="closeAndMainMenu"]:hover,
button[name="closeAndRematch"]:hover,
button[name="openSounds"]:hover,
button[name="openOptions"]:hover,
button[type="submit"]:hover,
button[name="logout"]:hover,
button[name="changepassword"]:hover,
button[name="undeleteSet"]:hover,
button[name="cancelSearch"]:hover,
.teambuilder-clipboard-button-right:hover {
    background: url("http://i.imgur.com/rkADfUd.png") top left no-repeat;
    background-color: #EEE;
}
}
