@-moz-document domain('wikipedia.org'){
  #left-navigation,
  #right-navigation li,
  #siteNotice,
  #footer,
  a[title="Learn about new features"],
  .vectorTabs,
  .ambox,
  .plainlinks>tbody>tr>td>a>img {
    display: none !important;
  }
  .portal{
  display:none !important
}
  body {
    background: #f5f5f5 !important;
    font-family: 'Liberation Serif','Tinos','Times New Roman',serif !important;
  }

  h1,h2,h3,h4,h5,h6,h1>span,.body:before, #firstHeading{
      color:#AAA !important;
      background-color:transparent !important;
      border:none !important;
      border-bottom:1px solid #ccc !important;
  }
  a{
    background-color:transparent !important;
    -moz-transition:all 0.3s !important;
    -webkit-transition:all 0.3s !important;
    -o-transition:all 0.3s !important;
    transition:all 0.3s !important;
    text-decoration:none !important;
    border-radius:3px;
  }
  a:hover{
    color:white !important;
      background-color:#777 !important; 
  }
  .firstHeading{
    font-size:64px !important;
  }

  #head {
    left: 50px !important;
    right: 50px !important;
    width: auto !important;
    height: 61px !important;
    padding-top: 10px !important;
  }

  .navbox-title,.navbox-group,.navbox-abovebelow{
    background-color:transparent !important;
  }
  
  #head-base,
  #page-base,
  #mw-head-base,
  #mw-page-base {
    height: 80px !important;
    background: transparent !important;
  }
  #mw-head{
    position: fixed !important;
    top: 0 !important;
    right: 0 !important;
    width: 100% !important;
    background-color:white !important;
    box-shadow:0px 0px 20px 0px #ccc;
    z-index:1000;
  }

  #panel,
  #mw-panel {
    padding: 0 !important;
    top: 10px !important;
    left: 50px !important;
    width: 60px !important;
    height: 60px !important;
  }
  #right-navigation{
    float:left !important;
    margin-top:5px !important;
    margin-bottom:5px !important;
    margin-left:20px;
  }
  #p-logo,
  #p-logo a {
    position: fixed !important;
    top: 5px !important;
    left: 5px !important;
    width: 25px !important;
    height: 25px !important;
    color: #333 !important;
    text-shadow: 0 3px 2px #fff;
    z-index:1001;
  }
  #p-logo a{
    background-image:URL('data:image/png;base64,') !important;
    background-size:contain !important;
  }

  #searchform {
    margin-left: 30px !important;
  }
  #simpleSearch label {
    top: 2px !important;
  }
  #simpleSearch,#searchform{
    margin-top:0px !important;
  }
  #content {
    width: auto !important;
    max-width:60em;
    padding: 22px 30px !important;
    margin: 0px auto !important;
    background: #fff !important;
    box-shadow:0px 0px 20px 0px #ccc;
  }
  @media (max-width: 1440px) {
    #content {
      width:80% !important;
    	margin-left:10% !important;
    }
  }
  .mw-body h3 {
    margin-bottom:1.38em !important;
    margin-top:1.7em !important;
  }

#B14_1120_enWW_dsk_tx_tw{
     display: block !important;
     width: 50% !important;
     right: -48% !important;
     top: 5% !important;
     z-index: 1 !important;
     background-color: white !important;
}
#B14_1120_enWW_dsk_tx_tw:hover{
	right:0px !important;
}
  #siteSub {
    color: #888 !important;
    font-size: 11px !important;
    line-height: 1.5 !important;
  }
  .mw-body-content {
    font-size:1rem !important;
  }
  .mw-body-content ol,
  .mw-body-content ul {
    margin-top:1.6rem;
    margin-bottom:2.625rem;
  }
  .mw-body-content ol + ol,
  .mw-body-content ul + ul,
  .mw-body-content ol + ul,
  .mw-body-content ul + ol,
  .mw-body-content ul ul,
  .mw-body-content ol ol,
  .mw-body-content ol ul,
  .mw-body-content ul ol {
    margin-top:.43em;
  }
  .mw-body-content li {
    font-size:.875em !important;
    color:rgba(0,0,0,.9);
    opacity:.9;
  }
  #bodyContent h2 {
    margin-top:1.85em !important;
    margin-bottom:.5em !important;
    border-bottom:0 !important;
  }
  .mw-body h2 + p,
  .mw-body h2 + table + p {
    padding-top:.2em !important;
  }
  .thumbcaption {
    font-size:87.5% !important;
  }
  #bodyContent p {
    margin: 1.4em 0 1.4em !important;
    line-height: 1.6 !important;
    max-width:56em;
  }
  #bodyContent h2 + p {
    margin-top:.85em !important;
  }
  .mw-body sup.reference {
    font-size:.6em;
  }
  #bodyContent blockquote p {
    line-height:1.43 !important;
    opacity:.5;
    font-size:.9em !important;
  }
  #bodyContent blockquote > p:first-child {
    margin-top:0 !important;
  }
  #bodyContent h3 ~ p:first-of-type {
    margin-top:2.25em !important;
  }

  .infobox>tbody>tr>th,.infobox>tbody>tr{
    background-color:transparent !important;
      border:none !important;
      border-bottom:1px solid #ccc !important;
  }
  .suggestions-result-current{
    background-color:#777 !important;
  }
  .suggestions-results>a{
    padding:0px !important;
    margin:0px !important;
  }
  pre{
    white-space: pre-wrap;
  }
  .mw-body h1,
  .mw-body h2 {
    font-family:inherit !important;
  }

/*
Freecyber's fixed header option
http://forum.userstyles.org/discussion/39577/x
*/

  /* lock header in place  */
    #mw-head{
      position: absolute !important;
    }

  /* allow logo to scroll */
    #p-logo {
      position: relative !important;
      top: -5px !important;
      left: -45px !important;
    }
  #p-logo a{
      position: static !important; /* allow logo to scroll */
  }


}
