css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lesechos.fr") {
  
  /*Nettoyage*/
  
  header{
    /*display: none;*/
    filter: invert(80%)
  }
  
  .paywall-fx {
    display: none;
  }
  
  #colonne_gauche_article {
    display: none;
  }
  
  #droite_article {
    display: none;
  }
  
  .bloc-bas.centre {
    display: none;
  }
  
  footer {
    display: none;
  }
  
  /*Article large*/
  
  .contenu_article {
    width: initial;
  }
  
  #gauche_article {
    width: initial;
    text-align: justify;
  }
  
  
  /*Black theme*/
  
  body {
    background-color: #202020;
    color: #E8E8E8;
  }
  
  /*Attention cette élément fait partie du header, il est donc affecter par le filter invert*/
  h1, .h1-like {
    color: black;
  }
  
  #ariane a.current {
    color: #E8E8E8;
  }
  
  /*Slider-------------------------*/
  .flexslider, .flexsliderPhotos {
    background: #262626;
  }
  
  .flex-caption {
    background: #262626;
    color: #E8E8E8;
  }
  
  .flex-caption span {
    color: #5C5D5C;
  }
  
  .count-diapo {
    border-left: initial;
    color: #D9D9D9;
    background-color: #262626;
  }
  /*Slider-------------------------*/
  
  .contenu_article .chapeau-article {
    color: #E8E8E8;
  }
  
  .article article {
    background-color: #262626;
  }
  
  .contenu_article p, .contenu_article figcaption, .contenu_article ul {
    color: #E8E8E8;
  }
  
}
