css:
tabbrowser tabpanels 
{
 background-color: #000 !important;
}

.tabbrowser-tabs
{
 -moz-appearance: -moz-win-media-toolbox !important;
 background-position: bottom !important;
 padding-top: 0px !important;
 padding-right: 3px !important;
}

/* Common Settings */
.tabbrowser-tab {
 margin: 1px !important;
 border-width: 1px 1px 0 1px !important;
 -moz-border-left-colors:   rgba(255,255,255,.1) !important;
 -moz-border-right-colors:  rgba(255,255,255,.1) !important;
 -moz-border-top-colors:   rgba(255,255,255,.1) !important;
 -moz-border-radius: 4px 4px 0 0 !important;
 padding: 0px !important;
 height: 30px;
 -moz-background-clip: padding !important;
}

.tabbrowser-tab .tab-image-left,
.tabbrowser-tab .tab-image-right{
 width: 5px;
 height: 30px!important;
}
.tabbrowser-tab .tab-text {
 color: white !important;
 padding-bottom: 2px !important;
}

.tabbrowser-tabs .tab-close-button .toolbarbutton-icon,
.tabbrowser-tabs .tabs-closebutton .toolbarbutton-icon{
 padding-bottom: 4px !important;
 display: -moz-box !important;
}

.tabbrowser-tab:last-child,
.tabbrowser-tab:last-child:hover,
.tabbrowser-tab[selected="false"]:last-child:hover {
 margin-right: 15px !important;
}

.tabbrowser-tab .tab-image-left {
 -moz-border-radius: 3px 0 0 0!important;
 -moz-border-left-colors:   rgba(0,0,0,.5) rgba(0,0,0,.0) !important;
 -moz-border-top-colors:    rgba(0,0,0,.5) rgba(0,0,0,.0) !important;
 border-width: 2px 0 0 2px!important;
 border-style: solid!important;
 margin: 0!important;
 position:absolute!important;
 top:0!important;
 width:5px !important;
}

.tabbrowser-tab .tab-image-right{
 -moz-border-radius: 0 3px 0 0!important;
 -moz-border-right-colors:   rgba(0,0,0,.5) rgba(0,255,0,.0) !important;
 -moz-border-top-colors:    rgba(0,0,0,.5) rgba(0,255,0,.0) !important;
 border-width: 2px 2px 0 0!important;
 border-style: solid!important;
 margin:0 !important;
 position:absolute!important;
 top:0!important;
 width:5px !important;
 right: 0px !important;
}
.tabbrowser-tab .tab-image-middle {
 border-top: 2px solid #000;
 -moz-border-top-colors:   rgba(0,0,0,.5) rgba(0,0,0,.0) !important;
 margin: 0!important;
 height: 30px !important;
}
.tabbrowser-tab > .tab-image-middle > .tab-icon {
 margin-left: 1px !important;
}
.tabbrowser-tab > .tab-image-middle > .tab-icon > .tab-icon-image {
 margin: -1px 0 0 0 !important;
 -moz-margin-end: 0 !important;
 -moz-margin-start:0 !important;
}
.tabbrowser-tab > .tab-close-button {
 border-top: 2px solid #000 !important;
 -moz-border-top-colors:   rgba(0,0,0,.5) rgba(0,0,0,.0) !important;
 margin: 0 !important;
 padding: 0 !important;
 height: 30px !important;
}

/* Faviconized Tabs */
.tabbrowser-tab[faviconized="true"] > .tab-image-middle {
 -moz-padding-end: 0px !important;
 text-align:center!important;
}

.tabbrowser-tab[faviconized="true"] > .tab-image-middle > .tab-icon {
 -moz-margin-start: 6px!important;
}
.tabbrowser-tabs tab[faviconized="true"],
.tabbrowser-tabs[flowing=multibar][multibar] tab[faviconized="true"] {
 min-width: 40px !important;
 max-width: 40px !important;

}
.tabbrowser-tabs tab[faviconized="true"] + tab[faviconized="true"],
.tabbrowser-tabs[flowing=multibar][multibar] tab[faviconized="true"] + tab[faviconized="true"] {
 margin-left: -1px!important;
}

tab[faviconized="true"] .tab-text,
tab[faviconized="true"] .tab-close-button {
 display: none !important;
}

/* Selected Tabs */
.tabbrowser-tab[selected="true"] {
 font-weight: normal !important;
 background-image: none!important;
 background-color: rgba(41,20,4,.3) !important;
}
.tabbrowser-tab[selected="true"] .tab-image-left {
 -moz-border-left-colors: rgba(0,0,0,.7) rgba(0,0,0,.3) !important;
 -moz-border-top-colors:  rgba(0,0,0,.7) rgba(0,0,0,.3) !important;
 border-width: 2px 0 0 2px!important;
 border-style: solid!important;
 margin: 0 !important;
 position: absolute!important;
 top: 0!important;
}

.tabbrowser-tab[selected="true"] .tab-image-right {
 -moz-border-right-colors: rgba(0,0,0,.7) rgba(0,0,0,.3) !important;
 -moz-border-top-colors:   rgba(0,0,0,.7) rgba(0,0,0,.3) !important;
 border-width: 2px 2px 0 0!important;
 border-style: solid!important;
 margin: 0 !important;
 position: absolute!important;
 top: 0!important;
}

.tabbrowser-tab[selected="true"] .tab-image-middle {
 -moz-border-top-colors:   rgba(0,0,0,.7) rgba(0,0,0,.3) !important;
}

.tabbrowser-tab[selected="true"] > .tab-close-button {
 -moz-border-top-colors:   rgba(0,0,0,.7) rgba(0,0,0,.3) !important;
}

/* Not Selected Rollover */
.tabbrowser-tab[selected="false"]:hover {
 margin: 1px !important;
 background: rgba(0,174,203,.1) !important;
}

.tabbrowser-tab[selected="false"]:hover .tab-image-left {
 -moz-border-radius: 3px 0 0 0!important;
 -moz-border-left-colors:   rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
 -moz-border-top-colors:    rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
 border-width: 2px 0 0 2px!important;
 border-style: solid!important;
}

.tabbrowser-tab[selected="false"]:hover .tab-image-right {
 -moz-border-radius: 0 3px 0 0!important;
 -moz-border-right-colors:  rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
 -moz-border-top-colors:    rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
 border-width: 2px 2px 0 0!important;
 border-style: solid!important;
}

.tabbrowser-tab[selected="false"]:hover .tab-image-middle {
 border-top: 2px solid #000;
 border-bottom: 2px solid #000;
 -moz-border-top-colors:   rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
 -moz-border-bottom-colors:   rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
 height: 28px!important;
}
.tabbrowser-tab[selected="false"]:hover > .tab-close-button {
 -moz-border-top-colors:   rgba(0,0,0,.7) rgba(255,255,255,.2) !important;
}


/* Hide the line under the Tabbar */
.tabs-bottom 
{
 display: none!important;
 border-top: none!important;
 margin: 0px!important;
}

/* Multiple Row Tabbar */
.tabbrowser-tabs[flowing=multibar][multibar][classic=v3] {
 padding-bottom: 0px!important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab{
 margin: 1px 1px 2px 1px !important;
 border-width: 1px 1px 1px 1px !important;
 -moz-border-bottom-colors:   rgba(255,255,255,.1) !important;
 -moz-border-radius: 4px 4px 4px 4px !important;
 border-style: solid!important;
 padding: 0px !important;
 height: 26px!important;
 max-height: 26px !important;
 -moz-background-clip: padding !important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab > .tab-image-left {
 -moz-border-radius: 3px 0 0 3px!important;
 -moz-border-bottom-colors:    rgba(0,0,0,.5) rgba(0,255,0,.0) !important;
 border-width: 2px 0 2px 2px!important;
 border-style: solid!important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab > .tab-image-right {
 -moz-border-radius: 0 3px 3px 0!important;
 -moz-border-bottom-colors:    rgba(0,0,0,.5) rgba(0,255,0,.0) !important;
 border-width: 2px 2px 2px 0!important;
 border-style: solid!important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab > .tab-image-middle,
.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab > .tab-close-button {
 border-bottom: 2px solid #000 !important;
 -moz-border-bottom-colors:   rgba(0,0,0,.5) rgba(0,0,0,.0) !important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab[selected="false"]:hover > .tab-image-left {
 -moz-border-radius: 3px 0 0 3px!important;
 -moz-border-bottom-colors:   rgba(0,0,0,.7) rgba(255,255,255,.08) !important;
 border-width: 2px 0 2px 2px!important;
 border-style: solid!important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab[selected="false"]:hover > .tab-image-right {
 -moz-border-radius: 0 3px 3px 0!important;
 -moz-border-bottom-colors:   rgba(0,0,0,.7) rgba(255,255,255,.08) !important;
 border-width: 2px 2px 2px 0!important;
 border-style: solid!important;
}

.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab[selected="false"]:hover > .tab-image-middle,
.tabbrowser-tabs[flowing=multibar][multibar] > .tabbrowser-tab[selected="false"]:hover > .tab-close-button {
 border-bottom: 2px solid #000 !important;
 -moz-border-bottom-colors:   rgba(0,0,0,.7) rgba(255,255,255,.08) !important;
}

