/* POST BOX */

.post .post-info {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none;
    padding: 0px 0px;
    padding-bottom:0px;
    border-left: 2px solid #1976D2;
}
.post-info:hover {
    -webkit-transition: all 280ms cubic-bezier(.4, 0, .2, 1);
    transition: all 280ms cubic-bezier(.4, 0, .2, 1);
    box-shadow: 0 10px 20px rgba(0, 0, 0, .19), 0 6px 6px rgba(0, 0, 0, .23);
    border-left: 2px solid #1976D2;
}
.feed-posts .post, .feed-stickies .post {
    padding: 0 0 20px;
}
.post .promote {
    display: none;
    position: absolute;
    top: 0;
    height: 90%;
    width: 90%;
    border-radius: 0px;
    background: rgba(25,118,210,0.85);
    color: white;
    margin-left: 5%;
}
.shareme-btn.twitter {
    background: #55acee;
    visibility:collapse;
    height: 1px
}
.shareme-btn.facebook {
    background: #3b5999;
    visibility:collapse;
    height: 1px;
}
/* Post Title */
.post-title {
    font-size: 22px;
    padding-top: 15px;
    padding-bottom: 7px;
    border-bottom: 1px solid #1976D2;
}
.contextualinfo {
    background: #fff;
    position: relative;
    border: none;
    border-bottom: 1px solid #1976D2;
}
p {
    font-size: 18px;
    line-height: 1.5;
    margin: 0 0 0px;
}
/* Form BOX */

.post-form-wrap {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none
}
.richtext {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none;
    padding-left: 10px;
    border-left: 2px solid #1976D2;
}
.comment-form {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none;
    padding: 10px 10px;
    border-left: 0px solid #ecf0f1;
}
.post-media-wrap {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none;
    padding: 10px 10px;
    border-left: 2px solid #1976D2;
    max-width: 100%;
}
.topic-avatar {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none;
    padding: 10px 10px;
    border-left: 0px solid #1976D2;
    max-width: 100%;
}
.topic-wrap {
    background: #fff;
    position: relative;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border: none;
    padding: 10px 10px;
    border-left: 0px solid #1976D2;
    max-width: 100%;
}
li.notification.unread {
    background: #1976D2;
}
li.notification {
    background: #438FDA;
}
.notifications-menu li>a {
    display: block;
    clear: both;
    font-weight: normal;
    line-height: 1.428571429;
    color: #fff;
}
.new-comment {
    color: #1976D2;
}
.topics-menu li.menu-group {
    padding: 5px 10px;
    background: #1976D2;
    font-weight: bold;
    display: none;
    border-bottom: 2px solid #dddddd;
}
.dropdown-menu {
    background-color: #fff;
    border: none;
    display: block;
    margin-top: 8px;
    opacity: 0;
    padding: 0;
    visibility: hidden;
    -webkit-box-shadow: none;
    box-shadow: none;
    -webkit-transition: 0.25s;
    transition: 0.25s;
}
.dropdown-menu .dropdown-footer {
    text-align: center;
    padding: 5px;
    border-top: 2px solid #dddddd;
    background: #fff;
}
/* HIDE COMMENTS LEFT*/
.comments-count-wrap {
    padding-top: 45px;
    padding-left: 80px;
    visibility:hidden;
}
/* Table */
.richtext table thead tr th {
    background: #1976D2;
    font-size: 16px;
    color:white;
    font-weight:400;
}
.table-bordered>thead>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>tfoot>tr>td {
    border: 1px solid #438FDA;
}

/* Highlight Link */
.richtext a {
    font-weight: bold;
    color: #1976D2;
}

.nav-wrap {
    background: #1976D2;
    box-shadow: 0 3px 6px rgba(0, 0, 0, .16), 0 3px 6px rgba(0, 0, 0, .23);
}
li.dropdown a.new {
    color: white;
}
ul.notification-dropdowns>li>a {
    color: #99a6ab;
}
ul.notification-dropdowns>li>a:hover {
    color: black;
}
.navbar-default .navbar-nav>li>a {
    color: #fff;
}
.btn-offcanvas i {
    color: #fff;
}
.xp-user {
    background: #1976D2;
    color: #f1f1f1;
}
#search_form input {
    background: #438FDA;
    color: #F1F1F1;
    font-size: 18px;
}
.offcanvas .list-group-item {
    background-color: #1976D2;
    color: #FFFFFF;
}
header .nav-wrap .logo:hover {
    color: #2980b9;
}
header .nav-wrap .logo {
    color: #fff;
}
header .payout-rate-inline {
    color: #fff;
}
/* Background */

.auth {
    background: #ECECEC;
}
/* FORM INPUT */

.form-control {
    border-radius: 0;
    border-width: 0 0 1px;
    border-style: solid;
    border-color: rgba(0, 0, 0, .42);
    font: 400 15px/18px Roboto, sans-serif;
    box-shadow: inset 0 -1px 0 transparent;
    box-sizing: border-box;
    padding: 12px 4px;
    background: 0 0;
    width: 100%;
    resize: none;
    overflow: hidden;
}
.form-control:hover {
    border-color: #1976D2;
    border-width: 0 0 1px;
}
.form-control:active {
    border-color: #1976D2;
    border-width: 0 0 2px;
}
.form-control:focus {
    border-color: #1976D2;
    border-width: 0 0 2px;
}
.input-group-addon {
    /* 
visibility:hidden; */
    background-color: transparent!important;
    padding: 0px;
}
.select-link {
    text-transform: uppercase;
    border: none;
    border-bottom: 2px solid;
    border-radius: 0!important;
    text-align: center;
    -webkit-transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    padding: 8px!important;
    margin: 0!important;
    top: 1px;
    margin-bottom: 0;
    margin-top: 0;
    z-index: 1;
    border-bottom-color: #fff
}
.select-link:hover {
    border-bottom-color: #949494;
}
.select-image:hover {
    border-bottom-color: #949494;
}
.select-poll:hover {
    border-bottom-color: #949494;
}
.select-text:hover {
    border-bottom-color: #949494;
}
.select-poll {
    text-transform: uppercase;
    border: none;
    border-bottom: 2px solid;
    border-radius: 0!important;
    text-align: center;
    -webkit-transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    padding: 8px!important;
    margin: 0!important;
    top: 1px;
    margin-bottom: 0;
    margin-top: 0;
    z-index: 1;
    border-bottom-color: #fff;
}
.select-image {
    text-transform: uppercase;
    border: none;
    border-bottom: 2px solid;
    border-radius: 0!important;
    text-align: center;
    -webkit-transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    padding: 8px!important;
    margin: 0!important;
    top: 1px;
    margin-bottom: 0;
    margin-top: 0;
    z-index: 1;
    border-bottom-color: #fff;
}
.select-text {
    text-transform: uppercase;
    border: none;
    border-bottom: 2px solid;
    border-radius: 0!important;
    text-align: center;
    -webkit-transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    padding: 8px!important;
    margin: 0!important;
    top: 1px;
    margin-bottom: 0;
    margin-top: 0;
    z-index: 1;
    border-bottom-color: #fff;
}
.post-select{
	color:white;
    text-transform: none;
    border: none;
    border-bottom: 1px solid;
    border-radius: 0!important;
    text-align: left;
    -webkit-transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    transition: all 220ms cubic-bezier(.4, 0, .2, 1);
    margin: 0!important;
    border-bottom-color: #dddddd;
}
.post-form .post-select a.active {
    background: white;
    color: black;
    border-bottom-color: #2196F3;
}
.btn-primary {
    background: #2196F3;
}
.btn {
    margin-left: 8px;
    border-radius: 0px;
}
.btn-default {
    color: #222222;
}
.input-group-addon {
    padding: 10px 12px;
    font-size: 15px;
    color: black;
    text-align: center;
    background-color: #fff;
    border: 0px solid #bdc3c7;
    border-radius: 0px;
    -webkit-transition: border 0.25s linear, color 0.25s linear, background-color 0.25s linear;
    transition: border 0.25s linear, color 0.25s linear, background-color 0.25s linear;
}
.post-form .post-select a {
    background: #fff
}
}
