css:
@-moz-document domain("kshowonline.com") {
.container, .page-header, .navbar, .panel, #wrap, .border-contain, .thumbnail {
    background-color: #131417 !important;
}

font, a, td, th, .border-bottom, #view {
    color: #858A8C !important;
}

b {
    color: #74797A !important;
}

.panel-heading {
    background-color: #398ECE !important;
}

.panel-heading b {
    color: #2E2E2E !important;
}

.panel a {
    color: #428BCA !important;
}

.pager a, .btn, .form-group #keyword {
    background-color: #1A1A1A !important;
    color: #A9ACAE !important;
}

.episode span {
    color: #A9ACAE;
}

.dropdown.open a, .dropdown-menu{
    background-color: #0B0B0D !important;
}

/*------------Disqus Thread-------------*/

#disqus_thread {
    position: relative !important;
    top: 35px !important;
    background-color: #C0C1C1 !important;
    border-style: solid !important;
}

/*--------End Of Disqus Thread---------*/
}
