css:
@-moz-document domain("hyunsdojo.com") {
body {
    background: url('http://i.imgur.com/U8adVZ4.jpg') 0 0;
    
}
/*.front_submit, .ptl_submit,.post_error,.ptl_up_btn{
    background: linear-gradient(to bottom, rgb(125, 30, 30) 0%, rgb(224, 143, 199) 100%);
}*/
#profile_top_footer {
    background: linear-gradient(to bottom, #FFF 0%, #EAEAC6 100%)
}
.forum_row_text_hi, a.mention_url, .gensmall{
    color: #867B2A;
}
.forum_row_right_topic a, .topictitle, a.topictitle:visited, [class="postlink"], .previewPosted a, #chat_tabs .chat_tab .chat_new_box .chat_box_messages a{
    color: #31A230!important;
}
.forum_cat, .view_forum_top, .ptl_title, #ptl_menu .front_menu_a.label, #front_menu .front_menu_a.label, #chat_tabs .chat_tab .chat_new_box .chat_box_title, .button, .right_cat,.usr_cat,input[type=submit], button, #chat_tabs .chat_tab, .more_info h3,.ptl_cat,dd input, .submit-buttons input,.box_header,.box_footer {
    background: linear-gradient(to bottom, #2F2C2F 0%, #5F633E 100%);
}
.u_com_blue, .dropdown{
    background: linear-gradient(to bottom,#C4CE9D 0%,#70A285 100%);
}
.u_com_blue:hover, .logo:hover,.dropdownlist li.hover{
    background: linear-gradient(to bottom,rgba(214,224,186,1) 0%,rgba(185,202,138,1) 100%);
}
.logo, #favorites div, #notifications, #messages div, .post_info{
   -webkit-filter: grayscale(0%) brightness(80%) sepia(65%) hue-rotate(70deg) saturate(350%) !important;
}
.activity_right a{
    color: #552200
}
#top-search:focus{
		border: 1px solid rgba(148, 199, 25, 0.7)
}
.mentions-input-box{
    background: -webkit-linear-gradient(top,#35291D 0%,#484222 100%);
}
.paginate,.paginate_btn{
    background: linear-gradient(to bottom,rgb(241, 236, 229) 0%,rgb(219, 220, 203) 100%)!important;
}
.forum_row_icon{
    background-image: url(http://i.imgur.com/KETLeSc.png);
    background-repeat: no-repeat;
}
.forum_row_icon img{
    width:0px
}
.quotetitle, .attachtitle, .codetitle{
    border-color: #CBD2B4;
    background-color: #CBD2B4;
}
.post_right{
    background: linear-gradient(to bottom,rgba(243, 255, 230, 0.57) 0%,#C7C4C1 65px);
}
.post_author{
	text-shadow: 1px 1px 1px rgba(199, 196, 193, 0.5);
}
.post_author span{
    color: #4A523D!important;
}
#swf_desc_box{
    background: linear-gradient(to bottom,#C5C2BF 0%,#E4E2DF 25px);
}
#view_left{
    background: #E4E2DF;
}
#swf_comments_top{
    background: linear-gradient(to bottom,rgb(241, 237, 230) 0%,rgb(218, 215, 208) 100%);
}
#view_right{
    background: linear-gradient(to right,rgba(26, 62, 26, 0.15) 0%,rgba(105, 91, 39, 0.22) 80%,rgba(10, 251, 140, 0.05) 100%);
}
.activity_right{
    background: linear-gradient(to bottom,rgb(241, 237, 230) 0%,rgb(218, 215, 208) 65px);
}
#pm_list_top a, #pm_footer a{
	color: #FFF;
}
/*following written by m_x*/
.photo_view_image {
     max-width: 100% !important;
  	 width: auto !important;
    max-height: 100vh !important;
}
}
