css:
@-moz-document domain("dota2lounge.com") {
.box {
    background-color: #121212;
}
.whenm,
.eventm {
    color: #666;
    text-shadow: 0 0 0;
}
.half .teamBValue, .half .yourValValue, .half .teamAValue, .half .yourValValue {
color: #666!important;
    text-shadow: 1px 1px 1px #000;
}

.whenm span {
text-shadow:0px 0px 0px #4A7010;
}
.lost {
    text-shadow: none!important;
    color:#d12121!important;
}

.won {
    text-shadow: none!important;
    color:green!important;

}

.tradeheader, .bpheader {
    width: 98%;
    min-height: 16px;
    border-top: 1px solid #121212;
    padding: 8px 1%;
    float: left;
    background: #e3e3e3;
    background: radial-gradient(ellipse at top,#121212 0,#121212 70%);
}

.matchheader {
    float: left;
    width: 98%;
    padding: .6em 1%;
    border-top: solid 1px #121212;
    background: #e3e3e3;
    background: radial-gradient(ellipse at top,#111 0,#121212 70%);
}

#tradelist a {
    text-shadow: 0 0 0;
    color: #666;
}

.full span {
    font-size: 14px;
    color: #bbb;
    font-weight:bold;
}

.potwin {
    margin: 0 1%;
    font-size: .8rem;
    padding: 6px;
    float: left;
    margin-top: -4px;
    background: #171717;
    -webkit-border-radius: 5px;
    border-radius: 5px;
}

.notavailable {
opacity: 0.4;
}

.left,
.right {
    background-color: #333;
}
.match,
.winnings {
    background-color: #333;
    box-shadow: 0 0 0;
}
.matchleft {
    width: 495px;
}
.teamtext {
    background: #444;
    border-radius: 5px;
    padding: 5px;
    line-height: 1.3em;
    color: #000;
    text-align: center;
}
body {
    background-color: #444;
}

tr {
    border-bottom: 1px solid #000;
}

header {
background: linear-gradient(to bottom,#444 -28px,#121212 100%);
    
}
.winsorloses {
    background-color: #333;
}



tr:nth-child(odd) {
    background: #141414!important;
}

td[style*="font-weight:bold"] a{
    color:green;
}



tr[class*="details"] {
    background-color: #191919;
    border-left: 2px solid #000;
    color: #666;
    border-right: 2px solid #000;
}

.winsorloses a{
color:#bbb;
}

span.user {
color:#CCC!important;
}
}
