css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?:\/\/[^?]*(wikipedia|wikimedia|mediawiki|wikibooks|wikidata|wikinews|wikiquote|wikisource|wikiversity|wikivoyage|wiktionary)\.(org|net|com)\/.+') {
  a:hover, a#togglelink {
    text-shadow: none !important;
  }
  
  a, #mw-panel, .mw-editsection a, a#togglelink, #left-navigation, #right-navigation {
    transition: all 0.1s !important;
  }
  
  #mw-page-base {
    height: 2.5em !important;
  }
  
  #mw-head-base {
    height: 0 !important;
    margin-top: 0 !important;
  }
  
  #left-navigation, #right-navigation {
    margin-top: 0 !important;
  }
  
  #right-navigation {
    position: relative !important;
  }
  
  #pt-createaccount, #pt-mytalk, #pt-mycontris {
    display: none !important;
  }
  
  div#content.mw-body {
    box-shadow: none !important;
    border: none !important;
  }
  
  #p-personal ul {
    padding-left: 0 !important;
  }
  
  table.infobox tbody tr th {
    background: none !important;
  }
  
  /* Full Width */
  body {
    margin-left: 0 !important;
    margin-right: 0 !important;
    background-color: rgba(9%,9%,9%,1.0)!important;
    background-image: none !important;
  }
  
  body, #content.mw-body, #bodyContent p, 
    #bodyContent dl, 
    #bodyContent dd, 
    #bodyContent td, 
    #bodyContent li, #bodyContent blockquote, #mw-head {
    max-width: unset !important;
  }
  
  /* Search */
  #p-search {
    margin-top: .45em !important;
    border: 0px none !important;
  }
  
  #searchform {
    margin-top: 0 !important;
    border: 0px none !important;
  }
  
  div#p-search > form#searchform > div#simpleSearch {
    margin-top: 0 !important;
    height: 2em !important;
    border: 0px none !important;
  }
  
  #searchInput {
    padding-top: calc(.8em - 1px) !important;
    border: 0px none !important;
  }
}

/*@-moz-document regexp('https?:\/\/[^?]*(wikipedia|wikimedia|wikibooks|wikinews|wikiversity|wikiquote|wiktionary)\.(org|net|com)\/(?!.)') {*/
@-moz-document regexp('https?:\/\/[^?]*wikipedia\.(org|net|com)\/(?!.)') {
  body .central-featured-lang a.link-box {
    transition: all .2s !important;
  }
  body .central-featured-lang a.link-box:hover,
    body .divTop10item a:hover,
      body .central-featured-lang a.link-box:focus,
    body .divTop10item a:focus {
      background-color: transparent !important;
      box-shadow: unset !important;
      margin: 0 !important;
      padding: 0 !important;
    }
  
  body .central-featured-lang br, body .central-featured-lang em, body .central-featured-lang small {
    display: none;
  }
  
  .central-featured {
    margin-top: 2em !important;
  }
  
  /* Home */
  html /*> body#www-wikipedia-org*/  {
    background-image: linear-gradient(rgba(0,0,0,.5), rgba(0,0,0,.5)),url(http://i.imgur.com/R85450X.png) !important;
    background-size: cover;
    background-attachment:fixed;
    background-position: 0px 0px !important;
    background-color: rgba(9%,9%,9%,1.0)!important;
    background-repeat: no-repeat!important;
    background-attachment: scroll!important;
    background-position: center top!important;
    height: 100% !important;
    /*background-image: none !important;*/
  }
  
  html > body#www-wikipedia-org {
    background: none !important;
  }
  
  body#www-wikipedia-org .central-featured-logo {
    margin-top: -1.5em;
  }
  
  .search-container, #p-search.portlet, form.search-form, form.search-form fieldset {
    background: none !important;
    box-shadow: none !important;
    border: 0 !important;
  }
  
  /* Set search to theme's default search */
  div.search-container,
  div.search-container form.search-form,
  div.search-container form.search-form fieldset,
  .search-container form.search-form fieldset input#searchInput {
    background-color: transparent!important;
    color: rgba(70%,70%,70%,1.0)!important;
    box-shadow: none !important;
  }
  
  div.search-container form.search-form fieldset {
    margin: 0px !important;
    padding: 0px !important;
  }
  
  div.search-container form.search-form fieldset input#searchInput,
  div.search-container form.search-form fieldset input.formBtn {
    border: 0 !important;
    background-color: rgba(100%,100%,100%,0.05) !important;
    margin: 0 !important;
  }
  
  input[type=submit].formBtn {
    background-image: url('http://i.imgur.com/BNQuN.png')!important;
    background-repeat: no-repeat!important;
    background-position: 50% 60%!important;
    color: transparent !important;
  }
}
