css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dubtrack.fm") {

.room-item .name{
  color: magenta !Important;
}

.room-item .description{
  padding: 0px !important;
  margin: 0px !important;
  text-align: center !Important;
}

.room-item .current-song{
  position: relative !important;
  top: -2px !important;
  font-style: normal !important;
}

.room-item .user-info{
  padding: 0px !important;
  margin: 0px !important;
}
.room-item .room-user{
  float: right !important;
  position: relative !important;
  top: -1px !important;
  padding-right: 8px !important;
}

.room-item .name{
  font-size: 18pt !important;
  line-height: 24px !important;
  position: relative !important;
  padding-left: 4px !important;
  padding-right: 4px !important;
  top: 2px !important;
}

.room-item *{
  background-color: transparent !Important;
}

.room-item{
  background-image: linear-gradient(45deg, transparent 13px, black 13px, black 100%) !Important;
}

.room-item .user-info{
  opacity: 1 !important;
  background-image: linear-gradient(45deg, transparent 12px, #333 12px, #333 100%) !Important;
}

.room-item .user-count{
  background-image: linear-gradient(135deg, magenta calc(100% - 7px), transparent calc(100% - 7px), transparent 100%) !important;
}

.room-item FIGURE.roomImage:before{
  content: '';
  width: 9px;
  height: 9px;
  position: absolute !Important;
  left: 271px;
  top: 157px;
  background-image: linear-gradient(135deg, transparent 50%, black 50%, black 100%) !Important;
}

}
