css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sitv.ru") {
  
@font-face {  font-family: 'PT Sans';  font-style: normal;  font-weight: 400;  src: local('PT Sans'), local('PTSans-Regular'), url(https://fonts.gstatic.com/s/ptsans/v8/ATKpv8nLYAKUYexo8iqqrg.woff2) format('woff2');
}
  

body { font-family:'PT sans','Tahoma'; font-size:18px; background:#333 !important;  color:#666;}  
  #container {  margin:20px auto 3% auto;   padding:1.5% 2%; box-shadow: 0px 20px 15px 0px rgba(0,0,0,0.35); background:linear-gradient(#fff, #eee); }
  #footer:after {content:'Привет!'; font-size:.7em; font-weight:bold; margin:10px auto 0 auto; display:block;   text-align:center;}
  #header {margin:0 0 20px 0; font-size:.8em; top:0;  }
 
 /*paginator*/.current:before {content:'▼';  width:100%; display:block; float:none; clear:both;  text-align:center; margin:10px 0;}
  
  /*header*/
     .ban1, .ban2, .ban3, .ban4, .ban5, .ban6, .ban7, .ban10, .ban11 {margin:0;}
    .line1r,.line1wr,.line1menu {margin:0;}
    .line11 { height:64px; text-align:right; padding:0 20px 0 0;}
  .line11 a {color:#999; }
    .line11 a:hover {color:#eee; }  
  .rg { padding:3px 8px; max-width:150px; overflow: hidden; height:14px; margin:0;  background:linear-gradient(#C03, #e00000);   right:0; margin:0 ; text-align:center; line-height:1em;  text-decoration:none;}
  .rg a {  text-decoration:none; color:#fff;}
   .line12  {position:relative; width:50%; background:none; margin:0 100px 0 auto; height:20px; padding:0; top:-32px; left:0; }
   .line12 span { line-height:1em; font-size:.9em;}
 

  
  /*search*/
  .srch { width:200px; margin:0 0 0 20px; float:right;  }
  .srch .srchtxt { width:200px; border:0; background: #999; padding:0 4px;}
  .srch .srchtxt:hover {background: #eee;}
  .gsrch {display:none;}

    h1 hr, h2 hr {display:none}
    h2,H1 {color:#C03; font-weight:100; padding:5px 0 5px 0; }
  
  .impauth a {text-transform:uppercase; padding:14px 0;}
  
    /*leftp*/
  .impwrapper {display:block;}
    .impnews {padding:0; background:none;}
  .impnews .impn {padding-bottom:30px;}
    .impnews .impauth a,.rightcomm .head a, .stvnews .head a {color:#196ad8; }
  .blog .imphead a {color:#536988; font-weight:bold;}
    .impn {opacity:.8;  }
    .impn:hover {opacity:1;}
   .icons {padding:20px 0 0 0;}
  .blog .imptxt {padding:0 0 16px 7px;}  
  .vertmenu {background:none;}
  
  
  /*center*/
    .centernews img, .anons img, .rightnews img {border:none; padding:2px;}
  a.newshead span,.vote .res a {text-decoration:underline; color:#196ad8;}
    a.newshead span:hover {text-decoration:none !important; color:#196ad8 !important;}
    span.allnews {  background:#196ad8; color:#eee; display:inline; padding:6px; }

   .maincenter  {margin-top:-5px; padding:0 25px}
  .maincenterm {padding:0 25px}
   /**/
 
  /*efir*/
  .tvtxt {background:none; border:none; padding:50px 30px 0 0; font-size:.75em; color:#555;}
   .tvtxt span { display:block; margin:0; padding:0; line-height:.7em;}
  table .videoimg {margin:50px 0 0 0;}
 
  article {padding:5px 0;}
  p { text-indent:0; font-size:1em; line-height:1.5em; margin:0 0 10px 0;}
 
  table td {vertical-align: top;}
  
  
  
  .maincenterm .newstxt p {text-indent:-15px;}
  
  
  
  ol li {margin: 0 0 0 17px; padding:6px 0; font-size:.8em; }
    ol li a { font-size:1.2em;}
  

  
  
  /*нахуй*/ 
  .blink2 {animation:none}
  .tooltip,.narodbtn,#tw_social,#fb_social,.footrightfix, #leftlenta hr,.social ,.elfoto,#vk_social,.yashare-auto-init,.ban1, .ban2, .ban3, .ban4, .ban5, .ban6, .ban7,.ban8, .ban10, .ban11,.counter  {display:none !important;}
  
  /*в пизду*/ 
  #kardescr, #tkardescr,.anonsdescr,.rightnews a,.rightcomm .txt a,.icons span, .ticons span,.blog .impnext a,.listdescr,.addlinkshead,.commentsdecr,#karhead, #tkarhead,.newstime,.rightcomm .time, .stvnews .time, .rightnews .time,.stvnews .txt a,.vote .res,.allnews {font-style:normal; font-weight:100;}
  


  
  /*LINKS*/
  a, #kardescr a, #tkardescr a,.impnews .imptxt a,.blog .impnext a,.anonsdescr a,.rightnews a,.rightcomm .head a, .stvnews .head a,.rightcomm .txt a,.category a,#leftlenta a ,#ShowAddComment a, h2 a,.footmenu a,.TagsCloud a,.stvnews .txt a,.allnews {color:#196ad8; text-decoration:underline;}
   a:hover, #kardescr a:hover, #tkardescr a:hover,.impnews .imptxt a:hover,.impnews .impauth a:hover,.blog .imphead a:hover,.blog .impnext a:hover,.anonsdescr a:hover,newshead .txt:hover,.rightnews a:hover,.rightcomm .head a:hover, .stvnews .head a:hover,.rightcomm .txt a:hover,.category a:hover,#leftlenta a:hover,#ShowAddComment a:hover,.stvnews .txt a:hover {text-decoration:none !important;}
  
 
 
  .div1 {background:none;}
 
  
  #newslenta article,.maincenterm article {float:left; max-width:45%; padding:0 20px 10px 0; min-height:130px; }
.maincenter section article {float:left; max-width:45%; padding:0 20px 10px 0; min-height:100px; }
  
  .newstxt {padding:0 0 30px 0}

  
 /*carouselka*/
  .mainkar {background:#333 url("http://sitv.ru/image/h1.png") repeat-y scroll 0% 0%; padding:4px; }
 #kardescr,#karhead {padding:12px;}
  #kardescr, #tkardescr,.icons {background:none;}
  #kardescr {display:none}
 
/*anons*/
  .anons,.archnews,.vote {width:224px; font-size:.8em; }
  .anons img {display:none;}
  .anonshead {color:#c93558; }
  .anonsdescr,.mainleftm, .addlinkstxt span {font-size:.8em;}
     .addlinkshead {text-transform:lowercase;   font-size:.9em; }
  .addlinkstxt {  float:left; max-width:20%; padding:0 10px 20px 0;  min-height:145px; font-size:90%;}
  .addlinkstxt span {color:#999;  font-size: 70%;  vertical-align: top;  content: "+"; width:100%; height:10px; padding:0 2px; float:left}
 

  /*голосувалка*/
  .vote .head {padding:5px 0;}
  .vote label {text-decoration:none;}
  .vote .button {border:0; padding:3px 5px;}
 

  
  #leftlenta {color:#333;}
   #leftlenta article { padding:10px 0;}
  .elnewstime {background:none; float:right; font-size:.6em;}
  
  .addlinkshead a {opacity:.7;  }
 .videoimg {margin:0 20px 0 0; border:0;}
  
  .addlinkshead {margin:20px 0; font-size:1em;}
  .elnewshead {padding:0 0 20px 0; font-size:1.5em; color:#333; display:block; max-width:500px;}
 
  
  .maintv img {border:0;}
  
  
  /*коменты*/ 
 
     .rightcomm {background:#333;}
  .rightcomm .head a, .stvnews .head a {color:#196ad8;}
  .rightcomm .txt a   {color:#fefefe;}
    .rightcomm .txt a:hover, .stvnews .txt a:hover {color:#fefefe;}
  .maincomments {background:#333; padding:20px; margin:10px 0 ;}
  .maincomments H2 {color:#eee; padding:0}
  .commentshead,.commentsdecr {color:#999; }
  
    .commentstext {  width:80%; background: #555; color:#fff; padding: 15px ; position: relative; border:0; }
  .commentstext:before { content:""; display: block; width: 0;  height: 0;  border-top:0px solid transparent; border-bottom: 22px solid transparent; border-right: 20px solid #555;  position: absolute; top: 16px; left: -12px;}
  
  .commentstext .commentsdecr  { margin:6px 0 0 0;}
    .commentstext .commentsdecr  a:nth-child(1) {}


  
   #ShowAddComment {margin:10px 0; display:block;}
  #ShowAddComment a, input.button {text-transform: lowercase; text-decoration: none; color:#fff !important;  background:#C03 ; padding:4px 6px; }
    #ShowAddComment a:hover, input.button:hover,.rg:hover {background:#dd5555 ;}
    #ShowAddComment:before  {content: '.';    display: block;    height: 0;    clear: both;    visibility: hidden;}
  
   .newstime span {display:inline; }
  
  /*  footer   */
   footer { margin:20px 0 0 0; }
  .footmenu {background:none; border:none; width:75%; margin:0 auto 20px auto;}
   .footmenu li {padding:2px 0; font-size:.7em;}
  ul.category {width:55% ; margin:0 auto; }
  .category li { line-height:1em; height:1.5em; }
   .category a {padding:0 2px; text-transform: uppercase; }
  .footmenu a {text-transform: lowercase;  }
  .line3 {background:#eee; padding:0; border-top:2px dashed #ddd; border-bottom:2px dashed #ddd}
 
  .vidmenu {height:72px; }
  .vidmenu div {background:#555; }
 
  .vidmenu div a {border:2px solid #555; }
  
  .tagcloud { font-size:.9em; text-align:center;}

  .copyright {text-align:center;}
  
  
  /*form*/
 
   form p { display:block; float:left; padding:0px;  text-indent: 0; min-height:30px;}
    form:after, form input:before {clear:both;content: ''; display: block; }
    
  input {border:none; background:#eee; padding:4px;}
  
  
  
  
  
   
  
  
  
  
  
 
}
