css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@namespace html "http://www.w3.org/1999/xhtml";


scrollbarbutton[sbattr="scrollbar-up-top"],

scrollbarbutton[sbattr="scrollbar-down-bottom"]{

	display: -moz-box  !important;}



/*remove incremental scroll buttons located at scrollbar ends.  important note: only partially zero out height/width dimensions to prevent entire scrollbar from disappearing (ie, scrollbarbutton collapse results in scrollbar collapse) */

scrollbar[orient='vertical'] > scrollbarbutton { min-height: 20px !important; }

scrollbar[orient='horizontal'] > scrollbarbutton { min-width: 20px !important; }

scrollbar[orient='vertical'] {padding-left: 5px; padding-right: 4px; padding-bottom: 12px; box-shadow: inset 0 5px 5px -5px white,inset 0 -5px 5px -5px white!important;}

scrollbar[orient='horizontal'] {padding-top: 6px; padding-bottom: 5px; padding-left: 5px;padding-right: 5px; box-shadow: inset 5px 0 5px -5px white,inset -5px 0 5px -5px white!important; }



scrollbar > slider, scrollbar > slider > thumb, scrollbar > scrollbarbutton{ -moz-appearance: none !important;box-shadow: -2px 0 2px rgba(0,0,0,.5), inset 3px 0 3px rgba(0,0,0,.7)!important; }

scrollbar, scrollbar > scrollbarbutton{ border: 0px solid red!important; background: white !important; }

scrollbar[orient='horizontal'] > slider > thumb { min-width: 39px; background: -moz-linear-gradient(top, rgba(255,255,255,0.7) 3%, rgba(0,148,255,0.7) 27%, rgba(11,136,232,0.7) 39%, rgba(11,95,239,0.7) 50%, rgba(39,218,249,0.7) 81%, rgba(0,109,219,0.7) 100%); 
	
	box-shadow:inset 5px -2px 2px -2px rgba(43,198,255,.6), inset 3px 0px 3px 1px rgba(196,209,225,0.8), 0 0 3px #638FBE, inset 0px 4px 5px rgba(0,148,255,.5),inset -3px -2px 2px -2px rgba(43,198,255,.6), inset 0px 5px 2px white, 1px 0px 2px #4B687F,-1px 0px 2px #4B687F,0px 2px 2px #69DAF6!important;}

scrollbar[orient='vertical'] > slider > thumb { min-height: 39px;max-width: 45px!important; } 

scrollbar[orient='horizontal'] > slider > thumb { min-height: 39px;max-width: 45px!important; } 


scrollbar > slider {

	background: white; 
	
	box-shadow: inset 4px 0 4px grey;

	border-radius: 90px!important;;  

}

scrollbar > slider > thumb {


		background-size: auto;

	border-width: 0;

	border-radius: 90px!important;


}

	

	html|* > scrollbar > slider > thumb {

		background-attachment: scroll;border-radius: 90px!important;}
scrollbar[orient='vertical'] > slider{ margin-top: 5px!important;

	background-image: -moz-repeating-linear-gradient( 

		123deg,
		
 #EDEDED,
 #EDEDED 10px,
  rgba(127,182,25099,.9) 10px,
 rgba(127,182,25099,.9)  15px) !important;box-shadow: -1px 0px 1px rgba(128,128,128,1), inset 3px 3px 4px rgba(0,0,0,.5), 1px 0 1px white, 2px 0 3px 1px #E2E2E2, inset 7px 0 7px rgba(0,0,0,.4)!important; border-radius: 90px!important}


scrollbar[orient='vertical'] > slider > thumb{background: -moz-linear-gradient(left, rgba(255,255,255,0.7) 3%, rgba(0,148,255,1) 27%, rgba(12,158,249,.7) 50%, rgba(39,218,249,.7) 81%, rgba(0,109,219,1) 100%)!important; 
	


		background-size: auto;

  
box-shadow:inset -2px 5px 2px -2px rgba(43,198,255,.6), inset 0 3px 3px 1px rgba(196,209,225,0.8), inset 4px 0 5px rgba(0,148,255,.5),inset 2px 5px 2px -2px rgba(43,198,255,.6), inset 5px 0 2px white,0px 1px 2px #4B687F,0px -1px 2px #4B687F,1px 0px 2px #69DAF6!important;}


scrollbar[orient='horizontal'] > slider{box-shadow: inset 3px 0px 3px 1px rgba(196,209,225,0.8),  inset 0px 4px 5px rgba(0,148,255,.5), inset 0 5px 2px white!important;
	
	margin-left: 5px!important; margin-right: 5px!important;
	
	


	background-image: -moz-repeating-linear-gradient( 

		33deg, 

 #fff,
 #fff 10px,
  rgba(127,182,25099,.9) 10px,
  rgba(127,182,25099,.9)  15px)!important;
	
	box-shadow: 0px -2px 1px rgba(0,0,0,.4), inset 0px 3px 5px rgba(0,0,0,.5),0px 1px 1px white, 0px 2px 3px 1px #E2E2E2,inset 0 7px 7px rgba(0,0,0,.4)!important;  }




scrollbar > slider > thumb:hover, scrollbar > slider > thumb:active{

	box-shadow:0 0 2px rgba(115,216,240,.5), inset 0 3px 3px 1px rgba(196,209,225,0.8), 0 0 5px #638FBE, inset 4px 0 5px rgba(0,148,255,.5), inset 5px 0 2px white!important; 

	
	
}

scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {border-radius: 2px 2px 0 0; margin-top: 6px;

	background: url('http://i58.tinypic.com/m7dgdl.jpg')no-repeat center !important;background-size:75%!important;

box-shadow:  inset 0 -1px 3px -1px rgba(63,137,150,.8),inset 0 1px 3px -2px  rgba(63,137,150,.8), 0px 0 0 1px white, 0 1px 2px, inset 0px 0 0 1px  #99CCD6, inset 0 1px 2px -1px black, inset 0 4px 3px white,inset 0 -6px 3px rgba(239,239,239,.5)  !important;

background-color: rgba(107,230,255,.3)!important}



scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] {border-radius: 0 0 2px 2px;

  	background: url('http://i61.tinypic.com/f20d4n.jpg')no-repeat center !important;background-size:75%!important;

box-shadow:  inset 0px 1px 3px -1px rgba(63,137,150,.8),inset 0 -1px 3px -2px  rgba(63,137,150,.8), 0px 0 0 1px white, 0 1px 2px -1px ,inset 0 0 2px rgba(0,0,0,.2),inset 0 -2px 3px white, inset 0 -6px 3px rgba(239,239,239,.5)  !important;

background-color: rgba(107,230,255,.3)!important}



scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"]:active {box-shadow:  0 0 0 1px white, 0 1px 2px -1px, 1px -1px 1px  #F8FAF9, 1px 0 1px rgba(0,0,0,.2), -1px 0 1px rgba(0,0,0,.3), inset 3px 1px 5px rgba(0,0,0,.6),inset 0px 0 0 1px  #99CCD6, inset 0 1px 1px -1px black, inset 7px 0px 5px rgba(0,0,0,.2)!important;background-color:#66DBF3!important;}


scrollbar[orient="vertical"] > scrollbarbutton[type="increment"]:active {box-shadow:  0 0 0 1px white, 0 1px 2px -1px, 1px -1px 1px  #F8FAF9, 1px 0px 1px rgba(0,0,0,.2), -1px 0px 1px rgba(0,0,0,.3), inset 3px 1px 5px rgba(0,0,0,.6),inset 0px 0 0 1px  #99CCD6, inset 0 1px 1px -1px black, inset 7px 0 5px rgba(0,0,0,.2)!important;background-color:#66DBF3!important;}




scrollbar[orient="horizontal"] > scrollbarbutton[type="decrement"] {border-radius: 2px 0 0px 2px;

	background: url('http://i61.tinypic.com/2m6qx5i.jpg')no-repeat center !important;background-size:70%!important;

box-shadow:  inset -1px 0 3px -1px rgba(63,137,150,.8),inset 1px 0 3px -2px  rgba(63,137,150,.8), 0px 0 0 1px white, 1px 0 2px, inset 0px 0 0 1px  #99CCD6, inset 1px 0 2px -1px black,inset 0 2px 3px white !important;

background-color: rgba(107,230,255,.3)!important}



scrollbar[orient="horizontal"] > scrollbarbutton[type="increment"] {border-radius: 0 2px 2px 0px;

  	background: url('http://i59.tinypic.com/xnbx1v.jpg')no-repeat center !important;background-size:70%!important;

box-shadow:  inset 1px 0 3px -1px rgba(63,137,150,.8),inset -1px 0 3px -2px  rgba(63,137,150,.8), 0px 0 0 1px white, 1px 0 2px -1px ,inset 0 0 2px rgba(0,0,0,.2),inset 0 2px 3px white !important;
	
background-color: rgba(107,230,255,.3)!important}



scrollbar[orient="horizontal"] > scrollbarbutton[type="decrement"]:active {box-shadow:  0 0 0 1px white, 1px 0 2px -1px, -1px 1px 1px  #F8FAF9, 0px 1px 1px rgba(0,0,0,.2), 0px -1px 1px rgba(0,0,0,.3), inset 1px 3px 5px rgba(0,0,0,.6),inset 0px 0 0 1px  #99CCD6, inset 1px 0 1px -1px black, inset 0 7px 5px rgba(0,0,0,.2)!important;background-color:#66DBF3!important;}

scrollbar[orient="horizontal"] > scrollbarbutton[type="increment"]:active {box-shadow:  0 0 0 1px white, 1px 0 2px -1px, -1px 1px 1px  #F8FAF9, 0px 1px 1px rgba(0,0,0,.2), 0px -1px 1px rgba(0,0,0,.3), inset 1px 3px 5px rgba(0,0,0,.6),inset 0px 0 0 1px  #99CCD6, inset 1px 0 1px -1px black, inset 0 7px 5px rgba(0,0,0,.2)!important;background-color:#66DBF3!important;}

scrollbarbutton[sbattr="scrollbar-up-top"]{

display: none !important;

}

scrollbarbutton[sbattr="scrollbar-up-bottom"]{

display: -moz-box !important;

}
