@-moz-document domain("empeopled.com") {
/**
  * Empeopled SolarDark v0.2 by @teamvista
  * A Solarized Theme Adapted From Empeopled Slim by @Valaramech | CC BY-SA
  * https://userstyles.org/styles/115923/empeopled-slim
  */

@media (min-width: 768px) {
    .govern > .container > div, .post, .future {
        padding: 5px 0px;
    }
    html,
    body {
        font-family: "Roboto", sans-serif;
    }
/**
 * These contain a lot of whitespace which prevent them being collapsed properly
 */
    
    .row.stickie .actions .contextual {
        display: none;
    }
    /**
   * Slim down headers
   */
    
    .future .future-info h3,
    h3.post-title,
    .govern > .container h3 {
        font-size: 120%;
        font-family: "Roboto", sans-serif;
    }
    /* WIP CONTENT */
    
    .feed-stickies > h3.post-title:active,
    .feed-stickies > h3.post-title:hover {
        color: #859900;
    }
    /**
   * Slim down future text on list pages and all text on govern page
   */
    
    .future .future-info p,
    .govern > .container p {
        font-size: 100%;
        font-family: "Roboto", sans-serif;
    }
    /**
   * Slim down regular posts
   */
    
    .post .comments-count-wrap {
        padding-top: 20px;
    }
    .post .media-image-link,
    .post .media-image-wrap {
        height: 55px;
        width: 55px;
        background-size: cover;
    }
    .post .post-info .contextualinfo {
        display: inline-block;
        vertical-align: top;
        margin: 1px 0 5px;
    }
    :not(.discussion-content) > .post .actions {
        display: inline-block;
        margin: 5px;
        float: right;
        text-align: right;
    }
    .post .actions:empty:not(.fa) {
        display: none;
    }
    .post .stickie-dtf {
        bottom: initial;
        top: 25px;
    }
    .post .new-comment {
        position: absolute;
        top: 0;
    }
    /**
   * Slim down new topic voting list
   */
    
    .choice {
        padding: 10px 0;
    }
    .choice:first-child {
        margin-top: 15px;
    }
    .choice h3 {
        font-size: 120%;
        font-family: "Roboto", sans-serif;
    }
    .choice .choice-info {
        padding: 5px 30px;
    }
    .choice .choice-info p {
        font-size: 1rem;
        margin: 0px;
    }
    .choice .votes i {
        font-size: 35px;
    }
    .choice .votes .vote-sum {
        font-size: 16px;
    }
}

@media (min-width: 992px) {
    .post-info .col-md-2 {
        width: 10%;
    }
    .choice .left-info {
        padding-top: 10px;
    }
}

html,
body {
    background-color: #073642;
    color: #93a1a1;
}

a {
    color: rgba(255, 255, 255, 0.8);
    color: #93a1a1;
}

h3 > a {
    color: #93a1a1;
}

.feed .hero {
    border-bottom: none;
}

.post .stickie-hide-left,
.post .stickie-hide-right {
    background: #073642;
}

.post .post-info .contextualinfo,
.profile .comment-icon {
    color: rgba(255, 255, 255, 0.6);
}

.feed .hero,
.post .stickie,
.post .post-media-wrap .post-media,
.dropdown-menu li.menu-group,
.dropdown-menu li > a:hover,
.dropdown-menu li > a:active,
.dropdown-menu li > a:focus,
.dropdown-menu .dropdown-footer,
.comment .comment-content.highlight,
.profile .comment-icon-box,
.account .account-section,
.topic .description,
.topic-header,
.comment-form .input-group-addon,
.input-group-addon,
.profile .profile-header,
.govern-header,
.notifications-all .section-header,
.feed-notifications .notification.unread,
.feed-notifications .notification a:hover,
.comment .comment-media,
li.notification.unread {
    background: #0A4F61;
}

.post-form .post-select a {
    background-color: #0A4F61;
}

.navbar-default {
    background-color: #002b36;
}

.navbar .btn i,
.navbar-default .navbar-nav > li > a,
.post .post-poll .poll table tr td a.poll-vote,
header .nav-wrap .logo {
    color: #586e75;
}

.navbar-default .navbar-nav > .dropdown > a .caret {
    border-top-color: #93a1a1;
    border-bottom-color: #93a1a1;
}

.dropdown-menu {
    background-color: #002b36;
}

.dropdown-arrow {
    border-bottom-color: #002b36;
}

.mytopics-wrap .dropdown-arrow {
    border-bottom-color: #586e75;
}

.dropdown-menu li > a,
ul.notification-dropdowns > li > a,
.feed-notifications .notification a {
    color: rgba(255, 255, 255, 0.7);
}

.modal,
.modal a {
    color: #000;
}

.modal a:hover,
.modal a:active,
.modal a:focus {
    color: #859900;
}

.profile-topic .profile-topic-wrap .topic-badge {
    color: #303030;
}

.profile .comment-icon:hover {
    color: #93a1a1;
}

.comment-form textarea {
    background-color: #0A4F61;
    color: #a4b2b2;
    font-family: "Roboto", sans-serif;
}

.feed-notifications .notification {
    background: #657b83;
}

.live-xp i,
.user-level i {
    color: #586e75;
}

.post .post-poll .poll .choice-bar-wrap .choice-bar {
    background-color: #002b36;
}

.post .post-poll .poll .choice-bar-wrap {
    background-color: #93a1a1;
}

.richtext table thead tr th {
    background-color: #002B36;
}

.table-hover > tbody > tr:hover > td,
.table-hover > tbody > tr:hover > th {
    background-color: #0A4F61;
}

.xp-user {
    font-family: "Roboto", sans-serif;
    background: #002B36;
    padding: 5px 20px 15px 20px;
    color: #93a1a1;
    font-size: 18px;
    border-bottom: 2px #073642 solid;
}

.xp-user-name {
    color: #93a1a1;
}

.xp-user .xp-user-info .xp-user-display-wrap {
    background: #93a1a1;
}

.xp-user .xp-user-info .xp-user-display-wrap .xp-user-display {
    background: #073642;
}

#search_form input {
    font-family: "Roboto", sans-serif;
    background: #0A4F61;
    color: #93a1a1;
}

.offcanvas {
    background-color: #002B36;
}

.offcanvas .list-group-item {
    font-family: "Roboto", sans-serif;
    background-color: #002B36;
    color: #93a1a1;
}

.offcanvas .list-group-item:hover {
    background-color: #93a1a1;
    color: #002B36;
}

.section-header {
    background: #0A4F61;
}

.post .post-info {
    border-left: 2px solid #0A4F61;
}

.post .stickie .post-info {
    border-left: 2px solid #93a1a1;
}

.profile-topic .profile-topic-wrap .topic-badge {
    color: #073642;
}

.faq table th {
    background-color: #002B36;
}

.section-nav {
    background: #073642;
}

.form-control,
.ui-widget-content,
.ui-widget-content a,
.ui-widget-content ui-autocomplete-input,
.ui-menu-item,
ul.tagit {
    font-family: "Roboto", sans-serif;
    background: #0A4F61;
    color: #93a1a1;
}

.modal-content {
    background-color: #002B36;
}

.modal-title,
.modal-body {
    color: #93a1a1;
}

.tagit-choice .ui-widget-content .ui-state-default .ui-corner-all .tagit-choice-editable,
.ui-state-default,
.ui-widget-content .ui-state-default {
    background: #002B36;
}

.tagit-close,
ul.tagit li.tagit-choice .tagit-close {
    background: #002B36;
    color: #93A1A1;
}

.btn-default {
    background: #073642;
    color: #93A1A1;
}

.btn-default:hover {
    background: #93A1A1;
    color: #073642;
}

.post-form .preview_wrap .selector,
.post-form .preview_wrap .selector .thumbnail {
	background: #0A4F61!important;
}
}
