css:
@-moz-document domain("hyunsdojo.com") {
/*removes shadows*/
*:not(.divider){
    -webkit-box-shadow: none !important;
    box-shadow:none!important;
    border: none!important;
    text-shadow:none!important;
    border-radius: 0px!important;
}
#rating a{
    border-top: 1px solid rgba(255, 255, 255, 0)!important;
    border-bottom: 1px solid rgba(255, 255, 255, 0)!important;
}
/*removes bg texture*/
body{
    background:#555!important;
}
/*removes gradients*/
.post_right, #abbc3posttable td, .activity_right, #u_show_list_top, #ptl_list_top, .status_update{
    background:#ccc!important;   
}
#ptl_menu a:not(.ptl_submit), .u_comments_top, .front_menu_a:not(.front_submit),.paginate_btn.sel, .paginate_btn:active:not(.disabled){
	background:rgb(219,219,219);
}
#ptl_menu .front_menu_a.label, .ptl_title, #footer, .button, .forum_cat, input[type=submit], button, .dropdown, #front_menu .front_menu_a.label, .right_cat, .usr_cat, .view_forum_top, .more_info h3 {
    background:#3c3c3c;
}
#profile_top_footer, .new_post_body{
    background:#fff!important;
}
.top_foot_btn{
    height: 17px;   
}
#ptl_menu_bottom, #ptl_menu_fade, .right_data, .new_post_btn, #view_right, #swf_comments_top, .paginate_btn, #top-search, .subscribe_btn{
    background:#f0f0f0;
}
.top_foot_btn:before, .top_foot_btn:last-child:after, .top_stats:before, .top_stats:last-child:after, .new_post_footer{
    background:#fff;
}
.top_foot_btn.active{
	height: 15px;
    background:#f0f0f0;
}
#ptl_footer{
    width: 936px;
    background:#f0f0f0;
}
.ptl_submit, .front_submit, .logo:hover, .subscribe_btn .s_ico{
    background: rgba(214,224,186,1);
}
input.blue, .button.blue, button.blue, .u_com_blue{
    background:#3797ff;
}
.u_com_blue:hover, .dropdownlist li.hover{
    background: rgb(125,188,255);
}
.forum_row_right, .new_post_btn:hover, .checkbox span.toggle{
    background: #DADCDE;
}
#author_det{
    background: rgba(0,0,0,0);
}
#author_box{
     background: rgba(0,0,0,0.3);
}
#user_background_overlay, #user_background_drag{
     background: rgba(0,0,0,0.1);
     width: 130px;
     left: 50%;
     margin-left: -65px;
}
#swf_viewport, .mentions-input-box{
    background: rgb(41,41,41);
}
#swf_viewport_bottom, #chat_tabs .chat_tab{
    background: rgb(26,26,26) 
}
.drop_item{
	background:#e0e0e0;
}
.u_comments_box, #swf_desc_box, .rate_bg{
    background:#e9e9e9;
}
.rate_0{background:rgb(254,187,187);}
.rate_1{background:rgb(255,198,191);}
.rate_2{background:rgb(255,207,191);}
.rate_3{background:rgb(239,225,144);}
.rate_4{background:rgb(230,226,135);}
.rate_5{background:rgb(220,227,119);}
.rating_hover {margin-top: 0;}
.previewPosted{
    background:none;
}
.forum_row_icon img{
    width: 0px;
}
.forum_row_icon{
    background-image: url(http://i.imgur.com/OX0FNMD.png);
    background-repeat: no-repeat;
}
.row2{
	background: #e6e9ec;
}
#header {
	background-image: none;
    background: #222;
}
.post_button .icon {
    color: #555;
}
.post_button, .f_sm_name{
 	background:none;   
}
textarea:not(.tnt){
    padding: 12px;
}
/*following written by m_x*/
.photo_view_image {
     max-width: 100% !important;
  	 width: auto !important;
    max-height: 100vh !important;
}
}
