css:
@-moz-document url-prefix("https://play.google.com/music/") {
sj-home #pageIndicatorContainer.sj-home {
    display: none;
    flex-direction: column;
    align-items: flex-end;
    justify-content: center;
    position: fixed;
    top: 64px;
    bottom: 90px;
    right: 24px;
    z-index: 1;
    pointer-events: none;
    will-change: transform;
}

/* ---------------------------- NEW UPDATES ABOVE ---------------------------- */
/* ----------------------------  OLD STUFF BELOW  ---------------------------- */

.sj-search-box-1[opened] #suggestionList.sj-search-box {
    display: block;
    background: black;
}

.sj-search-box-1 #input.sj-search-box {
    font-size: 16px;
    color: #212121;
    padding: 0 72px;
    box-sizing: border-box;
    height: 48px;
    width: 100%;
    font-weight: 400;
    outline: none;
    background: transparent;
    border: 1px solid transparent;
    transition: border 0.1s ease-out;
    background-color: black;
}

body.qp #sliding-action-bar-container {
    background-color: black;
    color: #212121;
}

.gpm-bottom-sheet-0 {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: none;
    box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.4);
    position: absolute;
    visibility: hidden;
    right: 0;
    bottom: 120px;
    left: 0;
    z-index: 2;
    width: 600px;
    margin: 0 auto;
    outline: none;
    padding: 10px;
    background-color: white;
}

.gpm-loading-indicator-0 #contentWrapper.gpm-loading-indicator {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: none;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    position: relative;
    min-width: 32px;
    height: 32px;
    border-radius: 16px;
    font-size: 12px;
    color: #bdbdbd;
    background: #616161;
}

.sj-scrolling-module-0 {
    display: none;
}

.sj-home-0 #pageIndicatorContainer.sj-home {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: none;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    -ms-flex-align: end;
    -webkit-align-items: flex-end;
    align-items: flex-end;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    position: fixed;
    top: 64px;
    bottom: 90px;
    right: 24px;
    z-index: 1;
    pointer-events: none;
}

body.qp #material-app-bar #material-one-left .music-logo {
    background-image: url(http://i.imgur.com/w7KviLe.jpg);
    width: 100%;
    height: 100%;
    /* background-size: cover; */
    background-position: center center;
    background-repeat: no-repeat;
}

.sj-search-box-0 #input.sj-search-box {
    font-size: 16px;
    color: #e75b00;
    padding: 0 72px;
    box-sizing: border-box;
    height: 48px;
    width: 100%;
    font-weight: 400;
    outline: none;
    background: rgb(0, 0, 0);
    border: 1px solid transparent;
    transition: border 0.1s ease-out;
}

.cluster.has-more .header .more {
    display: none;
}

.sj-search-box-0[opened]:not(.animating) #suggestContainer.sj-search-box {
    transition: none;
    background: black;
}

.sj-search-box-0[opened]:not([num-suggestions="0"]) #input.sj-search-box {
    border-bottom: 1px solid #ef6500;
    transition: border 0.2s ease-in;
}

#player #material-player-right-side-wrapper #volume-container, #player #material-player-right-side-wrapper #volume-container #material-vslider, #player paper-icon-button[data-id="queue"], #time-container-duration, #time-slash {
       display: inline-block;
}

.material .song-row.selected-song-row td, .material .song-row.currently-playing td {
    background-color: #151515!important;
}

#topBar.paper-toolbar {
    position: relative;
    background: black!important;
}

#playlist-drawer .playlist-drawer-item .playlist-wrapper:hover, #playlist-drawer .playlist-drawer-item .playlist-wrapper:focus, #playlist-drawer .playlist-drawer-item sj-play-button:hover~.playlist-wrapper, #playlist-drawer .playlist-drawer-item iron-icon:hover~.playlist-wrapper, #playlist-drawer .playlist-drawer-item.playlist-drop-target:not(.subscribed) .playlist-wrapper {
    background-color: #4E4E4E;
}

#left-nav-close-button {
    color: #EF6C00!important;
    -webkit-align-self: center;
    -ms-flex-item-align: center;
    align-self: center;
    margin: 0 0 0 2px;
}

.narrow-layout.paper-drawer-panel > #drawer.paper-drawer-panel > [drawer] {
    border: 0;
    background: black;
    width: inherit;
}

.new-playlist-drawer #nav-container .nav-toolbar .menu-logo {
    max-width: none;
    width: 170px;
    margin: 0;
    flex: none;
    background: #948172!important;
    display: none;
}

#loading-progress {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: #000000;
    display: flex;
    align-items: center;
    justify-content: center;
}

#mainContainer.paper-header-panel {
    -ms-flex: var(--layout-flex_-_-ms-flex);
    -webkit-flex: var(--layout-flex_-_-webkit-flex);
    flex: var(--layout-flex_-_flex);
    -webkit-flex-basis: var(--layout-flex_-_-webkit-flex-basis);
    flex-basis: var(--layout-flex_-_flex-basis);
    position: relative;
    overflow-y: auto;
    overflow-x: hidden;
    -webkit-overflow-scrolling: touch;
    background: black;
}

.upload-progress-container {
    height: 100%;
    position: relative;
    background:black!important;
}

.paper-drawer-panel-0 #main.paper-drawer-panel > [main] {
    height: 100%;
    background: black!important;
}

.upload-progress-bar-thumb {
    background-color: #000000!important;
    height: 100%;
    left: 0;
    position: absolute;
}
.iron-image-0 {
    display: none!important;
    overflow: hidden;
    position: relative;
}
.iron-image-0 #placeholder.iron-image {
    background-color: black!important;
    display:none!important;
}
paper-drawer-panel iron-image#material-hero-image {
    background-color: #000000;
    display:none;
}

#drawer.paper-drawer-panel > [drawer] {
    height: 100%;
}

#player {
    height: 90px!important;
    min-height: 90px;
    background-color: #fff;
    z-index: 2;
    box-shadow: 0 0 8px rgba(0,0,0,.4);
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    position: relative;
}

paper-header-panel.paper-header-panel-0[mode=waterfall] #mainContainer.paper-header-panel {
    background: black!important;
}

paper-header-panel.paper-header-panel-0 {
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
}

paper-header-panel[mode=scroll] {
    overflow-y: auto;
    overflow-x: hidden;
    -webkit-overflow-scrolling: touch;
}

paper-header-panel {
    position: relative;
    height: 100%;
}

sj-mini-queue #queueContainer.sj-mini-queue {
    overflow-y: scroll;
    height: calc(100% - 120px);
    transform: translate(0, 0);
    background: black!important;
}

.material sj-paper-button.nav-item-container.sub .button-content, .material paper-button.nav-item-container.sub {
    padding: 0.4em;
    display: none;
}

.material-detail-view .material-container-details .info {
    padding: 16px 20px;
    box-sizing: initial!important;
    height: 264px;
    margin: initial!important;
    background: black!important;
}

sj-mini-queue #queueContainer.empty.sj-mini-queue {
    overflow: hidden;
    height: 100%;
    background: black;
}

#music-content {
    position: absolute;
    top: 48px;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: #000000!important;
    -webkit-transition: top .25s;
    -moz-transition: top .25s;
    -o-transition: top .25s;
    transition: top .25s;
}

#doc {
  position: relative;
  height: 100%;
  background: black!important;
}

.now-playing-menu .goog-menuitem, .now-playing-menu .goog-submenu, .now-playing-menu .goog-submenu .goog-submenu-arrow, .goog-menuitem {
    background-color: black!important;
    border: none;
    padding: 0;
    margin: 0;
    height: auto;
}

#player .rating-container {
    height: auto;
    line-height: 0;
    display: none;
}

.material .goog-menu {
    border: 0px solid transparent!important;
    border-radius: 2px;
    padding: 16px 0px!important;
    box-sizing: border-box;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.37)!important;
    max-width: 320px;
    max-height: 50vh;
    background: black!important;
}

#material-app-bar {
    background-color: black!important;
    color: white;
    z-index: 700;
    font-size: inherit;
}

paper-toolbar {
    display: block;
    position: relative;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
}

#nav {
    position: absolute;
    height: auto;
    background-color: #000!important;
    overflow-x: hidden;
    overflow-y: auto;
    margin: 0 4px 0 0;
    width: 220px;
    bottom: 0;
    right: 0;
    left: 0;
    top: 48px;
}

#player {
    position: relative;
    height: 56px;
    min-width: 850px;
    bottom: 0;
    background-color: #000000!important;
    color: black;
    z-index: 600;
    box-shadow: 0 0 8px #888888;
    box-shadow: 0 0 8px rgba(0,0,0,.4);
    cursor: default;
}

paper-header-panel.paper-header-panel-0[mode=scroll] #mainContainer.paper-header-panel {
    background: black!important;
}

.material-cluster .cluster-page {
  display: none;
  width: 100%;
}

.material .song-row .song-indicator, .material .song-row .title-right-items, .material .song-row .column-content, .material .song-row .song-indicator[data-playback-status="paused"], .material .song-row .song-indicator[data-playback-status="loading"], .material .song-row [data-col="index"] .hover-button[data-id="play"], .material .song-row [data-col="track"] .hover-button[data-id="play"] {
    background-color: black!important;
}

div {
    outline: none;
    color: rgb(239, 108, 0)!important;
    background: rgba(0, 0, 0, 0);
}

.material .nav-toolbar {
    background-color: black!important;
}

.fade-out:after {
    display: none!important;
}

core-header-panel#nav-container, paper-header-panel#nav-container {
    width: 240px;
    background: #000;
}

.cluster:not(.material-card-grid) .lane-content {
    width: 100%;
    overflow: hidden;
    white-space: nowrap;
    padding: 0 0 8px;
    margin: 0 0 -8px;
    display: none;
}

.cluster .header-inner {
    -webkit-flex: 1;
    -ms-flex: 1;
    flex: 1;
    margin-left: 16px;
    height: 100%;
    display: none;
}

.material .cluster-text-protection::before {
  display: block;
  position: absolute;
  height: 25px;
  left: 0;
  right: 0;
  top: -25px;
  content: "";
  background: #000000!important;
  z-index: -1;
}

.material .nav-item-container {
  font-size: 13px;
  color: #EF6C00!important;
  cursor: pointer;
  white-space: nowrap;
  overflow: hidden;
  height: 24px;
  line-height: 24px;
  margin: 0;
  text-indent: 18px;
  display: block;
  text-decoration: none;
  outline: none;
  padding: 10px 0;
  font-weight: 500;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

#nav_collections .nav-item-container:focus, .nav-item-container:focus, .nav-item-container:hover {
  background-color: #626262!important;
}

.material .nav-item-container.selected {
  color: #EF6C00!important;
  background-color: #626262!important;
}

#nav-container .nav-toolbar .menu-logo {
    max-width: calc(100% - 42px);
    margin: 0;
    -webkit-flex: 1 1 100%;
    -ms-flex: 1 1 100%;
    /* background: aliceblue; */
    flex: 1 1 100%;
}

.material .song-row .fade-out:after {
  background: black!important;
}

.material .song-row td, .material .upload-progress-row td {
  border-bottom: none;
  background-color: black!important;
  font-size: 15px;
  color: #EF6C00!important;
}

.material .song-table {
  padding: 16px 0;
  text-align: left;
  background: black!important;
}

.material .song-table [data-col="title"], .material .song-table.mini [data-col="song-details"] .song-title {
  color: #EF6C00!important;
}

.narrow-layout > #main > ::content[select="[main]"] > * {
  margin: 0;
  min-height: 100%;
  left: 0;
  right: 0;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  background: black!important;
}

.nav-toolbar {
  background-color: black!important;
  color: #EF6C00!important;
}

.song-table th {
  height: 36px;
  text-transform: uppercase;
  font-size: 12px;
  font-weight: bold;
  color: #EF6C00!important;
  background: black!important;
  text-indent: 8px;
  padding: 0;
  border-bottom: 1px solid #c96500;
  text-align: left;
  white-space: nowrap;
  overflow: hidden;
}

.entity-card.material-card .details .title {
  height: 36px;
  vertical-align: top;
  white-space: normal;
  overflow: hidden;
  display: inline-block;
  color: #EF6C00!important;
}

.material-card .play-button-container {
  position: absolute;
  height: 40px;
  width: 40px;
  right: 4px;
  bottom: 4px;
  background: rgba(255, 255, 255, 0)!important;
}

.material-card .title {
  display: block;
  overflow: hidden;
  white-space: nowrap;
  font-size: 15px;
  line-height: 18px;
  font-weight: 400;
  color: #EF6C00!important;
  text-decoration: none;
  cursor: pointer;
  outline: none;
  width: 0px;
  -webkit-flex: 1 1 100%;
  -ms-flex: 1 1 100%;
  flex: 1 1 100%;
}

.material-card .sub-title {
  display: block;
  overflow: hidden;
  white-space: nowrap;
  font-size: 13px;
  line-height: 16px;
  height: 16px;
  font-weight: 400;
  color: #EF6C00!important;
  margin-top: 4px;
  text-decoration: none;
  outline: none;
  width: 0px;
  -webkit-flex: 1 1 100%;
  -ms-flex: 1 1 100%;
  flex: 1 1 100%;
}

.material .cluster-text-protection {
  margin: 0 -80px;
  padding: 0 80px;
  color: #EF6C00!important;
  background: black!important;
  background-color: black!important;
}

.material .song-row .song-indicator, .material .song-row .title-right-items, .material .song-row .content, .material .song-row .song-indicator[data-playback-status="paused"], .material .song-row .song-indicator[data-playback-status="loading"], .material .song-row [data-col="index"] .hover-button[data-id="play"], .material .song-row [data-col="track"] .hover-button[data-id="play"] {
  background-color: black!important;
}

.material .song-row td a {
  color: #EF6500!important;
}
#player.material #material-player-right-wrapper {
  flex: 1;
  height: 86px;
  margin-top: 4px;
  align-items: center;
  display: flex;
  justify-content: flex-end;
  position: relative;
  min-width: 0px;
}


#player sj-icon-button[data-id="queue"] {
  margin-right: auto;
}

core-header-panel #music-content .situations-container {
  margin: -10px 0 0 0;
  display: none;
}

.material .cluster-text-protection {
  margin: 0 -80px;
  padding: 0 80px;
  color: #EF6C00!important;
  background: black!important;
  background-color: black!important;
    display:none;
}

.material .song-row.selected-song-row td {
    background-color: #2D2D2D!important;
}

.ups.light {
  background-color: transparent;
  -ms-flex-pack: center;
  -webkit-justify-content: center;
  justify-content: center;
  display: none;
}

.cluster .header.has-subtitle {
    display:none!important;
}
.has-queue-details #mini-queue-details {
    display: flex;
}
.material .nav-item-container.sub {
    display:none;
}

.material .nav-section-divider {
    margin: 12px 0px 12px 0px;
    border-bottom: 1px solid rgb(239, 108, 0)!important;
}

.material-detail-view .material-container-details .actions {
  margin-top: 8px;
  padding-top: 8px;
  border-top: 1px solid #e5e5e5;
  text-align: center;
  display: none;
}

.material-detail-view .material-container-details .info .container-stats-container .container-stats span {
  font-size: 13px;
  font-weight: 400;
  color: #EF6C00!important;
}

.song-table [data-col="index"] .content, .song-table [data-col="track"] .content {
  color: #EF6C00!important;
  padding-left: 2px;
  overflow: visible;
  font-weight: normal;
}

.material .song-row.hover, .material .song-row.hover td {
  background-color: #6C7F91;
}
}
