css:
/* author: Patrols, 2015 */

@namespace url(http://www.w3.org/1999/xhtml);
/* i really want this to be global */
.header__inner {
    height: 64px;
    background: #222 none repeat scroll 0% 0%;
    position: relative;
    z-index: 7;
}
.nav li a {
    position: relative;
    background: #0095DA none repeat scroll 0% 0%;
    transition: transform 0.3s ease 0s;
    transform-origin: 50% 0px 0px;
    transform-style: preserve-3d;
}
.header__air {
    float: right;
    border-left: 1px solid #2483C5;
    padding: 15px 16px 0px;
    height: 49px;
}

.article__date {
    font: normal 14px/18px "ArimoRegular", sans-serif;
    color: #F1F1F1;
}
.article__title {
    font: normal 32px/36px "RussiaLight", sans-serif;
    color: #F1F1F1;
    padding: 4px 0 24px;
}
.article__text {
    font: normal 16px/24px "OpenSansRegular", sans-serif;
    color: #F1F1F1;
    padding-left: 146px;
    padding-top: 24px;
}
.content__inner_wide, .content__inner_search {
    width: 75%;
    background-color: #222;
    border-bottom: 1px solid #ccc;
}
.breadcrumbs h2 a {
    color: #05B0FF;
    display: block;
    float: left;
    padding-right: 16px;
    /* border-left: 1px solid #FFF; */
    padding-left: 16px;
}
.breadcrumbs .breadcrumbs__all-link {
    display: block;
    float: right;
    position: relative;
    padding-right: 42px;
    color: #666;
}
.breadcrumbs {
    height: 22px;
    border-bottom: 1px dotted #222;
    padding-top: 16px;
    padding-bottom: 16px;
    font: normal 14px/55px "OpenSansSemibold", sans-serif;
    line-height: 22px;
    /* font-weight: 300; */
    font-family: "OpenSansLight";
}


@-moz-document domain("www.vesti.ru") {

    body {
        background: none !important;
        background-color: #222222 !important;
    }

    .content {
        background-color: #333333 !important;
    }

    .left_block_all {
        background-color: #222222 !important;
    }

    .top_line_text {
        background: none !important;
        border-left: 12px solid #222222;
    }

    .bottom_bg {background: none !important;}

    .banner_order_reclama {
        display: none !important;
    }

    .doc_all_padding h1, .doc_all_padding h1 a {
        color: rgba(170, 162, 162, 1) !important;
    }

    .doc_body {
        color: rgba(170, 162, 162, 1) !important;
    }

    .news_h a {
        color: rgba(170, 162, 162, 1) !important;
    }

    .doc_photo_block img {
        display: none !important;
    }
    
    .all_wrap {background: none center -2px no-repeat !important;}
    .top_line_text_bg1 { background: none right top no-repeat; }
    .slide_anons { display: none !important; }
.main-news .b-item, .main-news .b-item_list, .main-news .b-item_video-recommend-main, .main-news .b-item_video-nowwatch, .main-news .title, .main-news .aside-menu__title, .main-news .aside-menu__list-item, .main-news .video-popular__title, .main-news .video-nowwatch__title, .main-news .title_broadcasts, .main-news .air-radio .air-radio__player .air-radio__title, .air-radio .air-radio__player .main-news .air-radio__title {
    background: #222222;
}
.b-item .b-item__title, .b-item_list .b-item__title, .b-item_video-recommend-main .b-item__title, .b-item_video-nowwatch .b-item__title {
    color: #F1F1F1;
}
.link-to-list {
    display: block;
    /* font: 300 12px/18px "Roboto", sans-serif; */
    font: normal 12px/18px "RobotoLight", sans-serif;
    color: #F1F1F1;
    padding-top: 18px;
    padding-bottom: 2px;
    -moz-transition: all 0.3s;
    -o-transition: all 0.3s;
    -webkit-transition: all 0.3s;
    transition: all 0.3s;
}
.title, .aside-menu__title, .aside-menu__list-item, .video-popular__title, .video-nowwatch__title, .title_broadcasts, .air-radio .air-radio__player .air-radio__title {
    font: normal 14px/55px "OpenSansSemibold", sans-serif;
    color: rgb(213, 229, 241);
    height: 55px;
    text-transform: uppercase;
    border-bottom: 1px solid #ccc;
}
.vgtrk-bar-big-wrap {
    width: 100%;
    height: 39px;
    border-bottom: 1px solid #666666;
    background: #222222;
    position: relative;
    z-index: 12;
}
.vgtrk-bar-wrap li.root {
    float: left;
    display: -moz-inline-stack;
    display: inline-block;
    position: relative;
    width: auto;
    height: 40px;
    padding: 0 24px;
    margin-bottom: -1px;
    border-right: 1px solid #666666;
    /* font: normal 14px/39px "Arimo", sans-serif; */
    font: normal 14px/39px "ArimoRegular", sans-serif;
    color: #F1F1F1;
}
.b-item .b-item__inner, .b-item_list .b-item__inner, .b-item_video-recommend-main .b-item__inner, .b-item_video-nowwatch .b-item__inner {
    padding: 24px 24px 17px;
    border-width: medium 1px 1px;
    border-style: none solid solid;
    border-color: -moz-use-text-color #333 #333;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    margin: -1px 0px 0px -1px;
}
.content {
    min-height: 700px;
    border-left: 1px solid #333;
    border-right: 1px solid #333;
    padding-bottom: 38px;
    margin: 0px auto;
}    
}
