css:
@-moz-document domain("kennesaw.view.usg.edu") {
/***** LOGIN PAGE ITEMS******/
.d2l-login-portal-bordered {
    background-color: rgba(254, 188, 17, 0.78) !important;
    border-radius: 3px;
    border: 1px solid #291F07;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1) !important;
    padding: 5px 20px !important;
    max-width: 700px !important;
    margin-right: 60px;
    width: 340px;
}
div > #formId > div {
    padding: 5px 15px 0px 15px !important;
}
.vui-button {
    margin: 0.15em 0.3em 0.15em 0;
    background-color: #21211F !important;
}
.vui-button-primary:hover, .vui-button-primary:focus {
    border-color: #FEBC11;
}
.vui-button-primary, .vui-button-primary.vui-disabled:hover, .vui-button-primary.vui-disabled:focus, .vui-button-primary[disabled]:hover, .vui-button-primary[disabled]:focus {
    background: #FEBC11;
    border-color: #FEBC11;
    box-shadow: 0 1px 0 0 transparent inset;
}
.vui-list>.vui-selected {
    background-color: #616161 !important;
}
.d2l-edit:focus, .d2l-longedit:focus {
    border-color: #FEBC11;
    box-shadow: inset 0 1px 1px 0 transparent, 0 0 5px 0 transparent;
}
form#formId {
    background-color: #2F2F2F;
}
.d2l-field {
    color: #FFF !important;
    display: block;
    padding-bottom: 4px !important;
    text-align: left;
    letter-spacing: 0.5px !important;
    text-shadow: 0px 1px 0px #171000 !important;
    font-weight: bold !important;
}
.d2l-fieldgroup > li {
    padding-top: 3px !important;
    padding-left: 0px !important;
    padding-right: 0px !important;
}
.d2l_1_5_764 {
    margin: 10px 12px !important;
    padding-bottom: 5px !important;
}
#portalNavbar div.d_nb_cFull {
    background-color: #151414 !important;
    border-bottom: 0px solid transparent !important;
}
#portalNavbar div.d_nb_cGlobal {
    background-color: #000000;
    border-bottom: 3px solid #febc11;
}
.d2l-page-main.d2l-max-width {
    background-color: #2F2F2F;
}
.d2l-background-right > div > div {
    background-color: #151414 !important;
}
.d2l-background-left > div > div {
    background-color: #151414 !important;
}
img.d2l-navbar-image.d2l-navbar-image-left.d2l-left {
    -moz-box-sizing: border-box;
    background: url(https://i.imgur.com/o5ILlUq.png) no-repeat !important;
    width: 0px !important;
    height: 0px !important;
    padding-left: 267px !important;
    padding-top: 80px !important;
}
/****  MAIN PAGES   ***/
body {
    background-color: #2f2f2f !important;
}
a.vui-link {
    color: #FEBC11 !important;
}
.vui-link, .vui-link:visited {
    color: #FBCB50 !important;
    text-decoration: none;
    cursor: pointer;
}
.d2l-widget-content {
    background-color: #000000;
}
.d2l-msg-container {
    background-color: #2F2F2F;
    border: 0px solid transparent; 
}
.d2l-msg-container-inner {
    background-color: #2F2F2F;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend {
    color: #FFF !important;
}
.d2l-body {
    background-color: #151414 !important;
}
.d2l-htmlblock a {
    color: #FEBC11 !important;
}
.d2l-htmlblock a:visited {
    color: #FBCB50;
}
#d2l_navbar div.d_nb_cFull {
    background-color: #2F2F2F;
    background-repeat: no-repeat;
    background-position: 0% 0%;
    border-bottom: 3px double #000000;
    padding: 0px;
}
.d2l-collapsepane-header {
    background-color: #2F2F2F !important;
}
.d2l-menuflyout.d2l-menuflyout-open .d2l-menuflyout-text, .d2l-menuflyout-open .d2l-menuflyout-link-link {
    color: #FFFFFF !important;
}
.d2l-menuflyout-link.d2l-menuflyout-open .d2l-menuflyout-link-link {
    background-color: #616161;
}
.d2l-menuflyout.d2l-menuflyout-open .d2l-menuflyout-opener {
    background-color: #616161 !important;
}
.d2l-menuflyout-open .d2l-menuflyout-contents {
    background-color: #616161;
}
.d2l-datalist-style1 > .d2l-datalist > .d2l-datalist-item-active > .d2l-datalist-item-content {
    background-color: #525252;
}
.d2l-datalist-item-content > .d2l-inline > div {
	background-color: #3D3E58;
}
.d2l-collapsepane-content {
    background-color: #000000;
}
p > span > span {
	background-color: #000000 !important;
}
.d2l-page-main-padding {
    background-color: #2f2f2f;
    color: white !important;
}
table.d_g {
    background-color: #000000;
}
.d2l-page-bg > div {
    background-color: #2f2f2f;
}
table.d_g tr th.d_gl {
    background-color: #616161;
}
table.d_gl tr th.d_hch {
    background-color: #616161;
}
.d2l-action-buttons-item > a {
	color: #000000 !important;
    background-color: #DCDCDC !important;
}
.vui-button, .vui-button.vui-disabled:hover, .vui-button.vui-disabled:focus, .vui-button[disabled]:hover, .vui-button[disabled]:focus {
    border-color: #FEBC11;
    background: #2f2f2f !important;
}
.d2l-page-header-dark {
    background-color: #616161;
}
.d2l-column-flip-side {
    background-color: #000000 !important;
}
.d2l-page-bg > div {
    background-color: #2f2f2f;
}
.d2l-column-side-bg {
    background-color: #000000 !important;
}
.d2l-column-flip-main {
    background: #2F2F2F !important;
}
.d2l-editable-padding > .d2l-textblock {
	color: #000000 !important;
}
.d2l-contextmenu {
    background-color: #2f2f2f;
}
.d2l-contextmenu-item a {
    background-color: #2f2f2f;
}
ul.dcm li {
    background-color: #2f2f2f;
}
.d2l-datalist-style1 > .d2l-datalist > .d2l-datalist-item-selected > .d2l-datalist-item-content {
    background-color: #AFADA6;
}
.d2l-datalist-style1 > .d2l-datalist > .d2l-datalist-item-selected {
    background-color: #AFADA6;
}
.d2l-multiselect .d2l-multiselect-choice {
    background: #000000;
}
.d2l-fileinput-add {
    background-color: #2f2f2f;
}
.d2l-htmlblock > p > span {
    background-color: #000000 !important;
}
.drt > p > span {
    background-color: #000000 !important;
}
.d2l-datalist-style1 > .d2l-datalist > .d2l-datalist-item-active.d2l-datalist-item-selected {
    background-color: #616161 !important;
}
.d2l-datalist-style1 > .d2l-datalist > .d2l-datalist-item-active.d2l-datalist-item-selected > .d2l-datalist-item-content {
    background-color: #616161;
}
.d2l-messagearea-errors .d2l-msg-container-inner {
    border-color: #862810;
    background-color: #862810;
}
.d2l-datalist-nesteditem-accordion .d2l-datalist-nesteditem-accordion > .d2l-datalist-item-content, .d2l-le-TreeAccordionItem-SelectedRoot a > div, .d2l-le-TreeAccordionItem-SelectedRoot li > div > div {
    background: #616161;
}
div.ddial_o {
    background-color: #2f2f2f;
}
.ddial_t, .dpopup_t {
    background: #2f2f2f !important;
}
.ddial_f, .dpopup_f {
    background-color: #2f2f2f;
}
body.dbd_dialog div#d_content_l, body.dbd_dialog div#d_content_lw {
    background-color: #616161 !important;
}
ul.dtb_ul > li > a.dtbi_sel, ul.dtb_ul > li > div.dtbi_sel, ul.datb_ul > li > a.dtbi_sel, ul.datb_ul > li > div.dtbi_sel {
    background-color: #A5A5A5 !important;
}
ul.dtb_ul li a:hover, ul.datb_ul li a:hover, ul.dtb_ul li a:active, ul.datb_ul li a:active, ul.dtb_ul li a:focus, ul.datb_ul li a:focus {
    background-color: #808080 !important;
}
.D2LField_Modified {
    background-color: #616161;
}
table.d_g tr.d_ga {
    background-color: #616161 !important;
}
.d2l-panel {
    background-color: #616161;
}
/*** LIBRARY INFO CONTAINER  ***/
html ul.tabs li.active, html ul.tabs li.active a:hover, html ul.tabs li.active a {
    background: #616161;
}
ul.tabs li a:hover {
    background: #191919;
}
.tab_container {
    background: #616161;
}
.tab_content > div {
    background: #2f2f2f !important;
}
ul.tabs li a {
    background: #2F2F2F;
}
div.dco {
    background-color: transparent;
}
div.dco div.dco_c, div.dco_f div.dco_c {
    background-color: transparent !important;
}
table.d_gl tr.d_ggl1, table.d_gn tr.d_ggl1 {
    background-color: #000000;
}
.ddl_c > ul > li {
    background-color: #2f2f2f !important;
}
.d2l-fileviewer > div {
    background-color: #616161;
}
/*** CONTENT PAGE  ***/
.d2l-twopanelselector-wrapper {
    background-color: #2f2f2f;
}
.d2l-twopanelselector-padding {
    background-color: #2f2f2f;
}
.d2l-datalist-nesteditem-accordion, .d2l-le-TreeAccordionItem-wrapper {
    background: #616161;
}
.d2l-datalist > .d2l-datalist-nesteditem-accordion .d2l-datalist >.d2l-datalist-nesteditem-accordion.d2l-le-DataListItem-Selected > .d2l-datalist-item-content.d2l-datalist-item-content-first, .d2l-le-TreeAccordionItem-Selected > a > div {
    background: #484848;
}
.d2l-datalist > .d2l-datalist-nesteditem-accordion .d2l-datalist > .d2l-datalist-nesteditem-accordion.d2l-datalist-item-active > .d2l-datalist-item-content, .d2l-le-TreeAccordionItem-Active > a > div {
    background: #2f2f2f;
}
.d2l-right > div > .d2l-textblock.d2l-textblock-strong {
    color: black !important;
}
.d2l-right > div > div > .d2l-textblock.d2l-textblock-strong {
    color: black !important;
}
div > span.d2l-textblock.d2l-textblock-strong {
    color: white !important;
}
.d2l-left > .d2l-textblock.d2l-textblock-strong.d2l-left {
    color: white !important;
}
table.d_gd tr.d_gd, table.d_gl tr.d_gd {
    background-color: #252525;
}

/***  DISCUSSIONS  ***/
.d2l-grid > thead > tr > .d2l-grid-cell-sel, .d2l-grid-header-cell {
    background: #000000 !important;
}
.d2l-grid > tbody > tr {
    background-color: #616161;
}
.d2l-le-disc-post {
    background-color: #616161;
}
.vui-moreless-blur {
    background: linear-gradient(rgba(255, 255, 255, 0) 0%, #616161 100%) !important;
}

/***   Dropbox  ***/
table.d_gd tr {
    background-color: #616161;
}
table.d_gd tr.d_ggl2 {
    background-color: #2f2f2f;
}
table.d_g tr th.d_gc {
    text-align: center;
    background-color: #000000;
}

/***  MINI CALENDAR ***/
.d2l-calendar-mini {
    background-color: #616161;
    color: white !important;
}
.d2l-calendar-mini-weekend {
    background-color: #808080;
}
.d2l-calendar-mini-day {
    color: #FFFFFF;
}
.d2l-calendar-mini-other {
    color: #D2CBB8;
}
.d2l-calendar-mini-event {
	color: #FFFDB5;
}
.d2l-calendar-mini-today {
    color: black !important;
}
}
