@namespace url(http://www.w3.org/1999/xhtml);

/* стр. юзеров со списком стилей  */
@-moz-document  
    url-prefix("https://userstyles.org/d/users/") ,
    url-prefix("https://userstyles.org/d/styles/") /* ,
    url-prefix("https://userstyles.org/users/") ,
    url-prefix("https://userstyles.org/styles/")    */
{

HTML ,
HTML > *  ,
BODY {
    background:  transparent  !important;
}  
  
dl > dd > a {
    font-weight: bold !important;
}  

HTML > BODY > UL > LI > A  {
  padding-left: 6px !important;
  padding-right: 6px !important; 
}   
/* Log out */  
HTML > BODY > UL > LI > A[href="/logout"] {    
  color: #c30  !important; 
  text-shadow:  none !important;   
  text-decoration:  none !important;  
} 
HTML > BODY > UL > LI > a[href="/logout"]:hover  {
   color: #fff  !important;
   background: #ee0000  !important;
   outline-color:  #ee0000 !important;
} 

}     /* url-prefix("https://userstyles.org/users/ -------- */




/* *** MAIN  ********************************************* */

/* userstyles.org and forum 
https://userstyles.org/styles/119762/userstyles-org-and-forum-userstyles-org
*/

@-moz-document  url-prefix("http://userstyles.org") , 
               url-prefix("https://userstyles.org")  
   /* url("http://userstyles.org") , 
  url("https://userstyles.org") ,
  url-prefix("https://userstyles.org/styles/")  */
{
 
HTML {
  margin-left: -1px !important;
}
HTML > BODY  {
  margin-top: 6px !important;
  min-width: 92% !important; 
  width: 93% !important; 
  max-width: 95% !important;  /* 990px  */
}
  
HTML ,
#navigation    /*  (left column) */
{
  background: #F9FBFD  /* fixed */    fixed
/*       url("http://pikbox.ru/img/15/201606132119523072880827d1f172f8e334b462d827f9724.png") */
    url("http://jpegshare.net/images/28/80/2880827d1f172f8e334b462d827f9724.png")   ;
}
HTML > BODY , 
#root  , 
#root  > div ,  
#container ,
#nav-view , 
#nav-view  > #view  ,
#view_inner  , 
#iframe  
{
  background: transparent  !important;
}
  
  
/*  линки  */ 
a  {    
  color: #3D6AB8  ;
  text-shadow:  none !important;  
  text-decoration:  none !important;  
} 
a:hover {    
  color: #CC0000 !important;   /*  D44406 CC0000 */
/*   text-decoration: underline  !important;  */
}   

a:visited {    
  color: #884BAD !important;
} 
a:visited:hover {    
  color: #AF3298 !important;
} 
      
  
  
  
/* на тёмном фоне */   
  
/*   header */  
#header  {
  box-shadow:  0 0 12px  0px   #333     !important;  
  background-color: #314A63  !important; 
  background-image: -moz-linear-gradient(to bottom, #314A63 , #5581AE ) !important;       
}  
/*   лого */
#top_logo {
  margin-left: -20px !important;
  margin-right: 36px !important;
  margin-top:  10px !important;
  transition: all 1150ms ease  !important;
}    
#top_logo:hover {
  transform:  scale(1.2)  ; 
  transition: all 350ms ease  !important;
}  
  
/*  поиск */
  #search_input , 
  #create_new_style   {
    border: 1px solid #fff !important; 
    border-radius: 8px !important;     
  }   
  #search_input {
    width:  310px   !important;
    max-width:  320px !important;
  } 
/*  строка поиска */  
  #input_text_search {
    font-weight: bold !important; 
    font-size: 1.2em !important; 
  }   
  #search_input:hover  {
    border-color:  #FFFF00 !important;
  } 
  #search_input:focus  {
  } 
  #input_text_search:hover  {
  } 

/* Create New Style */
#create_new_style:hover   {         
  border-color:  #FFFF00 !important;
}   
#create_new_style a , 
#header #fourm_link a  ,      /*     FORUM */
#header #account a            /*    My Account */
{         
  color: #fff !important;
  font-weight: bold !important; 
} 
#create_new_style:hover a  ,  
#header #fourm_link a:hover  ,      /*     FORUM */
#header #account a:hover            /*    My Account */
{         
  color: #FFFF00 !important;
}   
/* Stylish on Android */
#top_android_button    {         
  margin-left: 12px !important;
  margin-right: -16px !important;
  width:      135px   !important;
  max-width:  140px   !important;
  background: transparent  !important;
  border: 1px solid #FFF !important;
  box-shadow:   none !important;  
}   
#top_android_button > div   {         
  padding-left: 3px !important;
  font-weight: normal !important; 
  font-size: 0.9em !important; 
  color: #ddd !important;
} 
#top_android_button:hover   {         
  border-color:  #FFFF00 !important;
} 
#top_android_button:hover > div  {         
  color:  #FFFF00 !important;
}  
  
  
  
.ad ,
.adContainer ,  
.adsbygoogle ,  
#social  ,
#share_div  
{
  display: none !important;
}  
  
  
  


  
/****  left ***********************************/
  
#navigation  a > div  {    
  height: 2px !important;
  -moz-transition:  none  !important; 
}   
#navigation  a > div:hover  {    
  background: #D4F2FF !important;
}   
  
#navigation > div  {    
  color: #000 !important;  
}   
#navigation a , 
#navigation a span  {   
  font-weight: bold !important; 
  color: #3D6AB8  ;
  text-shadow:  none !important;  
  text-decoration:  none !important;  
}   
#navigation a:hover , 
#navigation a:hover span  {    
  color: #CC0000 !important;   /*  D44406 CC0000 */
/*   text-decoration: underline  !important;  */
}   
  
/**** fix left */
#navigation {
     position: fixed !important; 
     z-index: 7000 !important; 
     margin-top: -8px !important;
     margin-left: -36px !important;
     left: -0px !important;
     width: 198px !important;
  height: calc(100vh - 180px) !important;
  overflow-y:    scroll  !important; 
}  
  
  
  
  
  
  
  
  
#view {
     margin-top: -8px !important;
     margin-left: 260px !important;
} 
#footer {
     width: 86% !important;
     margin-left: 220px !important;
}   
#rightFooter {
}     
  
  
  
  
  
  
  
/* ***  в результатах поиска ***********************/   

/* 2-й эл-т -  рекламный блок */   
/* #view_inner > DIV.styles:last-child > DIV.style_card_long:nth-child(3) ,
#view_inner > DIV.styles:nth-child(2) > DIV.style_card_long:nth-child(3)  */
#view_inner > DIV.styles > DIV.style_card_long:nth-child(3)  
{
    background: #e00 !important;
    display: none !important;
}  
  
[class^="style_card"]  ,
.style_card_long  {
    height: auto !important;
    min-height:  auto   !important;
    max-height:  inherit   !important;
}
.style_card_long  {
    padding-left: 16px !important;
}  
.style_card_long .style_data_long {
    padding-right:  16px !important;
    margin-left:  0px !important;
    margin-right: 0px !important;
} 
.style_card_long #rating_and_download {
    width:  82px   !important; 
}     
[class^="style_card"] * ,  
.style_card_long * {
  font-family: Arial  !important; 
  color: #000 !important;
  opacity: 1 !important;
}  
/* подсветка */
.style_card_long:hover {
  outline: 2px solid #7EC4FD !important; 
}     
span[class*="tooltiptext"]   {
  color: #fff !important;
}  
[class^="style_card"] a:hover {
  text-decoration:  none !important;  
}  
/*   назв. стиля в результатах поиска */  
#title_long ,  
h4.ellipsis {
  font-weight: bold ;
}
[class^="style_card"] a ,
#title_long  a , 
.view_details span {    
  color: #3D6AB8  !important;  
} 
[class^="style_card"] a:hover,
#title_long  a:hover ,
.view_details:hover span  {    
  color: #CC0000 !important;  
}   
[class^="style_card"] a:visited ,
#title_long  a:visited {    
  color: #884BAD  !important;  
}   
[class^="style_card"] a:visited:hover ,
#title_long  a:visited:hover  {    
  color: #AF3298  !important;  
}  

  
  
  
  
  
/*   страница другого автора - список стилей  ************/
  
/*   Contact link */
HTML > BODY > DL > DD > A[href*="messages/add/"] {
  font-weight: bold !important;
}  
/* подсветка */  
ARTICLE[class^="style-brief"] {
  padding: 6px !important;
  margin-top: 2px !important;	
  margin-bottom: 20px !important;
}    
ARTICLE[class^="style-brief"]:hover {
  outline: 2px solid #7EC4FD !important; 
}     
.style-brief-text HEADER {
  font-weight: bold ;
}  
/* Updated */  
.style-brief-stats > SPAN:first-child {
  font-weight: bold ;
  color: #47B3B3 !important;  
}  

  
  
  
/*   стр. стиля *****************************/
  
#view > #style_info > div ,
#information_div * ,
span.by-author  {
  color: #000 ;
}  

#style_info #style-id::before  {
    content: 'style-id: ';
    white-space: pre;
}    
#style_info #style-id   {
  display:  block !important;
  font-weight: bold !important;  
  margin-bottom: -14px !important;
}  
  
/* style_author */  
#style_author {
   font-weight: bold !important;  
   margin-bottom: 10px !important;
}
  
/* Deleted  /  archived
This style was deleted by its author ... */ 
#style_info #archived   {
  background-color: rgba(252, 2, 2, .05) !important;
  outline:  3px solid #b00 !important;
  padding-top: 6px !important;
  padding-bottom: 6px !important;
  padding-left: 6px !important;
  padding-right: 4px !important;
  margin-bottom: 14px !important; 
  margin-left:  3px !important;
}
.archived_text  {
  font-weight: bold !important; 
  color: #b00 !important;
}  
  
/* image */
#preview_image_div  {
  outline: 8px solid rgba(119, 119, 119, .6) !important; 
  margin-bottom: 23px !important;
  margin-left:  8px !important;
}  
  
/* кнопки View Stats /  Edit Style  */
#ownedButtons > a  {
  font-weight: bold !important; 
}    
#ownedButtons > a > div {
  padding-left: 6px !important;
  padding-right: 6px !important;
  border: 1px solid #2C58DD !important;
  box-shadow:   none !important;  
}  
  
/*   install_style_button */
[id="install_style_button"][class="no-select"]  {
  font-weight: bold !important; 
  border: 1px solid #2C58DD !important;
  box-shadow:   none !important;  
}    
#ownedButtons > a:hover > div  ,
[id="install_style_button"][class="no-select"]:hover  {
  background-color: #30990C  !important;
  border-color:  #30990C !important;
  color:  #fff !important;
} 
/*   Style Installed */
[id="style_installed_button"] {
  padding-left: 6px !important;
  padding-right: 6px !important;
  border: 1px solid #30990C !important;
  color:  #30990C !important;
  box-shadow:   none !important;  
}    
  
#archived_title  {
  font-weight: bold !important; 
}    
  
/* Show CSS / Hide CSS / advanced_button */
/* .css_button / advanced_button */
#button_middle > div  
{
   border: 1px solid #2C58DD !important;
}
  
/*   код */
/*   TEXTAREA */
TEXTAREA#stylish-code /* на стр. стиля   */    , 
TEXTAREA#css /*  в редакторе */   {
   background-color: rgba(252, 246, 230, .5) ;   /*  rgba(252, 253, 238, .50) */
   color: #000 ;
   font-size: 15px   ;
   font-family:   /*  "Roboto Mono" , */ Lucida Console  ,  Courier New     !important; 
   padding:  6px   !important;
   border-radius: 0px !important; 
   border: 1px solid #C4A16D !important;
   overflow:     auto  !important;  
   white-space: pre  !important;  
   word-wrap:    normal  !important;
/*    min-height: 228px !important;
   height: 328px !important;
   max-height: 90vh !important; */
} 

#advancedsettings_area     {
   background-color: transparent   !important;
   color: #000 ;
   padding:  16px   !important;
   border-radius: 0px !important; 
   border: 1px solid #99B1CC !important;  
}    
  
/* Notes from latest update: */
#notes_textarea     {
   background-color: rgba(222, 223, 248, .30) ;
   color: #000 ;
   padding:  6px   !important;
   border-radius: 0px !important; 
   border: 1px solid #99B1CC !important;  
}    
  
/* кн. "Archive this Style" / "Retrieve Style"  */
[id="archive_style_button"][class="no-select"]  {
  font-weight: bold !important; 
  background-color: rgba(252, 2, 2, .07) ;  
  border: 1px solid #c00 !important;
  box-shadow:   none !important;  
}    
[id="archive_style_button"][class="no-select"]:hover  {
  background-color: #c00  !important;
  border-color:  #c00 !important;
  color:  #fff !important;
}   
  
/*    Install style as userscript  */
#as_userscript a {
  font-weight: bold !important; 
  text-decoration:  none !important;  
}      
#as_userscript:hover a {
  color:  #009900 !important;
  text-decoration: underline  !important; 
}   
  
/* Send Feedback */
#send_feedback_button   {
  color:  #006600 !important;
}    
#send_feedback_button:hover   {
  color:  #009900 !important;
  border-color: #009900 !important;
  background-color: #EBFDE5  !important;
}    
  
  
  
  
  
  
  
/*  МОЯ страница  - список стилей  ************/

HTML > BODY > UL > LI > A  {
    font-weight: bold !important; 
}   

TABLE.author-styles {
  border-spacing: 0px !important;
  border-collapse:  collapse  !important;  /* collapse separate */
/*   width: 106%  !important;
  min-width: 100%  !important;
  max-width: 106%  !important; */
}  
TABLE.author-styles THEAD TR TH , 
TABLE.author-styles TBODY TR TD {
  font-weight: normal ; 
  font-size: 15px ; 
  font-family: Arial  !important; 
  font-style: normal !important; 
  padding: 4px !important; 
}  

TABLE.author-styles THEAD tr th ,  
TABLE.author-styles TBODY tr td {
    border-top:  solid  1px !important;  
    border-color: #ccc !important;
} 
/* подсветка */  
TABLE.author-styles TBODY > tr:hover  {
   background-color: rgba(244, 252, 255, .7)  !important;  /*  rgba(2, 2, 2, 0.04)  */
   outline: solid #7EC4FD 2px !important; 
} 
TABLE.author-styles TBODY tr:hover td {
    border-bottom-color:   transparent !important;     /* transparent #dadada  */
} 
  
/* styles names  */ 
TABLE.author-styles TBODY TR > TD:first-child {
  font-weight: bold !important; 
  font-size: 17px !important;   
  padding-left: 8px !important;
}
/*  Installs (week) / Installs (total)  */ 
TABLE.author-styles TBODY TR > TD.numeric-value {
  text-align: center !important; 
}
/* warning ! в желтом треугольнике 32x32  */
TABLE.author-styles  img.style-warning-icon {
  position: relative   !important;
  top: -2px !important;
}
  
  
/*  МОЯ страница  - список стилей  - Edit/Delete/Stats  */  
TABLE.author-styles > TBODY > TR > TD:last-child ,
/* edit page - кнопки  */  
.edit-setting-buttons INPUT[type="button"] ,
.edit-dropdown-setting INPUT[type="button"] ,
.edit-style-options INPUT[type="button"] ,
.edit-option-buttons INPUT[type="button"] ,
#edit-style-settings INPUT[type="button"] 
{
  font-weight: normal !important;      /*   normal bold */ 
  font-size: 14px !important;   
} 
TABLE.author-styles > TBODY > TR > TD:last-child > a ,
/* edit page - кнопки  */  
.edit-setting-buttons INPUT[type="button"] ,
.edit-dropdown-setting INPUT[type="button"] ,
.edit-style-options INPUT[type="button"] ,
.edit-option-buttons INPUT[type="button"] ,
#edit-style-settings INPUT[type="button"] 
{
  -moz-appearance: none !important;
   border-radius: 0px !important; 
   padding: 0px 4px 0px 4px !important;
   margin-right: 1px !important;
   line-height:  1.5 !important;  
   text-decoration:  none !important;  
   background-image: -moz-linear-gradient(to bottom, 
        rgba(255, 255, 255, 0.5) 20% , rgba(55, 55, 55, 0.22) ) !important ;  
   color: #000 !important;
}    
/*   edit */
TABLE.author-styles > TBODY > TR > TD:last-child > a[href*="edit"]  {
   border: 1px solid #94D287 !important;  /* 7DC889 */
   background-color: #E2FDDE !important;  /* D1FDDF */
}  
TABLE.author-styles > TBODY > TR > TD:last-child > a[href*="edit"]:hover {
   border-color: #5CD225 !important;       /* 56BB60 */
   background-color: #89FD5A !important;  /* A4FDB5 */
}    
/*   stats */
TABLE.author-styles > TBODY > TR > TD:last-child > a[href*="stats"] ,
/* edit page - кнопки  */  
.edit-setting .edit-setting-buttons INPUT[type="button"] ,
.edit-dropdown-setting INPUT[type="button"] ,
.edit-style-options INPUT[type="button"] ,
.edit-option-buttons INPUT[type="button"] ,
#edit-style-settings INPUT[type="button"] 
{
   border: 1px solid #96BDE6  !important; /*  71A9FD */
   background-color: #E8EFFD !important;  /* E8EFFD */
}  
TABLE.author-styles > TBODY > TR > TD:last-child > a[href*="stats"]:hover ,
/* edit page - кнопки  */  
.edit-setting .edit-setting-buttons INPUT[type="button"]:hover ,
.edit-dropdown-setting INPUT[type="button"]:hover ,
.edit-style-options INPUT[type="button"]:hover ,
.edit-option-buttons INPUT[type="button"]:hover ,
#edit-style-settings INPUT[type="button"]:hover 
{
   border-color: #658AC3 !important;       /* 3880C3 */
   background-color:  #9ACAFD !important;  /* 9ACAFD */
}    
/*   delete */
TABLE.author-styles > TBODY > TR > TD:last-child > a[href*="delete"] 
{
   border: 1px solid #FE885D !important;  /* F46E56 */
   background-color: #FDBCB8  !important;  /* FDBCB8 */
}  
TABLE.author-styles > TBODY > TR > TD:last-child > a[href*="delete"]:hover 
{
   border-color: #ED5736 !important;      /* ED5736 */
   background-color:  #FD8878 !important;  /* FD8878 */
}      
  
  
  
  
  
  
  
/* Edit page ***************************** */  

INPUT#provide-screenshot {
     display:    flex  !important; 
}   
  
/*   кн. Save */
INPUT[type="submit"][value="Save"][name="commit"]   {
/*     position:   fixed !important; 
    z-index: 8000 !important; 
    top: 44px !important;
    right: 40px !important;   */   
     width: 140px !important;
     height: 32px !important;
     padding: 0px 0px 0px 0px !important;
     border-radius: 0px !important;   
     border: 1px  #94A7CC solid !important;  
     background-image: -moz-linear-gradient(to bottom, #fff 5% ,  #ABC4FD 95% ) !important;       
     font-weight: bold !important; 
     font-size: 18px !important; 
     color: #0066cc  !important;
} 
INPUT[type="submit"][value="Save"][name="commit"]:hover   {
     border: 1px  #6AAF5C solid !important;  
     background-image: -moz-linear-gradient(to bottom, #fff 5% ,  #89C97B 95% ) !important;       
     color: #259C07 !important;
     cursor: pointer  !important; 
} 
    

  

.errorExplanation {
  background:  url(http://i925.photobucket.com/albums/ad96/Hatrage/w4_zps6177b241.png)!important;
  background-size: 16% !important;
  border: 1px solid #FF9900!important;
  margin: 1em 1em 1em 1em !important;
  -webkit-border-radius: 5px!important;
  border-radius: 5px!important;
  background-color: rgba(255, 163, 0, 0.86)!important;
/*   box-shadow: 0px 0px 8px rgb(255, 173, 0)!important; */
}
/* 1 error prohibited this style from being saved */
.errorExplanation > h2 {
  color: #FFBD5A!important;
  -webkit-border-radius: 5px!important;
  border-radius: 5px!important;
  margin: 0 0.1em 0 0.1em !important;
  padding: 0.1em 0.1em 0.2em 0.7em !important;
  background-color: rgba(0, 0, 0, 0.5)!important;
  box-shadow: 0px 0px 8px rgba(255, 173, 0, 0.4)!important;
  text-shadow: 0px 0px 5px rgba(255, 153, 0, 0.8)!important;
}
/* There were problems with the following fields: */
.errorExplanation > p  {
  background: transparent !important;
  border: 0px solid #FF9900!important;
  font-size: 18px !important; 
  font-weight: bold !important;  
  color: #fff  !important;
  text-shadow:  1px  1px  0 #890000,
                1px -1px  0 #890000 ,
               -1px  1px  0 #890000 ,
               -1px -1px  0 #890000  !important;  
}
.errorExplanation > ul {
  -webkit-border-radius: 5px!important;
  border-radius: 5px!important;
  border: 1px solid #FF4A3A!important;
  background-color: rgba(200, 0, 0, 0.8)!important;
  margin: 1em 1em 1em 1em !important;
  padding: 0.5em 0.5em 0.5em 1.7em !important;
  color: #FFFFFF!important;
}
  
  
  
/* в списке своих силей */  
.style-warning {
/* воскл.знак в жутком красном треугольнике    
  background:  url(http://i925.photobucket.com/albums/ad96/Hatrage/at3_zps7fc4ef55.png) 
               center left 16px no-repeat!important; */
/* воскл.знак в желтом треугольнике 
  родной http://userstyles.org/images/warning.png 
  http://jpegshare.net/images/3c/af/3caf106abc320f7cc5c49cf9cefc2723.png
  http://s6.postimg.org/42ws1ye8d/warning_32x32_userstyles.png  убит    */
  background:  url("http://jpegshare.net/images/3c/af/3caf106abc320f7cc5c49cf9cefc2723.png") 
               center left 16px no-repeat!important;
  background-size: 2.3em !important;  /*   10% */
  -webkit-border-radius: 0px!important;
  border-radius: 0px!important;
