css:
@-moz-document url-prefix("http://bs.to") {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 700;
  src: local('Open Sans Bold'), local('OpenSans-Bold'), url(https://fonts.gstatic.com/s/opensans/v13/k3k702ZOKiLJc3WVjuplzHhCUOGz7vYGh680lGh-uXM.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: italic;
  font-weight: 400;
  src: local('Open Sans Italic'), local('OpenSans-Italic'), url(https://fonts.gstatic.com/s/opensans/v13/xjAJXh38I15wypJXxuGMBobN6UDyHWBl620a-IRfuBk.woff) format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: italic;
  font-weight: 700;
  src: local('Open Sans Bold Italic'), local('OpenSans-BoldItalic'), url(https://fonts.gstatic.com/s/opensans/v13/PRmiXeptR36kaC0GEAetxjqR_3kx9_hJXbbyU8S6IN0.woff) format('woff');
}
*, *:before, *:after{
	font-family: "Open Sans", Arial, sans-serif;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
}
html, body{
	color: #444;
	margin: 0;
	padding: 0;
	background: #310e08 none top left repeat;
}
a{
	color: #cc3610;
	text-decoration: none;
}
a:hover{
	color: #7e0000;
}
h2{
	color: #cc3610;
	margin: 10px 0;
	padding: 3px 15px;
	display: inline-block;
	font-weight: normal;
	border-bottom: 1px solid #cc3610;
}
h3{
	color: #cc3610;
	margin: 10px 0;
	padding: 3px 15px;
	display: block;
	font-weight: normal;
}
select, input[type="text"], input[type="email"], input[type="search"], input[type="password"]{
	height: auto !important;
	margin: 0;
	padding: 0 10px;
	font-size: 14px;
	line-height: 29px;
	border: 1px solid #d0d0d0;
	outline: none;
	box-shadow: 0 1px 1px 0 #dfdfdf;
	-moz-box-shadow: 0 1px 1px 0 #dfdfdf;
	-webkit-box-shadow: 0 1px 1px 0 #dfdfdf;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	vertical-align: top;
}
select{
	padding: 4px 10px;
}
select:hover, input[type="text"]:hover, input[type="email"]:hover, input[type="search"]:hover, input[type="password"]:hover{
	border-color: #c0c0c0;
	box-shadow: 0 1px 1px 0 #cfcfcf;
	-moz-box-shadow: 0 1px 1px 0 #cfcfcf;
	-webkit-box-shadow: 0 1px 1px 0 #cfcfcf;
}
select:focus, input[type="text"]:focus, input[type="email"]:focus, input[type="search"]:focus, input[type="password"]:focus{
	border-color: #cc3610;
	box-shadow: 0 1px 1px 0 rgba(204,54,16, 0.5);
	-moz-box-shadow: 0 1px 1px 0 rgba(204,54,16, 0.5);
	-webkit-box-shadow: 0 1px 1px 0 rgba(204,54,16, 0.5);
}
input[type="submit"]{
	color: #fff;
    width: auto;
    cursor: pointer;
    margin: 0;
    padding: 0 12px;
    border: 1px solid #dbb000;
    outline: none;
	font-size: 14px;
	line-height: 29px;
    background-color: #dbb000;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.15), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
input[type="submit"]:hover{
	border-color: #e2bf32;
    background-color: #e2bf32;
    box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.25), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
    -moz-box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.25), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
    -webkit-box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.25), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
input[type="submit"]:active{
	border-color: #cc3610;
    background-color: #cc3610;
    box-shadow: inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
    -moz-box-shadow: inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
    -webkit-box-shadow: inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
body>#root{
	width: 100%;
	margin: 0 auto;
	padding: 35px 0 0 0;
	display: block;
	position: relative;
	font-size: 16px;
	font-family: "Open Sans", Arial, sans-serif;
	background-color: #310e08;
	background-repeat: no-repeat;
	background-position: center 39px;
	box-shadow: none;
	-moz-box-shadow: none;
	-webkit-box-shadow: none;
}
/* @end GENERAL */

/* @start HEADER */
body>#root>header{
	width: 900px;
	margin: 0 auto;
	padding: 0;
	display: block;
}
body>#root>header h1 a{
	width: 170px;
}
body>#root>header form{
	width: 180px;
	height: 169px;
	margin: 0;
	border: none;
	background-color: #ff8d00;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
}
body>#root>header form label{
	font-size: 0;
}
body>#root>header form input[type="text"],
body>#root>header form input[type="password"]{
	width: 100%;
	margin: 10px 0 0 0;
	padding: 0 7px;
	border: none;
	outline: none;
	font-size: 12px;
	line-height: 27px;
	background-color: #fff;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
	box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.2), inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-moz-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.2), inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-webkit-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.2), inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
body>#root>header form input[type="text"]{
	margin-top: 20px;
}
body>#root>header form label#rememberLogin{
	cursor: pointer;
	color: #fff;
	font-size: 12px;
}
body>#root>header form label#rememberLogin input{
	float: left;
	width: 16px;
	height: 16px;
	margin: 0 5px 0 0;
}
body>#root>header form input[type=submit]{
	width: auto;
	margin: 10px 0 0 0;
	border: 1px solid #dbb000;
	background-color: #dbb000;
	box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.35), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-moz-box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.35), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-webkit-box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.35), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
body>#root>header form input[type=submit]:hover{
	border-color: #e2bf32;
	background-color: #e2bf32;
	box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.5), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-moz-box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.5), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-webkit-box-shadow: inset 0 1px 0 0 rgba(255, 255, 255, 0.5), 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
body>#root>header form input[type=submit]:active{
	border-color: #cc3610;
	background-color: #cc3610;
	box-shadow: inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-moz-box-shadow: inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
	-webkit-box-shadow: inset 0 1px 1px 0 rgba(0, 0, 0, 0.25);
}
body>#root>header form a{
	color: #fff;
	margin: 0 0 0 10px;
	display: inline-block;
	font-size: 15px;
	text-decoration: none;
	vertical-align: bottom;
}
body>#root>header form a:hover{
	color: #cc3610;
}
/* @end HEADER */

/* @start ERROR */
.messageBox{
	margin: 0;
	padding: 10px 15px;
	display: block;
	border: none;
}
.messageBox.error{
	color: #fff;
	font-size: 14px;
	line-height: 14px;
	border-bottom: 1px solid #7e0000;
	background-color: #cc3610;
}
.messageBox.info{
	color: #fff;
	font-size: 14px;
	line-height: 14px;
	border-bottom: 1px solid #ff8d00;
	background-color: #ff8d00
}
section.serie .messageBox{
	margin-bottom: 15px;
}
/* @end ERROR */

/* @start NAVIGATION */
body>#root>nav{
	top: 0;
	width: 100%;
	height: 38px;
	margin: 0 auto;
	padding: 0;
	display: block;
	position: fixed;
	line-height: 40px;
	border-bottom: 3px solid #7e0000;
	background-color: #cc3610;
	box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.5);
	-moz-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.5);
	-webkit-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.5);
}
body>#root>nav>ul{
	width: 900px;
	height: 35px;
	margin: 0 auto;
	padding: 0;
	border: none;
	background-color: #cc3610;
}
body>#root>nav>ul:before{
	float: right;
	color: #fff;
	content: "Burning Series - BS.to";
	font-size: 16px;
	font-weight: bold;
	line-height: 37px;
	text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.2);
}
body>#root>nav>ul>li{
	width: auto;
	height: 35px;
	border: none;
	text-align: left;
	line-height: 35px;
}
body>#root>nav>ul>li:hover{
	border: none;
	background-color: #7e0000;
}
body>#root>nav>ul>li a{
	color: rgba(255, 255, 255, 0.7);
	margin: 0;
	padding: 0 25px;
	display: block;
	font-size: 15px;
	text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.2);
	text-decoration: none;
	border-top: none;
	border-left: none;
	border-right: none;
	border-bottom: 3px solid #7e0000;
}
body>#root>nav>ul>li a:hover{
	color: rgba(255, 255, 255, 1.0);
}
body>#root>nav>ul>li ul{
	width: auto;
	height: auto;
	border: none;
	margin: 0 0 0 1px;
	padding: 0;
	background-color: #cc3610;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
}
body>#root>nav>ul>li ul li{
	height: auto;
	border: none;
	background-color: #7e0000;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
}
body>#root>nav>ul>li ul li:hover{
	background-color: #cc3610;
}
body>#root>nav>ul>li ul li a{
	color: rgba(255, 255, 255, 0.7);
	height: auto;
	margin: 0;
	padding: 8px 15px 9px 10px;
	display: block;
	font-size: 13px;
	text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.2);
	line-height: 13px;
	white-space: nowrap;
	border: none;
}
body>#root>nav>ul>li ul li a:before{
	content: "\203A ";
	margin: 0 10px 0 5px;
	padding: 0;
	display: inline-block;
	font-size: 18px;
}
body>#root>nav>ul>li ul li a:hover:before{
	margin: 0 5px 0 10px;
}
body>#root>nav>ul>li ul li a[href="andere-serien"]:before,
body>#root>nav>ul>li ul li#social-nav a:before{
	padding: 0;
	display: none;
}
body>#root>nav>ul>li ul li#social-nav a{
	padding: 0;
}
body>#root>nav>ul>li ul li a strong{
	display: block;
	text-align: center;
}
body>#root>nav>ul>li ul li a:hover{
	color: rgba(255, 255, 255, 1.0);
}
/* @end NAVIGATION */

/* @start CONTENT */
body>#root>section{
	width: 900px;
	margin: 0 auto;
	padding: 65px 20px 15px 20px;
	display: block;
	overflow: auto;
	position: relative;
	background-color: #fff;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
}
body>#root>section>h2{
	top: 0;
	left: 0;
	right: 0;
	color: #fff;
	width: 100%;
	height: 45px;
	margin: 0;
	padding: 5px 30px;
	text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.35);
	border-bottom: 1px solid #310e08;
	position: absolute;
	background-color: #ff8d00;
	background: #7e0000;
	background: -moz-linear-gradient(top,  #7e0000 0%, #7a1c1c 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#7e0000), color-stop(100%,#7a1c1c));
	background: -webkit-linear-gradient(top,  #7e0000 0%,#7a1c1c 100%);
	background: -o-linear-gradient(top,  #7e0000 0%,#7a1c1c 100%);
	background: -ms-linear-gradient(top,  #7e0000 0%,#7a1c1c 100%);
	background: linear-gradient(to bottom,  #7e0000 0%,#7a1c1c 100%);
	box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
	-moz-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
	-webkit-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
}
body>#root>section.home section#teaser{
	margin: 0;
	padding: 0;
	border: none;
	background: none;
	box-shadow: none;
	-moz-box-shadow: none;
	-webkit-box-shadow: none;
}
body>#root>section.home section#teaser header{
	color: #fff;
	text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.35);
	background-color: #cc3610;
}
body>#root>section.home section#teaser header h3{
	color: #fff;
	padding: 0;
	font-weight: normal;
}
body>#root>section.home section#teaser div{
	margin: 0;
	padding: 10px;
	border: 1px solid #cc3610;
	box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
	-moz-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
	-webkit-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
}
body>#root>section.home div.column{
	width: 420px;
	margin: 15px 0;
}
body>#root>section.home div.column section{
	width: 420px;
	margin: 0;
	padding: 0;
	border: none;
	background: none;
	box-shadow: none;
	-moz-box-shadow: none;
	-webkit-box-shadow: none;
}
body>#root>section.home div.column header{
	color: #fff;
	text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.35);
	background-color: #cc3610;
}
body>#root>section.home div.column header h3{
	color: #fff;
	padding: 0;
	font-weight: normal;
}
body>#root>section.home div.column div{
	margin: 0;
	padding: 10px;
	border: 1px solid #cc3610;
	box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
	-moz-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
	-webkit-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.5);
}
body>#root>section.home div.column section#shoutbox div{
	margin: 0;
	padding: 0;
}
body>#root>section.home div.column section#shoutbox div dl{
	margin: 0;
	padding: 0;
	display: block;
}
body>#root>section.home div.column section#shoutbox div dl dt{
	color: #fff;
	margin: 0;
	padding: 5px 10px;
	background-color: rgba(204, 54, 16, 0.75);
}
body>#root>section.home div.column section#shoutbox div dl dt a{
	color: #fff;
	margin: 0;
	padding: 0;
	display: inline-block;
	font-size: 14px;
	line-height: 14px;
	background: none;
	text-decoration: none;
}
body>#root>section.home div.column section#shoutbox div dl dt a:before{
	content: "\203A ";
	margin: 0 10px 0 5px;
	padding: 0;
	display: inline-block;
	font-size: 18px;
	line-height: 10px;
	vertical-align: top;
}
body>#root>section.home div.column section#shoutbox div dl dt a:hover:before{
	margin: 0 5px 0 10px;
}
body>#root>section.home div.column section#shoutbox div dl dt time{
	float: right;
	margin: 2px 10px 0 0;
	display: block;
	text-shadow: none;
}
body>#root>section.home div.column section#shoutbox div dl dd{
	margin: 0;
	padding: 10px 15px;
	font-size: 14px;
}
body>#root>section.home div.column section#newest_series div{
	margin: 0 0 15px 0;
	padding: 0;
}
body>#root>section.home div.column section#newest_series div ul{
	margin: 0;
	padding: 0;
	display: block;
}
body>#root>section.home div.column section#newest_series div ul li{
	margin: 0;
	padding: 0;
	display: block;
}
body>#root>section.home div.column section#newest_series div ul li:nth-child(2n+0){
	background-color: rgba(204, 54, 16, 0.75);
}
body>#root>section.home div.column section#newest_series div ul li a{
	margin: 0;
	padding: 5px 10px;
	display: block;
	font-size: 14px;
	text-decoration: none;
}
body>#root>section.home div.column section#newest_series div ul li a:before{
	content: "\203A ";
	margin: 0 10px 0 5px;
	padding: 0;
	display: inline-block;
	font-size: 18px;
	line-height: 14px;
	vertical-align: top;
}
body>#root>section.home div.column section#newest_series div ul li a:hover:before{
	margin: 0 5px 0 10px;
}
body>#root>section.home div.column section#newest_series div ul li:nth-child(2n+0) a{
	color: #fff;
}
body>#root>section.home div.column section#news div article header{
	height: auto;
	display: block;
	border-bottom: 1px solid #cc3610;
	background-color: transparent !important;
}
body>#root>section.home div.column section#news div article header h4{
	color: #cc3610;
	margin: 0;
	padding: 5px 10px;
	display: inline-block;
	font-weight: normal;
	text-shadow: none;
}
body>#root>section.home div.column section#news div article header h4 a{
	color: #cc3610;
}
body>#root>section.home div.column section#news div article header h4 a:hover{
	color: #7e0000;
}
body>#root>section.home div.column section#news div article header time{
	float: right;
	color: #555;
	margin: 8px 10px 0 0;
	display: block;
	text-shadow: none;
}
body>#root>section.home div.column section#news div article p{
	margin: 0 0 5px 0;
	padding: 5px 10px;
	font-size: 14px;
}
/* @end CONTENT */

/* @start CONTENT - SERIEN */
body>#root>section.andere-serien form#serForm{
	padding: 0;
}
body>#root>section.andere-serien form#serForm input{
	width: 100% !important;
	height: auto;
	margin: 0;
	padding: 5px 10px;
	border: 1px solid #d0d0d0;
	outline: none;
	font-size: 15px;
	box-shadow: 0 1px 1px 0 #dfdfdf;
	-moz-box-shadow: 0 1px 1px 0 #dfdfdf;
	-webkit-box-shadow: 0 1px 1px 0 #dfdfdf;
	border-radius: 0;
	-moz-border-radius: 0;
	-webkit-border-radius: 0;
}
body>#root>section.andere-serien form#serForm input:hover{
	border-color: #c0c0c0;
	box-shadow: 0 1px 1px 0 #cfcfcf;
	-moz-box-shadow: 0 1px 1px 0 #cfcfcf;
	-webkit-box-shadow: 0 1px 1px 0 #cfcfcf;
}
body>#root>section.andere-serien form#serForm input:focus{
	border-color: #cc3610;
	box-shadow: 0 1px 1px 0 rgba(204,54,16, 0.5);
	-moz-box-shadow: 0 1px 1px 0 rgba(204,54,16, 0.5);
	-webkit-box-shadow: 0 1px 1px 0 rgba(204,54,16, 0.5);
}
body>#root>section.andere-serien form#serForm+p{
	color: #777 !important;
	width: auto !important;
	margin: 10px 0 !important;
	padding: 5px 10px !important;
	border-color: #c0c0c0 !important;
	box-sizing: border-box !important;
	-moz-box-sizing: border-box !important;
	-webkit-box-sizing: border-box !important;
}
body>#root>section.andere-serien form#serForm+p strong{
	color: #7e0000;
}
body>#root>section.andere-serien form#serForm+p a{
	color: #cc3610;
	text-decoration: none;
}
body>#root>section.andere-serien form#serForm+p a:hover{
	color: #7e0000;
}
body>#root>section.andere-serien div#seriesContainer{
	margin: 0;
	padding: 10px 0 0 0;
	display: block;
}
body>#root>section.andere-serien div#seriesContainer .genre{
	margin: 0 0 15px 0;
}
body>#root>section.andere-serien div#seriesContainer .genre span{
	display: inline-block;
}
body>#root>section.andere-serien div#seriesContainer .genre span strong{
	color: #cc3610;
	margin: 0;
	padding: 0 10px 2px 10px;
	display: inline-block;
	font-size: 18px;
	font-weight: normal;
}
body>#root>section.andere-serien div#seriesContainer .genre ul{
	margin: 0;
	padding: 8px;
	display: block;
	border-color: #c0c0c0;
	list-style: none;
}
body>#root>section.andere-serien div#seriesContainer .genre ul li{
	margin: 0;
	padding: 0;
	list-style: none;
	text-indent: 0px;
}
body>#root>section.andere-serien div#seriesContainer .genre ul li a{
	color: #444;
	margin: 0;
	padding: 3px 0;
