css:
@-moz-document domain("hyunsdojo.com") {
* { text-shadow: none !important;}
.status_update, .activity_right, #profile_top, .forum_wrap, .right_wrap, #front_menu, #footer, .forum_row_right, .tablebg, .row1, .row2, #ptl_menu, .post_right, a.notify_box, .right_data, #notifications_box , #messages_drop, .m_msg, #user_box_drop, .drop_item, #ptl_main, #ptl_menu a:not(.ptl_submit), .list_msg_inner, .new_post_wrap, #swf_desc_box, .more_info, .user_content_avatar, .post_avatar, .avatar{ 
  box-shadow: 0px 1px 1px  rgba(0,0,0,0.2), 0px 1px 5px  rgba(0,0,0,0.1)  !important;
	background: white !important;
  border: none !important;
  color: #111 !important;
}
.view_forum_separator { background: #f6f6f6 !important; border: none !important;}
#footer_inner, #footer_inner a, .view_forum_pag a, .view_forum_pag, #viewforum_page_header a, .view_forum_top th {
  color: #333 !important;
  text-shadow: none !important;
}
button, .button, .front_submit, input.btn_small , #m_new_msg,  #chat_tabs .chat_tab .chat_new_box .chat_box_title,  #chat_tabs .chat_tab{ 
  background: linear-gradient(to top, rgb(60,90,150) 0%, rgb(80,105,160) 100%) !important;
  border: 1px solid rgba(30,50,110,0.5); 
  box-shadow: none !important;
  color: white !important;
}
.forum_cat, .view_forum_top, #front_menu .front_menu_a.label, .more_info h3,.ptl_cat,dd input, .submit-buttons input,.box_header,.box_footer,.right_cat, .right_cat .button, .post_info, .box_header, #m_head, #m_foot, #ptl_menu .front_menu_a.label, .ptl_title, .new_post_footer, table#abbc3posttable, #abbc3posttable > tbody > tr > td, #swf_viewport_bottom, #ptl_footer, .box_top {
  background: #F6F6F6 !important;
  box-shadow: none !Important;
  color: #333 !important;
  text-shadow: none !important;
  border-radius: 0px;
  border-color: #f6f6f6
}
.forum_cat a,.forum_cat h2, .right_cat h2, #front_menu .front_menu_a, .u_title {
  color: #111 !important;
}

.u_com_btn_sel:after,.u_com_btn_sel:before, .activity_right:after, .activity_right:before, .post_right:before, .post_right:after, #ptl_menu_fade,#ptl_menu_bottom, .status_update:after, .status_update:before { 
  display: none !important;
} 
.divider { border-color: #ccc !important;}
body, #swf_viewport { background: rgb(230,230,230) !important; }
.clicked, .m_clicked, .clicked div, .m_clicked div, .sm_clicked, .sm_clicked div {
  background: rgb(240,240,240) !important;
  color: #333 !important;
}

#header { 
  background: linear-gradient(to top, rgb(60,90,150) 0%, rgb(80,105,160) 100%) !important;
  border-bottom: 1px solid rgb(30,50,110);
}
#header div{ color: white; }
.logo { box-shadow: none !important; border: none !important;}
.logo:hover { background: none !important; -webkit-filter: drop-shadow(0px 0px 3px rgb(30,50,110));}

.box_header {
  text-align: left;
  font-size: 14px;
  background: white;
}

div #user_background:after { background: linear-gradient(to top, rgb(60,90,150) 0%, rgb(80,105,160) 100%) !important;}

#online { display: none !important;}
#profile_top_footer { background: none !important; height: 45px;}
#profile_avatar { 
  height: 0px !important; width: 0px; 
  border-radius: 0px; 
  box-shadow: none;
}	
#profile_top_footer div > :before, #profile_top_footer div > :after  { display: none;}
#profile_avatar div { display: none;}
#profile_avatar img { 
  border-radius: 0px !important; 
  height: 170; 
  width: 170; 
  border: 4px solid white;
  box-shadow: 0px 1px 1px rgba(0,0,0,0.2) ;
  margin: -55px 0px 0px -53px;
  background-color: white;
}
#top_foot_bottom_right { bottom: 65px;}
#top_foot_bottom_right > .button { background: white !important; color: rgb(50,50,50) !important; text-shadow: none !important; border: none; box-shadow: 0px 1px 1px rgba(0,0,0,0.2)}

#profile_name { 
  color: white ; font-weight: bold; 
  margin-top: -85px; 
  -webkit-filter: drop-shadow(0px 1px 1px rgba(0,0,0,0.5)); 
  filter: drop-shadow(0px 1px 1px rgba(0,0,0,0.5));
  margin-left: -30px;}
#profile_name > span { color: white !important; font-weight: lighter; line-height: 40px;}

#user_background { box-shadow: inset 0px -40px 80px rgba(0,0,0,0.4) !important;}

/*#user_background:before { 
  background: linear-gradient(to top, rgba(0,0,0,0.35) 0%, rgba(0,0,0,0) 50%)!important; 
	content: "";
  line-height: 0px;
	padding-left: 960px !important;
  padding-bottom: 269px !important;}*/
.u_comments_box, .status_update, #swf_comments_box { background: rgb(245, 245, 245) !important;} 
.u_com_btn, .u_comments_top, .post_button, .new_post_btn, #swf_comments_top {
  border: none !important; background: white !important; color: #666 !important; 
  text-shadow: none !important; box-shadow: none !important;
}
.mentions-input-box, textarea:not(.tnt), input {
  border-radius: 1px;
  border-color: #ccc !important;
  background: white !important;
  color: #888 !important;
  text-shadow: none !important;
}
.mentions-input-box textarea { color: #222 !important;}
.paginate, .paginate_btn, .paginate_btn.sel, .paginate_btn:active { background: none !important; border: none !important; box-shadow: none !important; color: #555;}
.paginate_btn.sel, .paginate_btn:active { color: #111}
.user_content_avatar, .post_avatar, .avatar, .user_content_avatar img, .post_avatar img, .avatar img{ 
  border: none !important; border-radius: 0px !important;
}


.forum_wrap, .front_menu_a:not(.front_submit) { border: none !important; background: white !important; }

.forum_row { border-color: #f6f6f6 !important; background: white !important; }

div.post_info > svg > ellipse { fill: #111 !important;}

#rate, #rate a, #rating { box-shadow: none !important; border: none !important;}


.photo_view_image {
    max-width: 100% !important;
  	width: auto !important;
    max-height: 100vh !important;
}
  
}
