css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iunctis.fr") {

html {
    background-color: #1d252e;
}    
    
body {
    background-color: #1d252e;
}
  
.d-header {
    background-color: #2e3740;
    box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.25);
}
    
.admin-content .admin-contents {
    background-color: #2d3741;
}
    
.admin-detail {
    background-color: #2d3741;
    border-left: 1px solid #353f49;
}
    
.settings .setting.overridden.string input[type="text"] {
    background-color: #8d5d23;
}
    
.settings .setting .input-setting-string {
    background-color: #353f49;
} 
    
.select2-container-multi .select2-choices {
    background-color: #353f49;
}    
    
.dashboard-stats table th {
    background: #505e6d none repeat scroll 0% 0%;
}    
  
.alert.alert-info {
    background-color: #526475;
    color: #c4c4c4;
}
  
.topic-list {
    background-color: #242f39;
    color: #D9D9D9;
    border-top: 1px solid #E9753F;
}
  
.topic-list > tbody > tr:nth-child(2n+1) {
    background-color: #2d3741;
}  
  
.topic-list > tbody > tr:first-of-type {
    border-top: 1px solid #242f39;
}
  
.topic-list > tbody > tr:nth-child(2n) {
    background-color: #353f49;
}  

button#create-topic.btn {
    background: #252d34 none repeat scroll 0% 0%;
    color: #eaffff;
    border-bottom: 2px solid #ed3b1c;
}
  
button.btn.standard.dropdown-toggle {
    background: #252d34 none repeat scroll 0% 0%;
    color: #eaffff;
}
    
.quote-button {
    background-color: #353f49;
    color: #eaffff;
    border-bottom: 2px solid #ed3b1c;
}    
    
.quote-button:hover {
    color: #eaffff;
    background-color: #27303a;
    border-bottom: 2px solid #ed3b1c;
}  
    
nav.post-controls .show-replies {
    color: #eaffff;
    background-color: #353f49;
    border-bottom: 2px solid #ed3b1c;
}  

nav.post-controls .show-replies:hover {
    color: #eaffff;
    background-color: #27303a;
    border-bottom: 2px solid #ed3b1c;
}
    
.nav-pills > li > a:hover {
    color: #eaffff;
    background-color: #27303a;
}
    
    
#user-card .btn {
    background: #353f49 none repeat scroll 0% 0%;
    color: #eaffff;
    border-bottom: 2px solid #ed3b1c;
}
    
#user-card .btn:hover {
    background: #27303a none repeat scroll 0% 0%;
    color: #eaffff;
    border-bottom: 2px solid #ed3b1c;
}    
    
#topic-footer-buttons .btn:hover {
    color: #eaffff;
    background-color: #27303a;
    border-bottom: 2px solid #ed3b1c;
}  
  
#topic-footer-buttons .btn {
    color: #eaffff;
    background-color: #353f49;
    border-bottom: 2px solid #ed3b1c;
}    
  
.topic-area {
    background-color: #2d3741;
}  
  
.topic-body {
    background-color: #2d3741;
    border-top: 1px solid #353f49;
}  
  
#topic-closing-info {
    border-top: 1px solid #4b5663;
}  
  
.topic-body {
    border-top: 1px solid #4b5663 !important;
}  
  
.small-action {
    max-width: 817px;
    border-top: 1px solid #4b5663;
}
  
.topic-avatar {
    border-top: 1px solid #4b5663;
}
  
.topic-list.categories>tbody>tr:nth-child(odd), .topic-list.categories>tbody>tr:nth-child(even) {
    background-color: #2d3741;
}  

.topic-map {
    margin: 20px 0px 0px;
    background: #495763 none repeat scroll 0% 0%;
    width: 760px;
}

.topic-map .information {
  border-top: none;
}

.topic-map .buttons .btn {
  border-bottom: 2px solid #ed3b1c;
  padding: 0 23px;
  color: #eaffff;
  background: #44505c;
}

.topic-map .buttons .btn:hover {
  background: #3f4a56;
  color: #eaffff;
  border: none !important;
  border-bottom: 2px solid #ed3b1c !important;
}

.topic-map section {
  border-top: 1px solid #637481;
}
    
.badge-notification.clicks {
    background-color: #08c;
    color: #eaffff;
    font-weight: bold;
    box-shadow: 0 1px 1px rgba(0,0,0,0.2);
}    
  
#topic-progress {
  background-color: #1d252e;
  color: #eaffff;
  border: 1px solid #242f39;
  border-bottom: none;
  box-shadow: 0 0px 2px rgba(0,0,0,0.3);
}

#topic-progress .bg {
  border-right: 1px solid #455361;
  background-color: #414e5b;
}

#topic-progress-expanded .btn {
  border: none;
  color: #eaffff;
  font-weight: normal;
  background: #252d34;
  border-bottom: 2px solid #ed3b1c !important;
}

#topic-progress i.fa {
  color: orange;
}

#topic-progress-expanded {
  border: 1px solid #2e3740;
  background: #1d252e;
  width: 135px;
  box-shadow: 0 0px 2px rgba(0,0,0,0.2);
}

.gap {
  background-color: #1f2830;
  padding: 5px 0;
  color: #eaffff;
  cursor: pointer;
  text-align: center;
  width: calc(45px + 750px + 2 * 11px);
}
  
span.highlighted {
    background-color: #FFEA00;
    color: #000;
    font-weight: bold;
}
  
.topic-body.highlighted, #topic-list .topic-list-item.highlighted {
    background-color: #414e5b;
}
  
.moderator .topic-body {
    background-color: #67282f;
}  
  
.wiki .topic-body {
    background-color: #275934;
}  
  
.gap.jagged-border {
    background-color: #67282f;
    background-image: none;
    padding: 10px 3px;
    margin-left: -5px;
}  
  
.deleted .topic-body, .deleted .small-action {
    background-color: #4d1a1f;
}  

a.mention {
    padding: 3px 5px;
    color: #eaffff;
    background: #647689 none repeat scroll 0% 0%;
    border-radius: 8px;
}

a.mention-group {
    padding: 3px 5px;
    color: #D9D9D9;
    background: crimson none repeat scroll 0% 0%;
    border-radius: 8px;
}
  
.surligne {
    background-color: #FFEA00;
    color: #000;
    padding: 0px 2px 0px 2px;
}  
  
blockquote {
  background-color: #414d58;
  border-left: 5px solid #1b2836;
  overflow: hidden;
  clear: both;
}  

aside.quote .title {
  border-left: 5px solid #1d252e;
  background-color: #414d58;
  color: #e4e4e4;
}  
  
.quote aside .quote, .quote aside .title, .quote aside blockquote, .quote aside .onebox, .quote aside .onebox-result {
  background: #414d58;
  border-left: 5px solid #1b2836;
}
  
aside.onebox {
  padding: 12px 25px 12px 12px;
  border-left: 5px solid #1d252e;
  background: #414d58;
}  
  
pre code {
  white-space: pre-wrap;
  display: block;
  padding: 5px 10px;
  color: #eaffff;
  background: #414d58;
  max-height: 300px;
}
  
.embedded-posts .fa-arrow-up, .embedded-posts .fa-arrow-down {
    margin-left: 5px;
    color: #eaffff;
}  
  
.embedded-posts {
  background: #414d58 !important;
}

.embedded-posts .topic-body {
  background: #414d58 !important;
}

.embedded-posts .quote .title, .embedded-posts blockquote, .embedded-posts .onebox, .embedded-posts .onebox-result {
  background: #414d58;
  border-left: 5px solid #1b2836;
}

.embedded-posts .quote .title, .embedded-posts blockquote, .embedded-posts .onebox, .embedded-posts .onebox-result {
  background: #414d58;
  border-left: 5px solid #1b2836;
}  
  
.badge-notification.new-posts, .badge-notification.unread-posts {
    background-color: #08c;
    color: #eaffff;
    font-weight: bold;
    box-shadow: 0 1px 1px rgba(0,0,0,0.2);
}
    
.d-header .icons .unread-notifications {
    color: #eaffff;
    font-weight: bold;
    background-color: #08c;
    box-shadow: 0 1px 1px rgba(0,0,0,0.2);
}
    
.unread-private-messages {
    color: #eaffff !important;
    font-weight: bold;
    box-shadow: 0 1px 1px rgba(0,0,0,0.2);
    background: #F39620 none repeat scroll 0% 0%;
} 
  
.list-controls .category-dropdown-menu {
    max-height: 600px;
    border: 1px solid #414c58;
    background-color: #242f39;
    box-shadow: 0 2px 2px rgba(0,0,0,0.2);
}

.menu-panel.drop-down {
    border: 1px solid #414c58;
    background-color: #242f39;
    box-shadow: 0 2px 2px rgba(0,0,0,0.2);
}
  
.drop-down-visible .d-header .icons .active .icon {
    background-color: #242f39;
    border-top: 1px solid #414c58;
    border-left: 1px solid #414c58;
    border-right: 1px solid #414c58;
}  

.user-menu .notifications .read {
    background-color: #242f39;
}  
  
.user-menu .notifications .read:hover {
    background-color: #353f49;
}  

.user-menu .notifications li {
    background-color: #165288;
}  

.user-menu .notifications li:hover {
    background-color: #1f5f99;
}
    
nav.post-controls .post-admin-menu {
    background-color: #242f39;
    border: 1px solid #414c58;
}
    
.popup-menu {
    background-color: #242f39;
    border: 1px solid #414c58;
}
    
.dropdown-menu {
    background-color: #242f39;
    border: 1px solid #414c58;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.4);
}   
    
a.reply-new i {
    background: #2d3741 none repeat scroll 0% 0%;
}
    
a.reply-new i:hover {
    background: #2d3741 none repeat scroll 0% 0%;
}
    
#reply-control {
    background-color: #242f39;
}
    
body .grippie {
    width: 100%;
    border: 1px solid #181f27;
    background-color: #181f27;
}
    
#reply-control .wmd-controls .wmd-input, #reply-control .wmd-controls .wmd-preview-scroller, #reply-control .wmd-controls .wmd-preview {
    background-color: #2d3741;
}
    
#reply-control .wmd-button-bar {
    background-color: #353f49;
    color: #eaffff;
}
    
.nav-stacked {
    background: #242f39 none repeat scroll 0% 0%;
}    

.nav-stacked .active > a {
    color: #eaffff;
    background-color: #353f49;
    border-bottom: 2px solid #ed3b1c;
}
    
.nav-stacked .active > a {
    color: #11111D;
    background-color: #E45735;
}    
    
.user-main .user-stream .item {
    background-color: #2d3741;
    border-bottom: 1px solid #4b5663;
}
    
.user-main .user-stream .item.moderator-action {
    background-color: #67282f;
}
    
.user-main .user-stream .item.wiki-action {
    background-color: #275934;
}
    
.autocomplete {
    background-color: #2d3741;
    border: 1px solid #4b5663;
}
    
.autocomplete ul li a:hover {
    background-color: #353f49;
}
    
div.ac-wrap {
    background-color: #353f49;
    border: 1px solid #575757;
    padding: 5px 4px 1px;
}
    
body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
    background-color: #353f49;
    border: 1px solid #575757;
    border-radius: 3px;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.3) inset;
}
    
.user-main .user-content {
    padding: 10px 8px;
    background-color: #242f39;
    border: 1px solid #575757;
    margin-bottom: 10px;
    box-sizing: border-box;
}    
    
.user-badge, #user-card.show-badges .more-user-badges {
    color: #eaffff;
    background-color: #242f39;
}
    
.discourse-no-touch .autocomplete ul li a[href]:hover {
    background-color: #414e5b;
    text-decoration: none;
}
    
.menu-panel ul.menu-links li a:hover, .menu-panel ul li.heading a:hover {
    background-color: #353f49;
}

.dropdown-menu li > a:hover {
    color: #D9D9D9;
    background-color: #353f49;
}

#reply-control .d-editor-button-bar {
   background-color: #2d3741 !important;
}    
   
#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview-header {
   background-color: #2d3741 !important;
}    
    
#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .d-editor-preview {
   background-color: #2d3741 !important;
}

.modal-inner-container {
    background-color: #2d3741;
    border: 1px solid #575757;
}

.modal-footer {
    background-color: #2d3741;
    border-top: 1px solid #575757;
}
    
.modal-body {
    background-color: #2d3741;
}

.period-chooser #period-popup {
    border: 1px solid #575757;
    background: #2d3741 none repeat scroll 0% 0%;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.4);
}
    
}
