css:
@-moz-document regexp("https?:\\/\\/(?:exelab|cracklab)\\.ru\\/f\\/.*") {
body, #wrapper, #site-navigation .gbody, table {
	background-image: url("http://i068.radikal.ru/1510/0a/7d4595720d94.jpg") !important;
	background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
	color: green !important;
	font-family: Verdana, Geneva, Arial, Helvetica, sans-serif !important;
}
i {
	color: #096 !important;
}
b>font {
	color: floralwhite !important;
}
div#dvd, #content > center:last-child > a > img, #cadr1, #kurs_container, .gbody > tbody:nth-child(1) > tr:nth-child(9), .hl_header, tbody tr:first-child td a img:last-child, #footer_btns, #site-navigation ul::after, #content > center:last-child, #content > div[align="center"] > div/*сайт переехал*/ {
	display: none !important;
}
div#logo {
	position: static !important;
}
#logo_img {
	margin-left: auto !important;
	margin-right: auto !important;
	margin-top: 10px !important;
	margin-bottom: 10px !important;
	display: block !important;
}
tbody tr:first-child td div:last-child {
	height: 10px !important;
}
.caption1 div, table.forums {
	color: silver !important;
}
#header{
	height: 90px !important;
}
div#site-navigation {
	width: 95% !important;
	height: 40px !important;
	margin: auto !important;
	background: #151515 !important;
	border: 1px #666 solid !important;
}
#site-navigation, .sub-menu, #footer, #site-navigation ul li a, #site-navigation ul li > .sub-menu {
	background: #151515 !important;
	border: none !important;
	font-size: 14px !important;
}
#site-navigation ul li a:hover {
	background: #333 !important;
}
#site-navigation ul li {
	border: none !important;
}
.caption5 div {
	color: silver !important;
}
.textForm, .selectTxt {
	padding: 5px !important;
	background: #333 !important;
	margin: 0 auto !important;
	border: 1px #666 solid !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
	color: silver !important;
}
.inputButton {
	background: black !important;
	color: green !important;
}
small {
	color: green !important;
}
table.forums, td.caption6, td.caption5, td.caption4, td.caption2, td.caption1, input.textForm {
	border: 1px #AAA solid !important;
}
.tbStBar {
	padding: 5px !important;
	background: #222 !important;
	margin: 0 auto !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
}
.tg, .caption2, .caption6 {
	padding: 5px !important;
	background: #111 !important;
	margin: 0 auto !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
}
.caption4, .caption7, .caption5, input.textForm {
	padding: 5px !important;
	margin: 0 auto !important;
	background: #111 !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
}
.tbCel1 {
	padding: 5px !important;
	margin: 0 auto !important;
	background: #222 !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
}
.tbCel2 {
	padding: 5px !important;
	margin: 0 auto !important;
	background: #333 !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
}
.caption3 {
	background: #111 !important;
	padding: 5px !important;
	margin: 0 auto !important;
	opacity: 0.7 !important;
	filter: alpha(Opacity=70) !important;
}
.header {
	color: green !important;
}
.txtSm {
	color: green !important;
}
#to_top {
	background-color: #333 !important;
	width: 26px !important;
	height: 26px !important;
	bottom: 10px !important;
	right: 10px !important;
}
#to_top::after {
	right: 1px !important;
	top: 1px !important;
	line-height: 25px !important;
	color: silver !important;
}
tbody tr:nth-child(4) td {
	background: transparent none repeat scroll 0% 0% !important;
	height: 0 !important;
}
a:link, a:visited {
	color: gold !important;
	text-decoration: none !important;
}
a:hover {
	text-decoration: underline !important;
}
::selection {
	background: green !important;
	color: gray !important;
}
::-moz-selection {
	background: green !important;
	color: gray !important;
}
}
