@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lazyfoo.net") {
  body {
    font-family: dejavu sans;
    font-size: 125%;
    color: #272822;
    background-color: #75715E;
  }
  
  h1 {
    width: 100%;
    color: #272822;
    text-align: center;
  }
  
  h2 {
    width: 100%;
    color: #F4BF75;
    text-align: center;
  }
  
  h3 {
    width: 100%;
    color: #272822;
    text-align: center;
  }
  
  h6 {
    width: 100%;
    color: #F9F8F5;
    text-align: center;
  }
  
  b {
    color: #F92672;
  }
  
  a.nav {
    padding: 10px;
    color: #F4BF75;
  }
  
  a.leftNav {
    float: left;
    color: #A6E22E;
  }
  
  a.tutLink {
    margin: 0px auto;
    color: #66D9EF;
  }
  
  div.tutPreface {
    width: 90%;
    margin: 30px auto 50px auto;
    padding: 10px 5px 10px 5px;
    border: medium solid #F4BF75;
    background-color: #f9f8f5;
    text-align: left;
  }
  
  div.tutCodeHeader {
    width: 90%;
    margin: 0px auto 0px auto;
    background-color: #F9F8F5;
    border: medium solid #F92672;
    color: #272822;
    text-align: left;
  }
  
  div.tutCode {
    width: 90%;
    margin: 10px auto 10px auto;
    font-family: dejavu sans mono;
    background-color: #272822;
    border: medium solid #A6E22E;
    color: #f9f8f5;
    white-space: pre;
  }
  
  div.tutText {
    width: 90%;
    background-color: #f9f8f5;
    border: medium solid #272822;
    margin: 0px auto 40px auto;
  }
  
  div.tutImg {
    margin: 0px auto 0px auto;
    text-align: center;
  }
  div.tutFooter {
    width: 90%;
    margin: 30px auto 50px auto;
    padding: 10px 5px 10px 5px;
    border: thin solid #111144;
    background-color: #f9f8f5;
    border: medium solid #272822;
    text-align: left;
  }
  
  td.ToCTitle {
    background-color: #272822;
    border: thin solid #66D9EF;
    color: #f9f8f5;
    height: 30px;
    text-align: center;
  }
  
  td.tutLink {
    background-color: #f9f8f5;
    border: thin solid #FFF;
    color: #272822;
    text-align: center;
  }

}
