css:
@-moz-document domain('lainchan.org'), url-prefix('https://lainchan.org')  {
header {
    display: none;
}
div.reply,
.box,
select,
input,
.opContainer {
    background-color: rgba(31, 33, 50, 0.7) !important;
    box-shadow: 5px 5px 0px rgba(0, 0, 0, 0.3);
    border: 1px !important;
    border-color: #926d7b !important;
    border-style: solid !important;
    margin: 1em;
}
div.reply:hover,
.opContainer:hover {
    box-shadow: 5px 5px 1px rgba(0, 0, 0, 0.1);
    background-color: rgba(31, 33, 50, 1) !important;
}
input[type="text"],
textarea,
.ban,
h2,
select {
    background-color: rgba(31, 33, 50, 0.7) !important;
    color: #fff!important;
    border: 1px !important;
    border-color: #926d7b !important;
    border-style: solid !important;
    margin: 3px;
}
form table tr th {
    background-color: rgba(31, 33, 50, 0.7) !important;
    color: #fff!important;
    border: 1px !important;
    border-color: #926d7b !important;
    border-style: solid !important;
}
.ban {
    padding: 5px;
    max-width: 800px;
}
body {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGCAYAAADgzO9IAAAAJUlEQVQIW2NkQID/DAwMjFDufzgDWRDEBkmgqIQpgOkAmUChUQBz9wwF8MJbKQAAAABJRU5ErkJggg==)repeat fixed, url(http://i.imgur.com/CoNVHv0.jpg) no-repeat fixed;
    -webkit-background-size: 5px, cover;
    -moz-background-size: 5px, cover;
    -o-background-size: 5px, cover;
    background-size: 5px, cover;
    overflow-y: scroll !important;
}
/* give image borders */

img {
    box-shadow: 5px 5px 0px rgba(0, 0, 0, 0.3);
}
.bar {
    background-color: rgba(31, 33, 50, 0.7) !important;
}
.bar:hover {
    background-color: rgba(31, 33, 50, 1) !important;
}
.top {
    border-bottom: 1px solid rgba(31, 33, 50, 0.7);
}
body,
.post.reply,
.opContainer,
#board-list,
#custom-board-list,
#menu.dialog,
.entry,
.yotsuba-b:root .suboption-list > div:last-of-type,
option,
#qr textarea.field,
.field,
#index-search.field,
#updater.dialog,
#thread-stats.dialog,
#thread-stats,
.boardSubtitle,
#thread-watcher,
div.post div.postInfo span.nameBlock span.postertrip,
div.post div.postInfo span.nameBlock span.name,
div.post div.postInfo span.subject,
#qr:not(.captcha-open) .captcha-counter > a,
#file-n-submit > input,
#watcher-link,
.settings-link,
.expand-all-shortcut,
.qr-shortcut {
    font-family: Open Sans !important;
    font-weight: 600 !important;
    font-size: 13px !important;
    color: #eee;
}
hr {
    margin-top: 0px !important;
    padding-top: 0px !important;
    border: 0 !important;
}
}
