css:
@-moz-document domain("www.thebackalleys.com") {
/*removes shadows*/
*{
    -webkit-box-shadow: none !important;
    box-shadow:none!important;
    border: none!important;
    text-shadow:none!important;
    border-radius: 0px!important;
}
/*removes bg texture*/
body{
    background:#111!important;
}
/*removes gradients*/
.post_right, #abbc3posttable td, .activity_right, #u_show_list_top, #ptl_list_top, .status_update{
    background:#ccc!important;   
}
#ptl_menu a:not(.ptl_submit), .u_comments_top, .front_menu_a:not(.front_submit),.paginate_btn.sel, .paginate_btn:active:not(.disabled){
	background:rgb(219,219,219);
}
#ptl_menu .front_menu_a.label, .ptl_title, #footer, .button, .forum_cat, input[type=submit], button, .dropdown, #front_menu .front_menu_a.label, .right_cat, .usr_cat, .view_forum_top, .more_info h3 {
    background:#3c3c3c;
}
#profile_top_footer, .new_post_body{
    background:#fff!important;
}
.top_foot_btn{
    height: 17px;   
}
#ptl_menu_bottom, #ptl_menu_fade, .top_foot_btn:before, .right_data, .new_post_btn, #view_right, #swf_comments_top, .paginate_btn, #top-search {
    background:#f0f0f0;
}
.top_foot_btn.active{
	height: 15px;
    background:#f0f0f0;
}
.ptl_submit, .front_submit, .logo:hover{
    background: rgba(214,224,186,1);
}
input.blue, .button.blue, button.blue, .u_com_blue{
    background:#3797ff;
}
.u_com_blue:hover, .dropdownlist li.hover{
    background: rgb(125,188,255);
}
.forum_row_right, .new_post_btn:hover{
    background: #DADCDE;
}
#swf_viewport, #author_det, .mentions-input-box{
    background: rgb(41,41,41);
}
#author_box{
    background: rgba(0,0,0,0);
    color:#333
}
#swf_viewport_bottom, #chat_tabs .chat_tab{
    background: rgb(26,26,26) 
}
.drop_item{
	background:#e0e0e0;
}
.u_comments_box, #swf_desc_box{
    background:#e9e9e9;
}
.previewPosted{
    background:none;
}
.row2{
	background: #e6e9ec;
}
#header {
	background-image: none;
    background: #222;
}

/*following written by m_x*/
.photo_view_image {
     max-width: 100% !important;
  	 width: auto !important;
    max-height: 100vh !important;
}
}
