  /* FOR OP AVATAR SIZE */
  #topicThread .authorAvatar {
    display: block;
    margin-left: 0;
    margin-right: 12px;
    overflow: hidden;
    height: 90px;
    width: 90px;
}
  /*FOR REPLY AVATAR SIZE*/
  .postReply .authorAvatar {
    float: left;
    height: 90px;
    margin-right: 10px;
    width: 90px;  
}
  

  
.topic .topicAuthor a {
    font-size: 13px;
    color: #EAECEF;
    padding-left: 26px;
    background-size: 17px;
    background-repeat: no-repeat;
    background-position-y: 1px;
}
  


  
/* enables titles for normal users*/  
#topicThread .employee .authorDisplayName::after,
#topicThread .ninja .authorDisplayName::after,
#topicThread .mentor .authorDisplayName::after,
#topicThread .authorDisplayName::after {
	display: inline-block;
	font-size: 12px;
	font-weight: 400;
	line-height: 15px;
	margin-left: 5px;
	padding: 1px 4px 3px 4px;
	position: relative;
	text-transform: uppercase;
	top: 1px;
	vertical-align: middle;
	margin-bottom: 5px;
	left: 4px;
	margin-right: 4px;
}
  

