css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.quizup.com") {
  
  
  body, .Layout
   {
    background-color: rgb(0,0,0)!important;
    background-image: none!important;
   }
  
  .PhotoAttachment__content
  {
   transition-property:opacity;
   transition-duration:0.3s;
   opacity:0.9;
  }
     
 .PhotoAttachment__content:hover
  {
   transition-duration:0.3s;
   opacity:1
  } 
      
                  /*  SIDEBAR  */ 
  
  .MyMiniProfile__name, .MyMiniProfile__stats__count, .MyMiniProfile__stats__countBox, .MyMiniProfile__title, .MyMiniProfile__location
  {
      color:rgb(245,245,245)!important;  
  } 
   
  .HomePage__sidebar, .CreatedTopics__container, .CreatedTopics__title, .MyTopics.u-clearfix, .GameHistory.u-clearfix, .TopicSidebar__about
  
    {
  background-color: rgb(40,40,40)!important;
  color:rgb(185,185,185)!important;  
  border-color:rgb(60,60,60)!important; 
    }
  
  .TopicSidebar__about__text__more
  {
   background-image: none!important;
  }
   
 .GameHistory__item:hover
    {
     background-color: rgb(60,60,60);
    }
  
       
 .TopicPopup__content, .ProfilePopup__content
    {
    background-color: rgb(0,0,0)!important;
    color: rgb(165,165,165);
    box-shadow: 0px 0px 4px 2px rgba(150, 150, 150, 0.175);
      
    }
  
  .TopicPopup__actions
    {
    background-color: transparent!important;
    }
  
  .CreatedTopics__title
   {font-size:18px;}
  
  .ListPopupGame, .ListPopupGame__link.u-clearfix, .ListPopup__container.ListPopup__container--up
  {
  background-color: rgb(0,0,0)!important;
  color:rgb(185,185,185)!important;  
  border-color:rgb(60,60,60)!important; 
 
  }
  
   .ListPopupGame__link.u-clearfix:hover
  {
  background-color: rgb(60,60,60)!important;
  }
  
  
  
    
             /*  POSTCOMPOSER  */
  
  
 .PostComposer__profile, section.PostComposer
    {background-color: rgb(40,40,40)!important;}
  
 .PostComposer__field
   {
  background-color:rgb(40,40,40);
  color:rgb(225,225,225)!important;  
   }
  
 .PostComposer__charactersRemaining 
   {color: rgb(200,200,200)!important;}
  
 .PostComposer__post
   { background-color: rgb(120,120,120)!important;
     color:rgb(0,0,0)!important
   }
    
  .PostComposer__footer.u-clearfix
   {
   border-color:rgb(60,60,60)!important;
   }
  
  .CommentForm__meta, .Comment__like_count
  {
   color: rgb(200,200,200)!important;
  }
  
   
              /*  NEWSFEED  */
  
  .TopicActivity__restriction
   {
    background-color: rgb(40,40,40)!important;
    color:rgb(175,175,175)!important;
    border-color:rgb(60,60,60)!important;
   }
 
   
  .Story.u-clearfix, .QuizUpAttachment
    {
    background-color: rgb(40,40,40)!important;
    color:rgb(175,175,175)!important;
    border-color:rgb(60,60,60)!important;
    }
  
  .StoryMeta__item
  {
    color:rgb(225,225,225)!important;
  }
  
  .Story__button
   {
    background-color: rgb(105,105,105);
    color:rgb(245,245,245)!important;
   }
  

  .PhotoAttachment.PhotoAttachment--portrait, .PhotoAttachment, .Profile__photo
   {
    background-color: black!important;
    border-color:rgb(60,60,60)!important;
   }
  
  
  .LinkAttachment
   {color:rgb(200,200,200)!important;
   border-color:rgb(60,60,60)!important;
   }
  
  
  .StoryLikes, .StoryLikes__text
  {
    background-color: rgb(40,40,40)!important;
    color:rgb(200,200,200)!important;
  }
  
  
  .CommentList
  {
    background-color: rgb(40,40,40)!important;
  
  }
  
  
  .Comment__content
  {
    background-color: rgb(70,70,70)!important;
    Color:rgb(200,200,200)!important;
  }
  
  
  .CommentForm__area
  {
      background-color: rgb(40,40,40)!important;
      Color:rgb(200,200,200)!important;
      border-color:rgb(60,60,60)!important;
  }
  
    .ProfileTextLink:link
  {
    Color:rgb(200,200,200);
  }
 
   .Story__content__link
  {
    color:rgb(200,200,200)!important;
  }
  
  
   .Story__content__link:hover
  {
    color:rgb(245,245,245)!important;
    border-bottom: 2px solid rgb(245,245,245)!important;
  }
  
  
  .ProfileTextLink.ProfileTextLink--minimal
   {
    color:rgb(200,200,200)!important;
   }
  
  .ProfileTextLink.ProfileTextLink--minimal:hover
   {
    color:rgb(245,245,245)!important;
   }
  
  .Comment__meta
  {
   color:rgb(150,150,150)!important;
  }
  
  .CommentForm__meta__charactersRemaining, .CommentForm__meta__send
  {
     color:rgb(200,200,200)!important;
  }
  
  
              /* TOPIC PAGE  */
  
  
  .TopicSidebar__counts.u-clearfix, .TopicSidebar__qustionsComplete, .TopicSidebar__share
  {
   background-color: rgb(40,40,40)!important;
   color:rgb(185,185,185)!important;  
  }
  
  
  .TopicSidebar__divider, .TopicSidebar__counts__item
  {
   border-color:rgb(60,60,60)!important;
  }
  
  
  .TopicHome__navigation, .TopicActivity__sorting, .TopicHeader__header
  {
  background-color: rgb(40,40,40)!important;
  color:rgb(165,165,165)!important;  
  }
  

  .TopicHome__navigation__link.active
  {
   color:rgb(225,225,225)!important;  
  }
  
  
  .TopicSidebar__progressBar__status
  {
   background-color: rgb(145,145,145)!important;
  }
  
  
  .TopicSidebar__share__inputContainer__input
  {
   background-color: rgb(60,60,60)!important;
   color:rgb(225,225,225)!important;
  }
  
  
 
         /*  LEADERBOARDS  */
  
  
  .Leaderboards
  {
      background-color: rgb(40,40,40)!important;
  }
 
  
  .Leaderboard__row.Leaderboard__row--me
  {
      background-color: rgb(100,45,45)!important;
  }
  
 
  .Leaderboard__row, .Leaderboards__timeFilter__header
  {
  background-color: rgb(40,40,40)!important;
    color:rgb(225,225,225)!important;
  }
  
  
  
           /*  PROFILE  */
  
 
  .ProfileSidebar
  {
  background-color: rgb(40,40,40)!important;
  color:rgb(185,185,185)!important;
    fill:white!important;
  }
  
  
  .ProfileSidebar__topicWheel
  {
    
    background-color: rgb(125,125,125)!important;
  }
    
  .ProfileSidebar__divider, .ProfileCounts__item
  {
  border-color:rgb(60,60,60)!important;
  }
  
   .ListPopup__container, .ListPopup__header
  {
  background-color: rgb(40,40,40)!important;
  color:rgb(185,185,185)!important;  
  border-color:rgb(40,40,40)!important;
  }
 
   
  
           /*    CHAT    */
  
  .Chat
  { 
    background-color: rgb(40,40,40)!important;
    box-sizing: border-box;
    border: 2px solid rgb(50,50,50)!important;
    border-width: 2px;
  }
  
 
  .PlayerChat__header.u-clearfix
  {
     background-color: rgb(0,0,0)!important;
  }
  
  
  .PlayerChat__header__player__name__link
  {
      Color:rgb(225,225,225);
  }
  
  
  .ChatTimeline
  {
     background-color: rgb(0,0,0)!important;
  }
  
  
  .ChatTimeline__item__text
  {
    background-color: rgb(100,100,100)!important;
    Color:rgb(245,245,245)!important;
  }
  
  
  .ChatTimeline__item__text.ChatTimeline__item__text--me
   {
    background-color: rgb(90,100,110)!important;
    Color:rgb(245,245,245)!important;
  }
  
 
  .PlayerChat__inputs
  {
    background-color: rgb(45,45,45)!important;
  }
      

  .MessageInput
    {
    background-color: rgb(45,45,45)!important;
    Color:rgb(240,240,240)!important;
    }

  
  .ContactList__header__editing, .ContactList__header__editing__text
  {
   background-color: rgb(85,85,85)!important;
   Color:rgb(185,185,185);
  }
  
  
  .ConfigBubble.ConfigBubble--light, .ConfigBubble__text, .ConfigBubble__action.ConfigBubble__action--light
  {
  background-color: rgb(0,0,0)!important;
  Color:rgb(185,185,185);
  border-color:rgb(60,60,60)!important;
  }
  
  
  
          /* SETTINGS PAGE */
  
  
  .SettingsPage__main, .SettingsPage__row__label, .SettingsPage__profilePhoto_label, .SettingsPage__wallpaper_label, .SettingsPage__main__sidebar__title
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(185,185,185)!important; 
   border-color:rgb(60,60,60)!important;
  }
    
 
  .SettingsPage__main__sidebar__row.SettingsPage__main__sidebar__row--active
  
   {
   background-color: rgb(60,60,60)!important;
   Color:rgb(225,225,225)!important;
   }
  

  .SettingsPage__main__sidebar__row
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(185,185,185)!important; 
   border-color:rgb(60,60,60)!important;
  }
      
  
  .TextInput__field, .SettingsPage__setting__select, .DatePicker__select.DatePicker__select--year, .DatePicker__select.DatePicker__select--month, .DatePicker__select.DatePicker__select--day,
  .SettingsPage__setting__textareaInput
   {
     background-color: rgb(0,0,0)!important;
     Color:rgb(185,185,185);
   }
  
  
  .SettingsPage__main__sidebar__title, .SettingsPage__main__sidebar
  {
   border-color:rgb(60,60,60)!important;
  }
  
  .TextInput__label, .SettingsPage__setting__label, .DatePicker__header, .DatePicker__label, .GenderPicker__header
  {
    Color:rgb(185,185,185)!important; 
  }
  
  .SettingsPage__profilePhoto:hover, .SettingsPage__wallpaper:hover
    {
      background-color: rgb(85,85,85)!important;
    }
  
  
  .SettingsPage__profilePhoto_label, .SettingsPage__wallpaper_label
  {
    background-color:transparent!important;
  }
  
           /*   ABOUT PAGE   */
  
 
  .About
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(225,225,225)!important; 
  }
  
  
            /*  PEOPLE PAGE */
  
  
  .PeopleFilters.u-antialised, .PeopleFilters__header
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(225,225,225)!important;
   border-color:rgb(60,60,60)!important;
  }
  
  
            /*  TOPICS PAGE */
  
  
  .EmbedCollectionWidget.u-clearfix, .CollectionTopic__name, .TopicListItem.u-clearfix
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(185,185,185);
    
  }
  
  .TopicListItem__info__description.u-antialiased
  {
    Color:rgb(185,185,185);
  }
  
  
  .EmbedCollectionWidget__header__more.u-antialiased
   {
    background-color: rgb(85,85,85)!important;
   }
  
  
  
          /* ADMIN TOPIC PAGE  */
  
  
 .TopicPage__adminHeader, .TopicPage__adminHeader__link.active, .TopicHeader__creator.u-clearfix, .TopicSettings, .TopicQuestions, .TopicSettingsSidebar__row, .TopicSettingsSidebar,
  .TopicSettingsPermissions__group__header__title, .QuestionsList__questionList__question, .QuestionsList, .TopicSettingsSidebar__title
   {
   background-color: rgb(40,40,40)!important;
   Color:rgb(225,225,225);
   border-color:rgb(60,60,60)!important;
   }
  
  .TopicPage__adminHeader__link
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(165,165,165);
   border-color:rgb(60,60,60)!important;
  }
  
  
  .IconCreatorShapePicker
  {
      background-color: rgb(120,120,120)!important;
  }
  
  
  .SelectInput__input, .TopicSettingsDetails__textarea
   {
     background-color: rgb(0,0,0)!important;
     Color:rgb(185,185,185);
     border-style:none!important;
   }
  

  .TopicSettingsSidebar__row.active
  {
   background-color: rgb(60,60,60)!important;
   Color:rgb(225,225,225)!important;
  }
  
 
  .TopicSettings__saveCard__text, .TopicSettings__saveCard
  {
      background-color: rgb(100,100,100)!important;
      Color:rgb(245,245,245)!important;
  }
  
  
  .QuestionsList__questionList__question:hover
  {
   background-color: rgb(60,60,60)!important;
   
  }  

  
  .Popup__contents, .Popup__container.Popup__container--up, .Popup__container.Popup__container--down
  {
   background-color: rgb(0,0,0)!important;
   Color:rgb(225,225,225)!important;
   box-shadow: 0px 0px 4px 2px rgba(150, 150, 150, 0.175);
   border-color:rgb(60,60,60)!important;
   
      
  }
  
    
         /* QUESTION EDITOR SCREEN */
  

  .Modal__backdrop.Modal__backdrop--light
  {
   background-color: rgb(0,0,0)!important;
    opacity: 0.75;
  }
  
 
  .IsolatedModal.IsolatedModal--report
  {
   background-color: rgb(40,40,40)!important;
   Color:rgb(185,185,185);
   border: 10px solid rgb(150,150,150);
  }
  
  .QuestionEditor__actions__cancel
  {
   Color:rgb(185,185,185);
  }
  
  
  .ReportCommentForm__select, .TextareaInput
  {
     background-color: rgb(0,0,0)!important;
     Color:rgb(185,185,185);
  }  
  
    
         /* REPORT SCREEN */
  
   
  .IsolatedModal__backdrop 
  {
    background-color: rgb(0,0,0)!important;
    opacity: 0.75;
  }

  
           /* SEARCH */ 
  
  
  .SearchResults, .ListPopupTopic, .ListPopupPlayer, .ListPopupTopic__link, .SearchResults__header, .SearchResults__list, .ListPopupPlayer__link, .SearchResults__item.SearchResults__default
  {
   background-color: rgb(0,0,0)!important;
   Color:rgb(225,225,225);
   border-color:rgb(60,60,60)!important;
  }
  
  
  .ListPopupTopic__link:hover, .ListPopupPlayer__link:hover
  {
  background-color: rgb(60,60,60)!important;
  }
  

        /* NOTIFICATIONS */ 
  
  
  .NotificationsDropdown__items, .NotificationsDropdown__header
  {
  background-color: rgb(0,0,0)!important;
  Color:rgb(185,185,185);
  border-color:rgb(145,65,65)!important;
  }
  
  .NotificationItem
   {
  background-color: rgb(0,0,0);
  border-color:rgb(60,60,60)!important;
  Color:rgb(185,185,185);
  border-width: 1px;
  }
   
  .NotificationItem:hover
  {
   background-color: rgb(60,60,60);
  }
  
  
  .NotificationItem--inverted.NotificationItem--highlighted
   {
   border-color:rgb(60,60,60)!important;
   background-color: rgb(50,50,50)!important;
   }
    
   .NotificationItem--inverted.NotificationItem--highlighted:hover
   {
   background-color: rgb(70,70,70)!important;
   }
  
  .NotificationItem.NotificationItem--toast
  {
   background-color: rgb(0,0,0);
   Color:rgb(185,185,185);
   border-color:rgb(0,0,0)!important;
   box-shadow: 0px 0px 2px 2px rgba(0, 255, 0, 0.75)!important; 
       
  }
  
  .NotificationItem.NotificationItem--inverted.NotificationItem--toast.u-antialiased
  {
  box-shadow: 0px 0px 2px 2px rgba(255, 0, 0, 0.75)!important; 
  }
  
    
          /*  LOGIN  */
  
  .LoginModal, .LoginPage, .u-clearfix.Layout__content.Layout__content--loggedout
  {
    
   background-color: rgb(50,50,50)!important;
   Color:rgb(185,185,185);
   border: 0px solid rgb(150,150,150);
  
  }

         /*  BUTTONS  */
  
  .u-antialiased.PlayButton.PlayButton, .u-antialiased.FollowButton.FollowButton, .u-antialiased.ChatButton
  {
    background-color: rgb(0,0,0)!important;
    box-shadow: 0px 0px 1px 1px rgba(255, 255, 255, 0.4);
    
  }
  
  .u-antialiased.PlayButton.PlayButton--big.PlayButton--inverted, .u-antialiased.FollowButton.FollowButton--big.FollowButton--inverted.FollowButton--followed
  {
    background-color: rgb(255,84,84)!important;
  }
  
  
 .u-antialiased.FollowButton.FollowButton--big.FollowButton--inverted.FollowButton--followed, .u-antialiased.FollowButton.FollowButton--big.FollowButton--inverted.FollowButton--notFollowed
  {
    background-color: rgb(2,209,116)!important;
  }
  
  .Story__button.Story__button__like.u-antialiased.Story__button__like--liked
  
  {
    background-color: rgb(255,84,84)!important;
  }
  
  
}
