css:
@-moz-document url-prefix("http://www.hyunsdojo.com/community/") {
* {
	box-shadow: none !important;
	text-shadow: none !important;
	font-family: Tahoma, Calibri, Verdana, Geneva, sans-serif;
}
body {
	background: rgb(233, 233, 233) !important;
}
#body > div:nth-child(7) {
	position: relative !important;
	margin: 0 auto 10px auto !important;
	width: 1269px !important;
}
#header {
	width: 1269px !important;
	height: 106px !important;
	position: relative !important;
	background: #2f4456 !important;
	margin: 0px 0px 10px 0px;
	box-shadow: none !important;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
}
.logo {
	box-shadow: none !important;
	border: none !important;
	background: transparent !important;
}
.logo img {
	display: none !important;
}
.logo:before {
	content: "Hyun's Dojo" !important;
	position: absolute !important;
	margin: 5px 0px 0px 0px !important;
	left: -140px !important;
	top: 10px !important;
	color: white;
	font-weight: 700;
}
#user_background_wrapper {
	display: none !important;
}
div.m-nav {
	position: absolute !important;
	top: 55px !important;
	width: 1249px !important;
	height: 26px;
	left: -154px !important;
	background: #7192A8 url(http://forums.stickpage.com/images/buttons/newbtn_middle.png) repeat-x;
	padding-left: 20px !important;
}
.menu_btn {
	border-top: 1px solid #Cedfeb;
	border-left: 1px solid #Cedfeb;
	border-right: 1px solid #5a7f97;
	display: block;
	background: #7192A8 url(http://forums.stickpage.com/images/buttons/newbtn_middle.png) repeat-x;
	min-width: 60px;
	width: auto !important;
	text-align: center;
	height: 25px !important;
	padding: 0 10px;
	color: white !important;
	margin: 0 !important;
}
.menu_btn .text {
	margin-top: 5px !important;
}
.menu_btn .text i[class^="icon-"] {
	display: none !Important;
}
#top-search {
	position: absolute !important;
	top: 47px !important;
	width: 120px;
	height: 13px !important;
	font-size: 11px !important;
	vertical-align: middle !important;
	padding: 1px;
	border: 1px solid #6B91AB !important;
}
.searchicon {
	position: absolute !important;
	top: 48px !important;
	left: 130px !important;
	color: white !important;
	font-size: 36px !important;
	font-family: "entypo" !important;
}
#user_box_drop {
	display: block !important;
	width: 500px !important;
	background: none !important;
	border: none !important;
	top: 0px !important;
	text-align: right !important;
	right: -145px !important;
}
.drop_back, .drop_item .icon, #user_box .user_icon {
	display: none !important;
}
.drop_item {
	display: inline !important;
	background: none !important;
	border: none !important;
	padding: 2px !important;
}
a.drop-item {
	color: white !important;
	background: none !important;
}
.drop_item strong {
	color: #FFF;
	font-size: 11px !important;
	;
	display: inline-block;
	margin-top: 0px !important;
	padding-top: 3px;
	padding-bottom: 3px;
	padding-left: 4px;
	padding-right: 4px;
	font-weight: bold
}
#user_box {
	display: none;
}
#favorites, #notifications, #messages {
	top: 67px !important;
	left: -1000px !important;
	color: white !important;
	font-family: sans-serif !important;
}
#favorites .icon-heart:before {
	content: "Favorites";
	font-family: sans-serif !important;
}
#notifications .icon-bell-alt:before {
	content: "Notifications";
	font-family: sans-serif !important;
	margin-left: 30px !important;
}
#messages .icon-inbox:before {
	content: "Messages";
	font-family: sans-serif !important;
	margin-left: 80px !important;
}
#content {
	width: 1269px !important;
	top: -50px !important;
}
#profile_top_footer {
	height: 100% !important;
	width: 100% !important;
	position: absolute !important;
	z-index: -2 !important;
	background: white !important;
}
#profile_top_footer:before {
	content: "Hyun's Dojo Forum";
	position: absolute !important;
	margin-top: 40px !important;
	left: 10px !important;
	font-size: 14px;
	color: rgb(62, 62, 62) !important;
	font-weight: bold !important;
}
div#dforumlist {
	width: 1240px !important;
	margin-top: 50px !important;
	margin-left: 15px !important;
	border: none !important;
}
.forum_cat {
	background: #7192a8 url("http://forums.stickpage.com/images/buttons/newbtn_middle.png") repeat-x;
	padding: 2px 0px 2px 0px !important;
	font: bold 12px Arial, Tahoma, Calibri, Verdana, Geneva, sans-serif !important;
	color: #ffffff;
	clear: both;
	float: left;
	border: 1px solid #5a7f97;
	width: 100%;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
	margin-bottom: 10px !important;
}
.forum_cat h2 {
	font: bolder 12px Arial, Tahoma, Calibri, Verdana, Geneva, sans-serif !important;
	padding-left: 12px !important;
}
.forum_row_icon img {
	display: none !important;
}
.forum_row_icon {
	background: url("http://forums.stickpage.com/images/statusicon/forum_new-48.png");
	border: none !important;
	background-size: 100%;
	background-repeat: no-repeat;
	width: 48px !important;
	height: 48px !important;
}
.forum_row {
	background: rgb(255, 255, 255) !important;
	box-shadow: inset 0 -3px 20px rgba(200, 200, 200, 0.5) !important;
	color: #4e4e4e;
	border-bottom: 1px solid #c4c4c4;
	border-left: 1px solid #c4c4c4;
	border-right: 1px solid #c4c4c4;
}
.forum_wrap {
	margin-bottom: 10px !important;
	border: none !important;
	box-shadow: -3px 3px 3px rgba(200, 200, 200, 0.5) !important;
}
.forum_row .forum_row_name {
	color: #417394 !important;
	font-size: 13px !important;
	font-weight: bold !important;
}
.forum_row_right {
	background: none !important;
}
.forum_row_right_icon {
	display: none !important;
}
}
