@enabled true
==/UserCSS== */
@-moz-document domain("jizz.ppy.sh"){
#topic-header-container, #topic-header-container .forum-header {
    height: 200px;
}
.forum-post[data-post-position="1"] {
    padding-right: 60px;
}
.well {
    padding: 10px;
}
.bbcode {
    line-height: 1.5;
	font-size: 12px;
}
.bbcode h2 {
    font-size: 25px;
    padding-bottom: 5px;
}

.post-footer {
    height: 0;
}
.row-page{
    padding: 10px 10px 0px 10px;  
    min-height: 100px;
}
.forum__poster-box--extra {
    padding: 5px 0px 0px 0px;   
    margin: -5px 0px -5px 0px;
}
.hidden-xs {
    padding-bottom: 0px;
}
.forum__poster-box, .name, .forum__info-row, .title, .forum__info-row, .forum__user-flags, .forum__info-row {
    margin-bottom: 5px;
}
.forum__poster-box {
    padding: 5px 10px 0;
}

.forum-post {
    padding: 10px 10px 0 10px;
}
.post-viewer__actions {
    margin: -5px;
}
.post-panel {
    margin: 0px 0px -30px -10px;
}
.lock-status-icon {
    height: 32px;
}
.topics li {
    margin: 0px;
    padding: 5px;
}
.forum-category-header--main {
    height: 100px;
}
.topics {
    margin: 0px 0 0px 0;
}
.topics-container {
    padding: 0 10px;
}
.forums {
    margin: 0;
    padding: 0;
}
.forums>li, .left, .forums>li, .right {
    padding: 0px;
}
.clickable-row:hover {
    width: 100%;
}
}
