css:
@-moz-document domain("imdb.com") {
/*Hide non-essential visual elements */
#supertab /* Ad at the top */
, #nb_extra
, .quicksearch_dropdown_wrapper
, #megaMenu
, #title-social-sharing-widget /* "Share" button */
, .star-rating-button /* "Rate this" button */
, .rightcornerlink /* "Edit" links in details boxes */
, #sidebar
,  #nb_personal /* Signin button */
,  .article .ab_widget /* Suggested streamings at the bottom */
, .watchbar2
, .heroic-overview .plot_summary_wrapper .watchlist-watchbox--titlemain /* Showtime and watchlist buttons */
, .rec_actions /* Buttons "add to watchlist" in the "More like this" section */
, .article.contribute /* Button to edit a page */
, #bottom_ad_wrapper
, .pro_logo_main_title /* Link to imdb-pro */
, #amazon-affiliates /* Links at the very bottom */
{
    display: none;
}

/* Re-arrange the layout to better use the void we created */
#content-2-wide .main,
#content-2-wide:before {
    max-width: none;
    width: auto;
    float: none;
}

#root,
#navbar {
    width: 800px;
}
}
