css:
@-moz-document domain("trackitonline.ru") {

*, html, body, .panel, .btn-default, .dropdown-menu, .panel-default > .panel-heading, .listform
, .f-table-infotopic>tbody>tr>td
, .breadcrumb
 {
    color: #cfecfe;
    background-color: #000;
}
panel-body a:link, a:link {
    color:#00ffff;
    text-decoration:none
}
panel-body a:visited, a:visited {
    color:#ffd700;
}
a {
    color: #0fffff !important;
}
a:link {
    color: #00ffff !important;
}
a:visited {
    color:#ffd700 !important;
    ;
}
a:hover {
    color: red !important;
}
.panel a:link, .btn-default a:link {
    color: #00ffff !important;
}
.panel a:visited, .btn-default a:visited {
    color: #ffd700 !important;
}
.panel a:hover, .btn-default a:hover {
    color: #e00 !important;
}
.header-menu {
    background-color: #111111;
}
.btn-default .menu-side, .menu-side {
    background-color: rgba(70, 66, 61, 0.99);
}
h2 {
    color:#fec;
}
h1 {
    color:#fec;
}
form {
    color: #666666;
}
form .form_item input {
    color: inherit;
    background-color: #030303;
}
input .captcha {
    color: inherit;
    background-color: #000;
}
form .form_item select {
    color: inherit;
    background-color: #030303;
}
form .form_item option:disabled {
    color: red;
}
.item-disabled {
    color: red !important;
}
.note
, .pagination>li>a, .pagination>li>span
 {
    background-color: #000;
}
.footer {
    /* IE9 SVG, needs conditional override of 'filter' to 'none' */
    background: url(data:image/svg+xml;base64,);
    background: -moz-linear-gradient(top, rgba(206, 206, 206, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
    /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(206, 206, 206, 0.65)), color-stop(100%, rgba(0, 0, 0, 0)));
    /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(206, 206, 206, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
    /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(206, 206, 206, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
    /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(206, 206, 206, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
    /* IE10+ */
    background: linear-gradient(to bottom, rgba(206, 206, 206, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
    /* W3C */
}
.footer .copyright {
    color: #888;
}
[data-toggle="buttons"] .active {
    background-color: rgba(236, 151, 149, .7);
}
.btn-danger, .btn-danger:hover {
    color: #fff !important;
}
.notered {
    color: #fefefe;
    background-color: #010101;
}
.media-list-striped .striped:nth-child(odd) {
    background-color: #010101;
}
.media-list-striped .striped:nth-child(even) {
    background-color: #020202;
}
.media-list-striped .striped .postlogo {
    background-color: #111;
}
.panel-warning {
    border-color: #FFD172;
    background-color: #600;
}
.dropdown-menu>.active>a, .dropdown-menu>.active>a:focus, .dropdown-menu>.active>a:hover {
    background-color: #222;
}
/* MEDIA */
 button[disabled], html input[disabled] {
    background-color: #283317;
}
.form-control {
    color: #f9e9e9;
    background-color: #2a208c;
}
.chosen-container-single .chosen-single {
    background: linear-gradient(#494eae 20%, #3940c3 50%, #161075 52%, #131163 100%);
}
	

}
