css:
@-moz-document url-prefix("http://elaws.e-gov.go.jp/search/") {
a:link {
    text-decoration: none;
}
a:hover {
    text-decoration: overline;
}

body {
  height: 100%;
}

/* --- e-Govヘッダ部分 --- */
#header,#gNavi {
    display: none;
}

#lsg0500 > div.container-fluid > table:nth-child(1) {
    margin-bottom: 1px;
}

#lsg0500 > div.container-fluid > table:nth-child(1) > tbody > tr > td:nth-child(2) > table > tbody > tr:nth-child(2) > td {
    font-size: 12px;
}

#lsg0500 > div.container-fluid > table:nth-child(1) {
    float: right!important;
    width: 50%!important;
}

#lsg0500 > div.container-fluid > table:nth-child(2) {
    float: left!important;
    width: 50%!important;
}

ul.nav.nav-tabs {
    text-align: right;
}

.nav > li > a {
    padding: 2px 15px;
}

#lsg0500 > div.container-fluid > table:nth-child(2) > tbody > tr:nth-child(1) > td:nth-child(2) {
    display: none;
}

/* --- 右枠 --- */
div#right_content {
    width: 75%!important;
    height: 65%!important;
}

/* --- 左枠 --- */
div#left_content {
    width: 25%!important;
    height: 550px;
}

/* --- e-Govフッタ部分 --- */
div#content_footer,div#lsg0500 > div:nth-child(2) {
    display: none;
}

/* --- 目次 --- */
.TOCPartTitle {
    margin: 0em 0.5em 0em 0.5em!important;
}
.TOCPart .TOCChapterTitle, .TOCChapterTitleNum {
    margin: 1em 0em 1em 0em!important;
}
.TOCSectionTitle, .TOCPart .TOCSectionTitle {
    margin: 2em 0.5em 0em 0.5em!important;
}
.TOCSubsectionTitle, .TOCPart .TOCSubsectionTitle {
    margin: 3em 0.5em 0em 0.5em!important;
}
.TOCDivisionTitle, .TOCPart .TOCDivisionTitle {
    margin: 4em 0.5em 0em 0.5em!important;
}

/* --- 本文 --- */
.LawBody {
    column-width: 35em;
    column-count: auto;
    column-fill: auto;
    column-gap: 3em;
    writing-mode: vertical-rl;
    text-align: justify;
    margin: 1em;
    padding: 3em;
    width: 100%;
    height: 75%;
}

/* --- 条文 --- */
/* --- 本則 --- */
/* --- 編 --- */
.PartTitle {
    margin: 0em 1em 0em 0em!important;
}
/* --- 章 --- */
.ChapterTitle {
    margin: 0em 1em 0em 0em!important;
}
/* --- 節 --- */
.SectionTitle {
    margin: 0em 1em 0em 0em!important;
}
/* --- 款 --- */
.SubsectionTitle {
    margin: 0em 1em 0em 0em!important;
}
/* --- 目 --- */
.DivisionTitle {
    margin: 0em 1em 0em 0em!important;
}
/* --- 見出し --- */
.ArticleCaption {
    margin: 0em 1em 0em 0em!important;
}
/* --- 本文 --- */
.ParagraphSentence {
    margin: 0em 0.5em 0em 0em!important;
}
/* --- 項 --- */
.Item {
    margin: 1em 0.5em 0em 0em!important;
}
/* --- 号 --- */
.Subitem1Sentence {
    margin: 1em 0.5em 0em 0em!important;
    text-indent: -1em!important;
}
/* --- 号の細分 --- */
.Subitem2Sentence {
    margin: 2em 0.5em 0em 0em!important;
    text-indent: -1em!important;
}
.Subitem3Sentence {
    margin: 3em 0.5em 0em 0em!important;
    text-indent: -1em!important;
}
/* --- 附則 --- */
.SupplProvisionLabel {
    margin: 0em 1em 0em 0em!important;
}

}
