css:
@-moz-document domain("hyunsdojo.com") {
*{
    -webkit-box-shadow: none !important;
    box-shadow:none!important;

    border-radius: 0px!important;
}
#header {
    background: linear-gradient(to top, #172635 0%, #385776 100%);
    background-image: url('https://cdn.pbrd.co/images/1tNUCKXf.png');
    color: #FFF;
	text-shadow: 2px 2px 2px rgba(0,0,0,1.3);
    box-shadow:2px 2px 2px rgba(0,0,0,0.3)!important;
}
.logo, #favorites div, #notifications, #messages div, .post_info{
  -webkit-filter: grayscale(0%) brightness(90%) sepia(100%) hue-rotate(+180deg) saturate(100%) !important;
}
.logo{
	background-color: #202020;
}
body {
    background: url('https://cdn.pbrd.co/images/1tNUwvoj.png') 0 0;
    background-repeat: repeat-x;
    background-color: #202020;
}
#front_menu .front_menu_a.label {
        color: #FFF;
}
.m_msg {
    background: #f0f0f0;
    color: #111;
    padding: 10px;
    border-bottom: 1px dotted #aaa;
    text-shadow: 0px 0px 0px rgba(0,0,0,0.3);
}

.quotetitle, .attachtitle { 
    color: #FFF;
	text-shadow: 0px 0px 0px rgba(0,0,0,0.3);
}
.menu_btn {
	color: #FFF !important;
    text-shadow: 2px 2px 2px rgba(0,0,0,1.3);
}
.button {
    color: #020e18;
	font-weight: Normal;
	font-size: 14px;
    font-family: "Lucida Grande", Helvetica, Arial, sans-serif;
}
.forum_cat a {
	font-family: 'Lucida Grande', sans-serif !important;
	font-weight: bold;
    font-size: 0.8em;
    text-shadow: 0px 0px 0px rgba(255,255,255,0.3);
	color: #3e6689 !important;
	text-decoration: none;
}
.forum_cat, .view_forum_top, .ptl_title, #ptl_menu .front_menu_a.label, #front_menu .front_menu_a.label, #chat_tabs .chat_tab .chat_new_box .chat_box_title, .button, .right_cat, .usr_cat, input[type=submit], button, #chat_tabs .chat_tab, .more_info h3, .ptl_cat, dd input, .submit-buttons input, .box_header, .box_footer, .quotetitle, .attachtitle, .codetitle{
        background: linear-gradient(to top, #172635 0%, #385776 100%);
}
.forum_cat, #chat_tabs .chat_tab .chat_new_box .chat_box_title, .button, .right_cat,.usr_cat, #chat_tabs .chat_tab, .more_info h3,.ptl_cat,dd input, .submit-buttons input {
    background: linear-gradient(to bottom, #D0D0D0 0%, #ECECEB 100%);
}
.forum_row {
	font-family: 'Open Sans', sans-serif;
	color: #385675;
	padding-top: 6px;
    padding-right: 0px;
    padding-bottom: 0px;
    padding-left: 8px;
	background: #ffffff;
	border-bottom: 1px solid #879098;
}
.forum_row .forum_row_name {
	color: #020e18;
	font-weight: Bold;
	font-size: 14px;
    font-family: "Lucida Grande", Helvetica, Arial, sans-serif;
}
.forum_row_right {
    padding: 12px;
	background: #d5dce1;
    margin-top: -7px;
    margin-right: 0px;
    margin-bottom: 0px;
    margin-left: 0px;
}

.forum_cat h2 {
	font-family: 'Lucida Grande', sans-serif !important;
	font-weight: bold;
    font-size: 1.4em;
	color: #3e6689 !important;
	text-decoration: none;
}
#chat_tabs .chat_tab .chat_tab_title {
	color: #FFFFFF;
	background: linear-gradient(to top, #172635 0%, #385776 100%);
}

/*following written by m_x*/
.photo_view_image {
     max-width: 100% !important;
  	 width: auto !important;
    max-height: 100vh !important;
}

}
