css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("progtest.fit.cvut.cz") {
  
body {
  background-color:#f7c8dc;
  font-family:"Open Sans";
}
  
table.header {
  border:none;
}

.topLayout {
  width:80vw;
}

input, button, select, tr {
  font-family:"Arial";
}

body select {
  padding:5px 10px;
}
  
a.navLink:hover {
  color:#666666;
}

.outBox,.insBox {
  border:none;
  background-color:transparent;
}

.insBox {
  width:80vw;
  display:block;
  margin:10vh auto;
  background:yellow;
  position:relative;
}

table:nth-child(1) td {
  background-color:#ffffff;
  padding:20px;
  border:none;
}

table#main {
  position:absolute;
  top:0;left:0;
  display:block;
  margin:0 auto;
  width:100%;
  border-radius:30px;
  background-color:#ffffff;
}

table.topLayout {
  width:80vw;
  border-radius:30px;
  overflow:hidden;
  margin:10vh auto;
}

table.topLayout tr:hover td {
  background-color:#eeeeee;
}

.but1,.outButton {
  background-color:transparent;
}

.but1 > div {
  border-radius:30px;
  padding:5px 10px;
  background-color:#e82d7c;
  cursor:pointer;
}

.outButton input {
  border-radius:30px;
  padding:5px 10px;
  background-color:#e82d7c;
  display:block;
  height:auto;
  color:#ffffff;
  cursor:pointer;
}

.but1 > div:hover {
  background-color:#be125b;
}

.but1 > div a {
  color:#ffffff;
  width:100%;
  display:block;
}

.but1 .hdrDis {
  background:none;
  border:none;
  color:#666666;
  font-size:16px;
  text-align:center;
}

.but1 .hdrDis:hover {
  background:none;
}


}
