css:
@-moz-document domain("diki.pl") {
img[src="/images/miniicons/link.gif"] {display: none;}

form.dikiSearchForm.dikiSearchHeaderTop {margin: 10px 0px -10px 15px;}
div.dikibody form.dikiSearchForm {border:none; border-bottom: 1px solid #c30407; border-radius:0px}
div.dikibody form.dikiSearchForm .dikiSearchInputField {width: 450px;}
 

div.loginLinksTopRightCorner,
td[style="vertical-align: top; padding-right: 1em;"],
div.diki-panels,
div.eTutorPromotionalLink,
div.additionalSentences,
div.showCollocationsLink,
a.grammarTag,
div.collocationSearchResults,
a.starsForNumOccurrences,
button.dikiSearchMainPageSubmit,
div.banner,
div.dikiPromoLeftBottomBanner,
div.dikiBackgroundBannerPlaceholder > a[href="/dictionary/about/repetitions-invitation"]
{display:none}

body.dikibody {border-top: 4px solid white}
div.dikibody table.dsplit td.ehl {border-width: 0px; border-right-width: 1px;}
div.dikibody {margin: 0 50px}

div.jqac-menu {margin-top: 7px; margin-left: -5px; border-color: #c30407;}



  div.lastSearchesTabsLabel,
div.addsentshow,
object,
div.center,
div.footer,
div.lastSearchingLabel,
h2.dictionarySectionHeader,
div.fb-container,
div#logindiv,
a[href='/slownik-angielskiego/'],
span.icon-add-to-repetitions,
a.gtag,
div.cat,
ul[style="line-height:1.8"],
ul[style="line-height:1.8; margin-top:3px;"],
  input#dictionarySearchSubmit,
  div.eTutorPromotionalLink
 
  {display:none}
 


  div.dikibody {margin:1% 3%;}
  div.dikitop {padding-left:1%}
 
  div.addsent {height:32px; overflow:hidden; opacity:0.3;}
  div.addsent:hover {height:100%; opacity:0.5}
  div.addsent p {padding:0; margin:0}
 
  div.dikibody {font-size:0.85em}
 
  div.exampleSentence {opacity:0.3; }
  div.exampleSentence:hover {opacity:0.3}
 
  div.dikibody #searchfield {width:300px;}
 
  .diki-panels {position:absolute;top:16px;right:0px;}
 
  div.dikitop {padding-top:46px;}
 
div.additionalSentences {height:33px;overflow:hidden}


div.dikibody > div.dikitop div.dikiHeaderLogo,
div.dikibody > div.dikitop div.dikiHeaderMenuIcon,
div.diki-results-right-column div.partOfSpeechSectionHeader,
div.dikiBackgroundBannerPlaceholder > div.dictionaryCollapsedSection,
div.dikiBackgroundBannerPlaceholder > div.partOfSpeechSectionHeader
{display: none;}

form.dikiSearchForm input[type=search] {border: none;}
div.dikibody div.diki-results-container {border: none;}
div.dikibody div.hws {margin-top: 0px;}
div.diki-results-container.clearfix div.diki-results-left-column div.hws > h1 {margin-top: 0px;}
div.dikibody div.dikitop div.dikiHeaderInputSearch input {padding-left: 0px}
div.dikibody form.dikiSearchForm .dikiSearchInputField {width: 470px;}


div.dikiHeaderAdContainer,
#contentWrapper > div.dikiBackgroundBannerPlaceholder > p > img,
#contentWrapper > div.dikiBackgroundBannerPlaceholder > p > a,
#automaticTranslationgnoaukd
{display:none}
#contentWrapper > div.dikiBackgroundBannerPlaceholder > p {margin-left: 15px;}

}
