@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.soup.io/"), url-prefix("https://www.soup.io/") {
#head {
    background-color: rgb(32, 32, 32);
    height: 120px;
    color: rgb(0, 0, 0);
    overflow: hidden;
    margin-bottom: 1em;
}


body {
    background-color: #000000 !important;
    height: 100%;
    width: 100%;
    font-size: 14px;
    color: white;
    margin: 0;
}

a:link, a:active, a:hover, a:visited {
    color: rgb(225, 225, 225);
}

h1, h2, h3, h4, h5 {
    color: rgb(255, 255, 255);
}

.post .content .source {
    clear: both;
    font-size: 12px;
    color: rgb(225, 225, 225);
}

#bar ul#menu li.sel a {
    background-color: rgb(225, 225, 225);
}

#loginsignup_header #signuplink, .state-toggled #loginsignup_header #loginlink {
    background: none repeat scroll 0% 0% rgb(225, 225, 225);
    color: rgb(0, 0, 0);
    font-size: 1.1em;
    margin-top: 0px;
}

#loginsignup {
    padding: 1em 1em 0.3em;
    background-color: rgb(0, 0, 0);
}

body.frontpage #loginsignup label {
    color: rgb(255, 255, 255);
}

#features h3, #features2 h3 {
    background-color: rgb(0, 0, 0);
    padding: 1px 6px;
    margin-top: 0.8em;
    line-height: 1.1em;
    display: inline;
    color: white;
}

#posts .post_quote .body {
    color: rgb(225, 225, 225);
}

.post_image.f_nsfw.ad .content img {
    display: none;
}

.post.ad {
    display: none;
}
}
