@-moz-document url-prefix("http://www.jeuxvideo.com"), url-prefix("https://www.jeuxvideo.com") {
/* On commence doucement les enfants :hap: */
/* On modifie le fond du site et on agrandit l'espace entre la barre du menu et la page */

#content {
background-color: #2a2a2a !important;
background-image: url("http://image.noelshack.com/fichiers/2015/43/1445337155-essaieforumjv.png") !important;
} 
#content { 
padding-top: 100px !important; 
} 
}

@-moz-document url-prefix("http://www.jeuxvideo.com/forums/0-1000021-0-1-0-1-0-communaute.htm"), url-prefix("https://www.jeuxvideo.com/forums/0-1000021-0-1-0-1-0-communaute.htm") {
/* Petite dédicace au Communauté, ils ont leur propre fond :cool: */

#content {
background-color: #2a2a2a !important;
background-image: url("http://image.noelshack.com/fichiers/2015/43/1445337154-backgroundforumblabla.png") !important;
}
#ascrail2001 >:nth-child(1) {
background-color: #F3680C  !important;
} 
}

@-moz-document url("http://www.jeuxvideo.com/forums/0-50-0-1-0-1-0-blabla-15-18-ans.htm"), url("http://www.jeuxvideo.com/forums/0-51-0-1-0-1-0-blabla-18-25-ans.htm"), url("http://www.jeuxvideo.com/forums/0-15-0-1-0-1-0-blabla-moins-de-15-ans.htm"), url("http://www.jeuxvideo.com/forums/0-53-0-1-0-1-0-blabla-35-ans-et-plus.htm"), url("https://www.jeuxvideo.com/forums/0-50-0-1-0-1-0-blabla-15-18-ans.htm"), url("https://www.jeuxvideo.com/forums/0-51-0-1-0-1-0-blabla-18-25-ans.htm"), url("https://www.jeuxvideo.com/forums/0-15-0-1-0-1-0-blabla-moins-de-15-ans.htm"), url("https://www.jeuxvideo.com/forums/0-53-0-1-0-1-0-blabla-35-ans-et-plus.htm") {
/* J'ai pas oublié les forums calmés vous roh ! :hap:*/

#content {
background-color: #2a2a2a !important;
background-image: url("http://image.noelshack.com/fichiers/2015/43/1445337154-essaieforum.png") !important;
} 
#ascrail2001 >:nth-child(1) {
background-color: #F3680C  !important;
} 
}

@-moz-document domain("jeuxvideo.com") {
/* Là c'est le gros pavé de la mort qui tue :hap: */

#prospect,
#prospect.show-pub,
#prospect #table-prospect,
.part-boutique, 
.bonus-track, 
.bonus-sponso,
.mdd-card-suggest, 
#jv-feedback .btn-feedback,
#prospect-square.show-pub, 
#prospect-square2.show-pub,
#prospect-square3.show-pub {
display:none !important;
}/* Supprime les pubs parce que sinon ça fait mosh */

