css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {

    body {
        background: rgba(122, 223, 206, 1);
        background: -moz-linear-gradient(top, rgba(122, 223, 206, 1) 0%, rgba(114, 149, 206, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(122, 223, 206, 1)), color-stop(100%, rgba(114, 149, 206, 1)));
        background: -webkit-linear-gradient(top, rgba(122, 223, 206, 1) 0%, rgba(114, 149, 206, 1) 100%);
        background: -o-linear-gradient(top, rgba(122, 223, 206, 1) 0%, rgba(114, 149, 206, 1) 100%);
        background: -ms-linear-gradient(top, rgba(122, 223, 206, 1) 0%, rgba(114, 149, 206, 1) 100%);
        background: linear-gradient(to bottom, rgba(122, 223, 206, 1) 0%, rgba(114, 149, 206, 1) 100%);
        font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
        font-weight: 300 !important;
    }

    h3,
    .js-open-board,
    .sidebar-section-header-title,
    .header-btn-text,
    .board-header-btn-name,
    .strong,
    .button-link,
    .window-title-text,
    .js-open-move-from-header {
        font-weight: 300 !important
    }

    .board-header-btn-name-org-logo {
        -webkit-filter: contrast(3000%) invert(100%);
    }

    .board-canvas {
        padding-top: 20px
    }

    .thumbnail {
        background: #979798 !important
    }

    .sidebar-section-header-title,
    .text,
    .board-header-btn-text,
    .window-wrapper a {
        text-decoration: none !important;
    }

    #search-text {
        border-radius: 0px !important
    }

    .board-header {
        margin-bottom: 20px !important;
        padding-bottom: 0px !important
    }

    .header-btn.header-notifications,
    .header-btn.header-boards,
    .header-btn.header-search,
    .header-btn.header-login,
    .header-btn.header-signup,
    .header-btn:hover,
    .header-member,
    .list-card-operation,
    .window,
    .add-list,
    .boards-drawer .quiet-button {
        background: none !important;
    }

    .member {
        border-radius: 20px !important;
        border: none !important;
        background: none !important;
    }

    .member img {
        border-radius: 0 !important;
        border: none !important;
    }

    .list-area {
        margin-top: 30px !important;
    }

    .list {
        background-color: rgba(255, 255, 255, 0.3) !important;
    }

    .list-card:hover {
        transition: background 0.5s ease-out !important
    }

    .add-list:hover {
        background: #fff !important;
    }

    .add-list span {
        color: #F45B5B !important;
    }

    .list-card {
        border: 1px #e5e5e5 !important;
    }

    .open-card-composer {
        color: #666 !important;
    }

    .open-card-composer:hover {
        text-decoration: none !important;
        background: none !important;
        color: #000 !important;
    }

    .window-wrapper {
        background: rgba(255, 255, 255, 0.9) !important;
    }

    .window-wrapper h3 {
        font-weight: normal !important
    }

    .js-open-move-from-header,
    .checklist-progress-bar-current,
    .confirm,
    .ui-state-active {
        background: #7295CE !important
    }

    .icon-sm {
        color: #4d4d4d !important;
    }

    .icon-sm:hover {
        color: #000 !important;
    }

    .list-card-labels .card-label {
        height: 4px !important;
        width: 10px !important;
    }

    .icon-edit {
        color: #000 !important;
    }

    .list-card-details {
        padding: 6px 6px !important;
    }

    .pop-over,
    .boards-drawer {
        background: rgba(0, 0, 0, 0.8) !important;
    }

    .board-menu,
    .board-menu a,
    .board-menu,
    .board-menu .icon-sm,
    .board-menu .icon-lg,
    .board-menu .quiet,
    .pop-over-list li > a .sub-name,
    .pop-over .quiet,
    .pop-over .icon-sm,
    .boards-drawer .sidebar-section-header.quiet,
    .boards-drawer .sidebar-section-header a,
    .boards-drawer .sidebar-section-header .icon-sm,
    .boards-drawer .sidebar-section-header .icon-lg,
    .boards-drawer .quiet-button,
    .u-inline-block a,
    .primary {
        color: #fff !important;
    }

    .u-inline-block a {
        padding: 5px;
        border-radius: 5px;
    }

    .pop-over .disabled,
    .pop-over .disabled .sub-name,
    .pop-over .disabled .icon-sm {
        color: #666 !important;
        opacity: 1;
    }

    .board-menu .button-link,
    .board-menu .action-comment {
        background: rgba(0, 0, 0, 0.3) !important;
        border: 0px solid black !important;
    }

    .board-menu-navigation-item-link:hover,
    .board-menu-section-header:hover,
    .board-menu .button-link:hover,
    .board-menu .action-comment:hover,
    .empty,
    .search-suggestion.selected,
    .search-suggestion:hover,
    .boards-drawer .quiet-button:hover,
    .quiet-button:hover,
    .board-menu,
    .window-overlay {
        background: rgba(0, 0, 0, 0.5) !important;
    }

    .pop-over,
    .pop-over-header-title,
    .pop-over a,
    .empty {
        color: #fff !important;
    }

    .pop-over {
        border: none !important;
    }

    .pop-over-list li > a:hover {
        background: rgba(41, 143, 202, 0.5) !important;
    }

    .empty {
        border: 1px solid #333 !important;
    }

    .search-result-card {
        padding-bottom: 30px !important;
        margin-bottom: 10px !important;
    }

    .search-result-card-container-fade {
        display: none !important;
    }

    form .button-link,
    .search-result-card .list-card-title,
    .form-grid,
    .pika-table abbr {
        color: #333 !important;
    }

    /* Stack Boards - Responsive View */
    #board {
        display: block !important;
        text-align: left !important;
        position: relative !important;
        white-space: initial !important;
        padding-bottom: 24px !important;
    }

    .board-canvas {
        height: auto !important;
    }

    .body-board-view {
        overflow-y: visible !important;
    }

    .js-list,
    .js-add-list {
        display: inline-block !important;
        vertical-align: top !important;
        margin: 0 0 10px 10px !important;
    }

    .u-gutter {
        margin-left: 0 !important;
    }

    /* End Stack Boards - Responsive View */

    /* Show Text on Labels */
    .body-board-view .list-card-labels .card-label {
        font-weight: bold;
        color: #fff !important;
        text-transform: uppercase;
        letter-spacing: 0.1em !important;
        line-height: 14px !important;
        height: auto !important;
        padding: 2px 6px !important;
        margin-right: 5px !important;
        width: auto !important;
        text-shadow: none !important;
        font-size: 8px !important;
        opacity: .8 !important;
        margin-left: 0 !important;
    }

    .body-board-view .list-card-labels {
        display: flex !important;
    }

    /* End Show Text on Labels */

}
