css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://apps.facebook.com/mousehunt/") {
  .canvas_main_content, #pagelet_iframe_canvas_content {
    background-color: #FDFCF1 !important;
  }
}

@-moz-document url-prefix("https://www.mousehuntgame.com/canvas/"), url-prefix("https://www.mousehuntgame.com/") {
  
  /* Blanket styling for most of the page */
  
  body {
    font-family: Arial !important;
  }
  
  body, .container, .mousehuntHeaderView, body.fixedcanvas .hgSite {
    background-color: #FDFCF1 !important;
  }
  
  body.fixedcanvas {
    background: #FDFCF1 !important;
  }
  
  /* Styling for HitGrab site's sidebar */
  
  .hgSideBar div {
    font-size: 12px !important;
  }
  
  .hgSideBar .groupad {
    background-color: white !important;
    border-radius: 10px !important;
    font-family: Cambria !important;
    box-shadow: 1px 1px 2px gainsboro;
    padding-bottom: 5px !important;
  }
  
  .hgSideBar tr, .hgSideBar td, .hgSideBar td a {
    font-size: 11px !important;
  }
  
  .hgSideBar table {
    background-color: white !important;
  }
  
  /* Styling for the header portion */
  
  .mousehuntHeaderView-gameTabs a {
    font-size: 12px !important;
  }
  
  .mousehuntHeaderView-newsTicker {
    border: 0px !important;
  }
  
  .mousehuntHeaderView-newsTicker div {
    font-size: 12px !important;
    margin: 5px !important;
  }
  
  .dropdownmenu li a {
    font-family: Calibri !important;
    font-size: 1.4em !important;
    color: #374662 !important;
  }
  
  div.hudstatlist {
    font-size: 12px !important;
    padding-right: 10px !important;
  }
  
  .hornresponse div {
    font-size: 12px !important;
  }
  
  /* Sizing for Journal text */
    
  .journaltext {
    font-size: 12px !important;
  }
  
  /* Styling for the left half of Camp content */
  
  .campLeft .tabbar .control div {
    font-family: Calibri !important;
    font-size: 14px !important;
  }
  
  .campLeft .tabbar .control .inactive div.bcenter {
    color: #654732 !important;
  }
  
  .campLeft #trapSelectorEffectivenessContainer .header {
    font-size: 11px !important;
  }
  
  .campLeft #trapSelectorImage, .campLeft #trapSelectorEffectivenessContainer {
    background-color: white !important;
  }
  
  .campLeft #trapSelectorStats {
    font-family: Cambria !important;
  }
  
  .campLeft #trapSelectorCurrentTrap#trapSelectorCurrentTrap .empty {
    font-family: Arial !important;
    font-size: 10px !important;
    line-height: 12px !important;
  }
  
  .campLeft #trapSelectorEffectiveness {
    font-size: 13px !important;
  }
  
  .campLeft #huntingTips .header {
    font-family: Cambria !important;
    font-size: 16px !important;
  }
  
  .campLeft #huntingTips .content p {
    font-family: Calibri !important;
    font-size: 14px !important;
  }
  
  /* Styling for the right half of Camp content */
  
  .campRight .adventureBookBanner-container div, .adventureBookBanner-container span {
    font-size: 12px !important;
  }
  
  .campRight .adventureBookBanner-adventureName {
    padding: 8px !important;
  }
  
  .campRight .adventureBookBanner-moreInfo {
    font-family: Calibri !important;
    line-height: 12px !important;
    border: 1px solid #8E7664 !important;
    padding: 2px 0 0 0 !important;
  }
  
  /* Styling for the tabs for sub-menus */
  
  #tabbarControls_page .control a {
    font-family: Arial !important;
    font-size: 12px !important;
  }
  
 #tabbarControls_page .control a:hover {
    font-family: Arial !important;
    font-size: 12px !important;
    color: #354767 !important;
  }
  
  /* Blanket styling for non-camp contents */
  
  .dialogBox-WarmBrown {
    font-size: 14px !important;
  }
  
  #tabbarContent_page {
    font-size: 12px !important;
  }
  
  #tabbarContent_page div.itembox {
    background-color: white !important;
    font-family: Arial !important;
    font-size: 12px !important;
  }
  
  #tabbarContent_page div.description {
    font-family: Arial !important;
    font-size: 12px !important;
    backgrond-color: pink !important;
  }
  
  #overlayPopup div.description {
    font-family: Arial !important;
    font-size: 13px !important;
  }
  
  #tabbarContent_page_1 span, #tabbarContent_page_3 span, #tabbarContent_page_4 span {
    font-size: 12px !important;
    line-height: 1.5em !important;
  }
  
  #tabbarContent_page_2 span, #tabbarContent_page_5 span {
    font-family: Calibri !important;
    font-size: 12px !important;
    line-height: 1.1em !important;
  }
  
  .inventoryitemview .itembox.recipe .components .part, .recipeOptions li, .inventoryitemview .itemBoxSmall .name {
    font-family: Calibri !important;
    font-size: 12px !important;
    line-height: 1.1em !important;
  }
  
  .larrybox .larryboxContent p {
    font-size: 12px !important;
  }
  
  /* Styling for Travel tab's content */
  
  #tabbarContent_page_0 #mapControls {
    font-size: 12px !important;
  }
  
  #tabbarContent_page_0 .environmentShops span {
    font-family: Lucida Sans !important;
    font-size: 9px !important;
  }
  
  #tabbarContent_page_0 .areafriends {
    padding-top: 5px !important;
    font-size: 12px !important;
  }
  
  /* Blanket styling for item related content */
  
  .tagDirectory a.tag {
    font-size: 12px !important;
  }
  
  .itempurchase {
    background-color: white !important;
  }
  
  .itempurchase li {
    font-size: 12px !important;
  }
  
  .itempurchase li.desc {
    font-family: Calibri !important;
    font-size: 14px !important;
  }
  
  .inventoryitemview .itembox.convertible input[type="button"], div.contentcontainer input[type="submit"], #overlayPopup .button, .jsDialogClose, #overlayPopup.themeSelector .themeContainer input {
    font-family: Arial !important;
    font-size: 12px !important;
  }
  
  .itemImageBox {
    background-color: white !important;
  }
  
  /* Styling for Friends content */
  
  #friendContainer {
    background-color: white !important;
  }
  
  #friendControls .control h1 {
    font-size: 11px !important;
  }
  
  ul.friendlistul {
    font-size: 12px !important;
    line-height: 1.3em !important;
  }
  
  #socialInviteForm .friendList .recipient, #supplytransfer .friendList .recipient span, #supplytransfer-recipient-span, #supplytransfer-item-span, #supplytransfer .itemList .details {
    font-size: 12px !important;
  }
  
  #giftSelectorContainer .friendList span.content, #giftSelectorContainer .backButton, #GiftSelectorMenu input {
    font-size: 12px !important;
  }
  
  #GiftSelectorGiftList .giftbox {
    background-color: white !important;
    border: 1px solid #E9E1C6 !important;
  }
  
  #GiftSelectorGiftList .giftbox:hover {
    border: 1px solid black !important;
  }
  
  /* Styling for Teams content */
  
  .tournamentHeader .buttonBackToListings {
    font-size: 12px !important;
  }
  
  .teamAwards .awardItem {
    background-color: white !important;
  }
  
  .tournamentListingRow .heading {
    font-size: 12px !important;
  }
  
  .tournamentListingRow .value {
    font-size: 13px !important;
  }
  
  .scoreboard-tournaments td {
    font-size: 12px !important;
  }
  
  .RewardInformation .reward b {
    font-size: 13px !important;
  }
  
  .RewardInformation .reward .range {
    padding: 3px 0 3px 0 !important;
  }
  
  .RewardInformation .reward .rewardRow {
    font-size: 12px !important;
    line-height: 25px !important;
  }
  
  /* Styling for popup contents */
  
  #overlayPopup .jsDialogContainer {
    font-size: 12px !important;
  }
  
  #overlayPopup.larry_mouse_manual_popup .catchStats {
    font-family: Calibri !important;
    font-size: 13px !important;
    line-height: 1.1em !important; 
  }
  
  #overlayPopup.larry_mouse_manual_popup .mouseHeader {
    font-family: Cambria !important;
    font-size: 12px !important;
  }
  
  #overlayPopup.larry_mouse_manual_popup .mouseContent .mouseDifficulty {
    font-size: 14px !important;
    font-family: Calibri !important;
  }
  
  #messengerUINotification .notificationMessageList .tournaments .messageText, .MHCheckoutFooter .viewTOS, .adventureBookPopup-adventure-details div {
    font-size: 12px !important;
  }
  
  .adventureBookPopup-header-title, .adventureBookPopup-header-subtitle {
    font-family: Cambria !important;
  }
  
  .marketplace .featured-item-info {
    font-size: 11px !important;
  }
  
  .marketplace .dataTables_empty, .market-data td, .dataTable td, .select2-chosen, .select2-result-label, .dataTables_info {
    font-size: 12px !important;
  }
  
  .marketplace .instructions {
    font-family: Calibri !important;
    font-size: 14px !important;
    margin-bottom: 30px !important;
  }
  
  .hunterTitle .titles {
    font-family: Cambria !important;
    font-size: 16px !important;
  }
  
  .hunterTitle .description {
    font-size: 12px !important;
  }
  
  /* Other styling */
  
  #profileCampLeft > div:nth-child(1) > div:nth-child(1), #profileCampLeft > div:nth-child(1) > div:nth-child(3) {
    background-color: white !important;
  }
  
  .messageBoardView-pager-section.current, .messageBoardView-pager-section.next a {
    font-size: 11px !important;
  }
  
  .townOfGnawniaHUD-mouse-name {
    font-family: Cambria !important;
  } 
  
  .townOfGnawniaHUD-mouse-bait-name, .townOfGnawniaHUD-reward {
    font-size: 12px !important;
  }
  
  .townOfGnawniaHUD-description {
     font-size: 11px !important;
   }
  
  .windmillHud-bonusContainer {
    padding: 5px 3px 3px 3px !important;
  }
  
  .windmillHud-helpContainer-section-content {
    margin-top: -5px !important;
    padding: 8px !important;
  }
  
  .windmillHud-bonusContainer {
    width: 100px !important;
  }
  
  .windmillHud-bonus, .windmillHud-helpContainer-section-content, .windmillHud-flour-description {
    font-family: Arial !important;
    font-size: 11px !important;
  }
  
  .puzzleImageContainer .content .text h3 {
    font-family: Cambria !important;
    font-size: 16px !important;
  }
  
  .puzzleImageContainer .content .text {
    font-size: 12px !important;
    line-height: 1.2em !important;
  }
  
  #overlayPopup .themeContainer .themeContent .description {
    font-family: Calibri !important;
    font-size: 12px !important;
    line-height: 15px !important;
  }
  
  .themeContent h2 {
    font-size: 14px !important;
    font-weight: bold !important;
  }
  
  .themeContent .error {
    font-family: Calibri !important;
    font-size: 12px !important;
    line-height: 9px !important;
  }
  
}
