css:
@-moz-document domain("saraba1st.com")
{
  /*style*/
  #ft{padding: 0;border: 0;width: 550px;margin-right: 15px}
  .cttp{border: 0}
  div#scrolltop{margin-left:-30px;opacity:0.75}
  div#ct>.mn>.bm.fl{border: 0;margin: 0}
  #um{margin-top: 30px;padding: 0}
  #qmenu_menu{width:410px}
  #nv{position: fixed;top:0px;width:100%;left: 0 !important;border-right:0;border-left:0;height: 34px !important;opacity:0.8 !important}
  #scbar_txt{width: 96%}
  .scbar_txt_td{width: 52.3%}
  #scbar{border: 0;position: fixed;width: 190px;right:120px;top:-4px;z-index:200;opacity:0.8}
  #hd .wp, #wp{min-width: 540px}
  #hd{height: 37px}
  #f_pst{margin: 0}
  .pg a, .pg strong, .pgb a, .pg label{border-radius: 2px !important}
  .pg a.nxt{display: block !important;background-image: none;padding: 0 4px}
  #visitedforums>a{background-image: none;padding: 0 4px}
  input.px{border-radius: 2px !important;text-align: center}
  div#pgt{position: fixed;top: -1px; right: 310px; margin: 0 !important; padding: 0 !important; height: 35px; min-width: 290px; z-index: 200}

  /*deleted elements*/
  #toptb,.xs0,#scbar_hot,.scbar_btn_td,.lk.bm,#hd h2,#online,.z.chart,#newspecial,#post_reply,.num.xg1,.bm_h,#um .avt,.p_pop.blk.bui,td.fl_icn,td.fl_by,td.fl_i,.fl_icn_g,.pbg2,#chart,.pg a,#mn_F77/*八卦体育*/,#mn_N8d21/*页游*/{display: none}
  span.xg1>img{display: none}
  #um>p:nth-child(3){display: none}
  .jump_bdl>li:nth-child(3){display: none}
  #pt>div.z>a:nth-child(3),em:nth-child(4){display: none}

  /*dark theme*/
  /*body#nv_forum,a,#nv li a,#nv li.a a,.flb em{color:#cfd8dc}
  .quote{color:#cfd8dc}
  div.card_gender_0,.p_pop,.p_pof,.sllt{border: 0;background: #263238}*/
  
  /*my custom*/
}

@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/forum\.php(#.*|gid=.*)?")
{
  /*style*/
  table.fl_tb>tbody>tr{display:inline-block;margin:0.7% 1.2%;width:21.5%;min-width: 0px;min-height: 74px;border: 1px solid;border-radius: 3px;background: #D1D9C1;box-shadow: #aaa 2px 2px 3px}
  table.fl_tb>tbody>tr.fl_row{display:inline-block;margin:0.7% 1.2%;width:21.5%;min-width: 0px;min-height: 74px;border: 1px solid;border-radius: 3px;background: #D1D9C1;box-shadow: #aaa 2px 2px 3px}
  table.fl_tb>tbody>tr>td:nth-child(2){border:0;width:70%;padding:5px 10px}
  table.fl_tb>tbody{min-width: 0px}
  div#ct{min-height: 435px}
  div#ct>.mn{min-height: 435px}
  /*deleted elements*/
  table.fl_tb>tbody>tr>td:nth-child(2)>p.xg2{display: none}
  table.fl_tb>tbody>tr.fl_row>td:nth-child(2)>p.xg2{display: none}
  div#category_7>.fl_tb>tbody>tr:nth-child(2){display: none !important}
  div#category_1>.fl_tb>tbody>tr.fl_row:nth-of-type(14){display: none !important}
  div#category_1>.fl_tb>tbody>tr:nth-child(11){display: none !important}
  .cl.bm_h,div.pgs:nth-child(6){display: none}
  /*dark theme*/
  /*my custom*/
  #category_117{display: none}
}

@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/forum-.*")
{
  /*style*/
  td.fl_g>dl{margin:0.7% !important;width:82.5% !important;padding: 2% 0 0 3%;min-width: 0px;min-height: 69px;border: 1px solid;border-radius: 3px !important;background: #D1D9C1;box-shadow: #aaa 2px 2px 3px}
  .fl_row td{border: 0}
  #thread_types{border-width: 1px 0px 0px}
  .ttp a, .ttp strong{border-radius: 2px !important}
  /*deleted elements*/
  th>img{display: none}
  #separatorline,div.bm:nth-child(14){display: none}  
  div.bm.bml.pbn{display: none}/*版规，想显示就删掉这行，老爷饶命*/
  /*dark theme*/
  /*my custom*/
}

@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/.*(thread-|viewthread).*") 
{
  /*style*/
  .pls p,.pls .pil,.pls .o{margin:1px 10px 1px 20px}
  .pls .tns{padding: 0px 10px 5px}
  /*deleted elements*/
  .authicn,.pls dt,.pls dd:nth-of-type(1){display: none}
  #fastpostform>table>tbody>tr>td.pls{display: none}
  div.pgs:nth-child(6),div.pgs:nth-child(5){display: none}
  /*dark theme*/
  /*my custom*/
}
