  src: url(//fonts.gstatic.com/ea/droidarabickufi/v6/DroidKufi-Bold.eot);
  src: url(//fonts.gstatic.com/ea/droidarabickufi/v6/DroidKufi-Bold.eot?#iefix) format('embedded-opentype'),
       url(//fonts.gstatic.com/ea/droidarabickufi/v6/DroidKufi-Bold.woff2) format('woff2'),
       url(//fonts.gstatic.com/ea/droidarabickufi/v6/DroidKufi-Bold.woff) format('woff'),
       url(//fonts.gstatic.com/ea/droidarabickufi/v6/DroidKufi-Bold.ttf) format('truetype');
}





@-moz-document url-prefix("https://twitter.com"), url-prefix("http://twitter.com") {
  
  
  
  
.avatar.js-action-profile-avatar,.DashboardProfileCard-avatarLink.u-inlineBlock,.DashboardProfileCard-avatarImage.js-action-profile-avatar{

  border-radius: 30px !important;

}
  
  
  
.TweetTextSize.js-tweet-text.tweet-text.tweet-text-rtl,.tweet-box.rich-editor.notie,.ProfileCardStats-statLabel.u-block,.change-trends.js-trend-toggle,.metadata.social-context,.btn-link.js-refresh-suggestions,.js-view-all-link,.ProfileCardStats-statValue{
  
  font-family: 'Droid Arabic Naskh';
}
  
.fullname.js-action-profile-name.show-popup-with-id.fullname-rtl,.js-retweet-text,._timestamp.js-short-timestamp.js-relative-timestamp,.new-tweets-bar.js-new-tweets-bar,.trend-location.js-trend-location,.u-tableCell.u-alignMiddle.remove-discover-pymk,.fullname,.flex-module-header > h3,.js-view-all-link,.nav > li > a .text{
  
  font-family: 'Droid Arabic Kufi';
  
}
.ProfileCardStats-statLabel.u-block{
  
  letter-spacing: opx !important;
  
}
  
}
