a:link {
    color: #901212 !important;
    font-size: 15px!important;
    font-weight: 700!important;
}
a:visited{color:#760000 !important}
a:hover {color:#DC0000 !important;
	background: #000!important;
}
a:active{color:#FF0000 !important}




body {
    background-color: black!important;
}


body.darktheme .file-list-item .desc, .file-list-item .bottombar {
    color: #AD2020;
}

.file-list-item {
    background-color: #000!important;
    position: relative;
    border: 2px solid #9F0E0E!important;
}

.tag-tree {
    background-color: #232121;
    padding: 15px;
    margin-bottom: 15px;
    border: 3px solid #9F0E0E!important;
}
Inherited from div#main


#main {
    color: #981313!important;
    font-size: 24px;
}


.toplistcol .toplabel {
    background-color: #000000!important;
    line-height: 35px;
    padding-left: 10px;
    font-size: 24px;
    color: #D80000!important;
    border: 3px solid #9F0E0E!important;
}

.toplistcol .topitems .list {
    background-color: #020202!important;
    padding: 6px 0 8px;
    border: 3px solid #9F0E0E!important;
}


.cardlist .cards .card {
    -ms-flex: 1 1 20%;
    -webkit-flex: 1 1 20%;
    flex: 1 1 20%;
    padding: 12px;
    margin: 10px;
    background-color: black!important;
    box-shadow: 0 0 8px 0 #F00!important;
    position: relative;
}


.cardlist .cards .card .card-data {
    border: 1.5px solid #630000!important;
}


.headline.contentheadline.linetitle.blocktitle> span {
    color: #BB0000!important;
    background-color: #000000!important;
    padding: 7px 20px 5px;
    border: 3px solid #9F0E0E!important;
}



.headline.contentheadline.linetitle{
border-bottom: 3px dotted #730303!important;
}



.filtersegments .segment:hover, .filtersegments .segment.selected {
    background-color: #2D0000!important;
    color: white;
}


.filtersegments {
    border: 2px solid #BF0000!important;
}


body>footer {
    border-top: 1px dotted #E41F1F!important;
    border-top-width: 1px;
    border-top-style: dotted;
    border-top-color: rgb(228, 31, 31);
    background-color: #000!important;
    padding: 30px 0 45px;
    margin-top: 15px;
}


body>footer .inner .box>div {
    border-left: 1px solid #F10000;
    border-left-width: 1px;
    border-left-style: solid;
    border-left-color: rgb(241, 0, 0);
    background: black!important;
    background-color: black!important;
}






body>header .header-bar .searchbar input[name=q] {
    box-sizing: border-box;
    height: 30px;
    width: 230px;
    border: 0px;
    border-left-width: 0px;
    border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
    padding: 1px 8px;
    float: left;
    background: black;
    background-color: black;
    color: red;
    font-weight: 600;
    font-size: 16px;
}





body>header .header-bar .searchbar button[type=submit] {
    height: 30px;
    background-color: #000000;
    background-image: -moz-linear-gradient(#C5B129,#757C17);
    background-image: -webkit-linear-gradient(#000000,#860909);
    background-image: linear-gradient(#000000,#7C1717);
    border: 0;
    border-top-color: rgb(144, 16, 16);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(166, 29, 29);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(134, 24, 24);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-width: 0px;
    border-top: 1px solid #901010;
    border-right: 1px solid #A61D1D;
    border-bottom: 1px solid #861818;
    padding: 0 4px;
    border-top-right-radius: 2px;
    border-bottom-right-radius: 2px;
    cursor: pointer;
}


.tag-tree .section .title-line .title {
    font-size: 16px;
    text-transform: uppercase;
    padding: 0 8px 8px 4px;
    display: inline-block;
    position: relative;
    white-space: nowrap;
    color: red!important;
}


.list-infocard .list-infocard-content {
    position: absolute;
    padding: 10px;
    background-color: #400404!important;
    color: #F00!important;
    border: 2px dotted #BD1313!important;
    border-top-color: rgb(189, 19, 19);
    border-top-style: dotted;
    border-top-width: 2px;
    border-right-color: rgb(189, 19, 19);
    border-right-style: dotted;
    border-right-width: 2px;
    border-bottom-color: rgb(189, 19, 19);
    border-bottom-style: dotted;
    border-bottom-width: 2px;
    border-left-color: rgb(189, 19, 19);
    border-left-style: dotted;
    border-left-width: 2px;
    box-shadow: -5px 5px 15px rgba(255, 3, 3, 0.98);
    box-sizing: border-box;
    width: 250px;
    left: 15px;
    min-height: 63px;
    margin-top: -48px;
    z-index: 0;
    white-space: normal;
}




.list-infocard .list-infocard-content:after {
    content: "";
    position: absolute;
    background-color: black;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: -1;
}

.list-infocard .list-infocard-content:before {
    content: "";
    display: block;
    position: absolute;
    top: 35px;
    left: -11px;
    width: 20px;
    height: 20px;
    background-color: black!important;
    border: 2px dotted #F00;
    box-shadow: 0 0 20px rgb(255, 0, 0);
    transform: rotate(45deg);
    z-index: -2;
}


#detailstop {
    background-color: #000000!important;
    border: 2px solid #BF0000!important;
    
}


#detailstop .bottom {
    background-color: #020202!important;
    padding: 10px 20px 10px;
}

.comments .reply-box .reply-text, .comments .reply-box textarea {
    width: 100%;
    background: black;
    background-color: black;
    height: 90px;
    padding: 5px;
    box-sizing: border-box;
    color: #ff0000!important;
    font-family: sans-serif;
    font-size: 16px;
    font-weight:600;
}

.detailspage .videos .video-holder {
    position: relative;
    background-color: rgba(246, 246, 246, 0.15)!important;
}



#detailstop .tags .tag:not(.grouptag):not(.nohover):hover {
    display: inline-block;
    background-color: black!important;
    color: #505050!important;
    margin: 3px 0 3px 5px;
    padding: 4px 10px;
    border-radius: 4px;
    border: 3px solid #ff0000!important;
    font-size: 16px;
    font-weight:600;
}


#detailstop .tags .tag {
    background-color: #000000!important;
    color: #CA8282!important;
    border: 1.3px dotted #ff0000!important;
    font-size: 14px;
    font-weight:400;
}


    


.tag-tree .section .options .option a {
    background-color: #000000!important;
    color: #CA8282!important;
    border: 1.2px dotted #ff0000!important;
    font-size: 14px!important;
    font-weight:400;
}

.tag-tree .section .options .option.selected {
    background-color: rgba(0, 0, 0, 0)!important;
    color: #CA8282!important;
    border: 2.6px inset #ff0000!important;
    box-shadow: 0 0 20px rgb(255, 0, 0)!important;
    font-size: 14px!important;
    font-weight:400!important;
}


select {
    color: #F90000!important;
    border: 3px double #B91212!important;
    background-color: #000000!important;
    box-shadow: 0 0 5px 0px #F50000 inset!important;
}


.short-file-list-item .iteminfo {
    background-color: #000000!important;
    line-height: 30px;
    min-height: 30px;
    padding: 0 5px;
    position: relative;
    border: 3px inset #B91212!important;
}



.pager .pagebutton {
    color: #000!important;
    background-color: red!important;
    font-size: 16px;
    font-weight: 600;
    border: 3px inset #B91212!important;
}

.pager .pagebutton:not(:hover):not(.current) {
    background-color: #000000!important;
    font-size: 16px;
    font-weight: 600;
    border: 3px outset #B91212!important;
}


.tag-tree .section .title-line {
    border-bottom: 2px solid #000!important;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    border-bottom-color: rgb(198, 6, 6);
    margin-bottom: 5px;
}

.tag-tree .section .title-line .title:after {
    border-bottom: 2px solid #C60606!important;
}

.tag-tree .expand {
    border: 1px solid #E00F05!important;
    color: #920000!important;
    background-color: black!important;
    font-size:14px!important;
}


#mainleft.login .loginbox>.title {
    font-size: 32px;
    color: #FFF!important;
    margin-bottom: 25px;
}


#mainleft.login .loginbox .label {
    display: block;
    color: #FFF!important;
    margin: 20px 0 2px;
}



#mainleft.login .loginbox {
    position: relative;
    display: inline-block;
    text-align: start;
    background-color: #000000!important;
    padding: 40px;
    font-size: 14px;
    width: 320px;
    color: white;
}

#mainleft.login .loginbox input:not([type=checkbox]) {
    width: 100%;
    font-size: 16px;
    box-sizing: border-box!important;
    border: 2px inset#F70000!important;
    border-top-color: rgb(247, 0, 0);
    border-top-style: inset;
    border-top-width: 2px;
    border-right-color: rgb(247, 0, 0);
    border-right-style: inset;
    border-right-width: 2px;
    border-bottom-color: rgb(247, 0, 0);
    border-bottom-style: inset;
    border-bottom-width: 2px;
    border-left-color: rgb(247, 0, 0);
    border-left-style: inset;
    border-left-width: 2px;
    box-shadow: 0 0 3px 0px #F70000;
    padding: 10px 4px;
    background: black!important;
    background-color: black;
    font-weight: 600;
    color: red;
}


#mainleft.contact form .graylabel {
    color: #FFF!important;
    font-size: 12px!important;
}


#mainleft.contact form .blacklabel {
    color: #FF7272!important;
    font-weight: bold!important;
    font-size: 12px!important;
}


#mainleft.contact form input:not([type=submit]):not([name=captcha]), #mainleft.contact form textarea {
    border: 1px solid #B50101!important;
    border-top-color: rgb(181, 1, 1);
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: rgb(181, 1, 1);
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: rgb(181, 1, 1);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: rgb(181, 1, 1);
    border-left-style: solid;
    border-left-width: 1px;
    background-color: #000000!important;
    box-shadow: 0 0 5px 0px #DA0000 inset;
    margin-top: 3px;
    margin-bottom: 10px;
    font-family: inherit;
    color: red;
    font-size: 15px;
    font-weight: 500;
}





.contentholder>.content {
    border-top: 0px;
    border-top-width: 3px;
    border-top-style: double;
    border-top-color: rgb(255, 0, 0);
    line-height: 18px;
    font-size: 14px;
    overflow: hidden;
    border: 3px double #ff0000!important;
    border-right-color: rgb(255, 0, 0);
    border-right-style: double;
